INSERT INTO `cmswing_temp` VALUES (1, 'home', 'index', 'index', '首页模版', '{% extends \"./inc_base.html\" %}\n{% block style %}\n<link href=\"/static/assets/css/layout-question.css\" rel=\"stylesheet\" type=\"text/css\" />\n{% endblock %}\n{% block content %}\n<section>\n	<div class=\"container\">\n		{{11|show_ad(\"code\")|safe}}\n		<div class=\"row\">\n\n			<div class=\"col-md-9\">\n				<!-- REVOLUTION SLIDER -->\n				<div class=\"slider fullwidthbanner-container roundedcorners margin-bottom-0\">\n					<!--\n                        Navigation Styles:\n\n                            data-navigationStyle=\"\" theme default navigation\n\n                            data-navigationStyle=\"preview1\"\n                            data-navigationStyle=\"preview2\"\n                            data-navigationStyle=\"preview3\"\n                            data-navigationStyle=\"preview4\"\n\n                        Bottom Shadows\n                            data-shadow=\"1\"\n                            data-shadow=\"2\"\n                            data-shadow=\"3\"\n\n                        Slider Height (do not use on fullscreen mode)\n                            data-height=\"300\"\n                            data-height=\"350\"\n                            data-height=\"400\"\n                            data-height=\"450\"\n                            data-height=\"500\"\n                            data-height=\"550\"\n                            data-height=\"600\"\n                            data-height=\"650\"\n                            data-height=\"700\"\n                            data-height=\"750\"\n                            data-height=\"800\"\n                    -->\n					<div class=\"fullwidthbanner\" data-height=\"550\" data-shadow=\"\" data-navigationStyle=\"\">\n						<ul class=\"hide\">\n							{% topic data = \"slider\",limit= \"10\",position=\'2\',issub=\"1\"%}\n							{%for val in slider%}\n							{%if val.model_id==4%} {% set pic = val.pics|strToArray%}{%else%}{%set pic = val.cover_id%}{%endif%}\n							<!-- SLIDE  -->\n							<li data-transition=\"fade\" data-slotamount=\"7\" data-masterspeed=\"300\"  data-saveperformance=\"off\" >\n								<img src=\"/static/assets/images/1x1.png\" data-lazyload=\"{{pic|get_pic(\'m=2,w=848,h=400\')}}\" alt=\"\" data-bgfit=\"cover\" data-bgposition=\"center center\" data-bgrepeat=\"no-repeat\" />\n\n								<div class=\"tp-caption lft start\"\n									 data-x=\"0\"\n									 data-y=\"0\"\n									 data-speed=\"750\"\n									 data-start=\"750\"\n									 data-easing=\"easeOutExpo\"\n									 data-elementdelay=\"0.1\"\n									 data-endelementdelay=\"0.1\"\n									 data-endspeed=\"300\">\n									<img src=\"/static/assets/images/1x1.png\" alt=\"\" data-lazyload=\"/static/assets/images/caption_bg.png\">\n								</div>\n\n								<a class=\"tp-caption medium_light_white lfb start\" href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\"\n								   data-x=\"left\" data-hoffset=\"70\"\n								   data-y=\"96\"\n								   data-speed=\"300\"\n								   data-start=\"1200\"\n								   data-easing=\"easeOutExpo\"\n								   data-splitin=\"none\"\n								   data-splitout=\"none\"\n								   data-elementdelay=\"0.1\"\n								   data-endelementdelay=\"0.1\"\n								   data-endspeed=\"300\">\n									{{val.title|truncate(20, true, \"\")|insert_flg(\"<br>\",10)|safe}}\n								</a>\n\n								<div class=\"tp-caption block_black lfl start\"\n									 data-x=\"left\" data-hoffset=\"70\"\n									 data-y=\"195\"\n									 data-speed=\"300\"\n									 data-start=\"1500\"\n									 data-easing=\"easeOutExpo\"\n									 data-splitin=\"none\"\n									 data-splitout=\"none\"\n									 data-elementdelay=\"0.1\"\n									 data-endelementdelay=\"0.1\"\n									 data-endspeed=\"300\" >\n									{{val.create_time|moment(\'YYYY-MM-DD HH:mm\')}}\n								</div>\n\n								<div class=\"tp-caption small_light_white lfl start\"\n									 data-x=\"left\" data-hoffset=\"70\"\n									 data-y=\"250\"\n									 data-speed=\"1000\"\n									 data-start=\"1800\"\n									 data-easing=\"easeOutExpo\"\n									 data-splitin=\"none\"\n									 data-splitout=\"none\"\n									 data-elementdelay=\"0.1\"\n									 data-endelementdelay=\"0.1\"\n									 data-endspeed=\"300\" >\n									{{val.description|truncate(90, true, \"...\")|insert_flg(\"<br>\",20)|safe}}\n								</div>\n							</li>\n							{%endfor%}\n\n\n						</ul>\n\n						<div class=\"tp-bannertimer\"><!-- progress bar --></div>\n					</div>\n				</div>\n				<!-- /REVOLUTION SLIDER -->\n			</div>\n			<div class=\"col-md-3\">\n				<div class=\"panel panel-default margin-bottom-0\">\n					<div class=\"panel-heading\">\n						<a class=\"btn btn-primary btn-xs pull-right\"><i class=\"fa fa-fire\"></i></a>\n						<h2 class=\"panel-title\">热点</h2>\n					</div>\n					<div class=\"panel-body con \" style=\"height: 354px\">\n						<ol class=\"current\">\n							{%model data=\"hotnews\",table=\"document\",order=\"view DESC,create_time DESC\",limit=\"10\",cache=\"1000\"%}\n							{% for val in hotnews %}\n							<li>\n								<span {%if loop.index < 4 %}class=\"topRank\"{%endif%}>{{loop.index}}</span><a target=\"_blank\" href=\"{{val.name|get_url(val.id)}} \" >{{val.title}}</a>\n							</li>\n							{%endfor%}\n							</ol>\n					</div>\n				</div>\n\n			</div>\n			{%if 14|show_ad(\'code\') %}\n			<!-- INFO BAR -->\n			<div class=\"margin-top-20 col-md-12 \">\n				{{14|show_ad(\'code\')|safe}}\n			</div>\n			<!-- /INFO BAR -->\n			{%endif%}\n			<!-- RIGHT -->\n			<div class=\"col-sm-9 margin-top-20\">\n\n				<ul class=\"nav nav-tabs nav-justified\" style=\"position: inherit;background-color: #f1f2f7\">\n					<li class=\"active\"><a href=\"#new\" data-toggle=\"tab\" class=\"size-18\">最新</a></li>\n					<li><a href=\"#wenda\" data-toggle=\"tab\" class=\"size-18\">问答</a></li>\n					{#\n					<li class=\"dropdown\">\n						<a href=\"#\" class=\"dropdown-toggle\" data-toggle=\"dropdown\">Dropdown <span class=\"caret\"></span></a>\n						<ul class=\"dropdown-menu\">\n							<li><a href=\"#dropdown1\" tabindex=\"-1\" data-toggle=\"tab\">@fat</a></li>\n							<li><a href=\"#dropdown2\" tabindex=\"-1\" data-toggle=\"tab\">@mdo</a></li>\n						</ul>\n					</li>\n					#}\n				</ul>\n				<div class=\"panel panel-default\" style=\"margin-top: -5px\">\n					<div class=\"panel-body padding-10\">\n\n				<div class=\"tab-content\">\n					<div class=\"tab-pane fade in active\" id=\"new\">\n						{% topic data = \"newlist\",limit=\"10\"%}\n						{% for val in newlist %}\n						{% set categoryname = val.category_id|get_cate %}\n						{%if val.model_id==4%}\n						{% set pic = val.pics|strToArray%}\n						<!-- POST ITEM -->\n						<div class=\"blog-post-item padding-bottom-20 margin-bottom-20 clearfix\">\n\n							<!-- IMAGE -->\n							{% if pic[0]>0 %}\n							<figure class=\"blog-item-small-image margin-bottom-0\">\n								<img class=\"img-responsive\" src=\"{{pic[0]|get_pic(\'m=1,w=200,h=120\')}}\" alt=\"\">\n							</figure>\n							{% endif %}\n							<div class=\"blog-item-small-content\">\n								<h2><a href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\" title=\"{{val.title}}\">{{val.title|truncate(50, true, \"...\")}}</a></h2>\n\n								<ul class=\"blog-post-info list-inline padding-bottom-10\">\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-clock-o\"></i>\n											<span class=\"font-lato\">{{val.create_time|dateformat(\'Y-m-d H:i\')}}</span>\n										</a>\n									</li>\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-comment-o\"></i>\n											<span class=\"font-lato\">{{val.view}}</span>\n										</a>\n									</li>\n									<li>\n										<i class=\"fa fa-folder-open-o\"></i>\n\n										<a class=\"category\" href=\"{{categoryname.url}}\">\n											<span class=\"font-lato\">{{categoryname.name}}</span>\n										</a>\n									</li>\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-user\"></i>\n											<span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n										</a>\n									</li>\n								</ul>\n\n								<p><div class=\"shop-item-price\" style=\"font-size: 20px;margin-top: 6px;font-weight: bold; float: left\">\n								{% if val.price|get_price_format(\'2\') %}<span class=\"line-through\" style=\"color: #999;font-size: 15px;display: inline-block;padding: 0 10px;font-weight: 400;\">¥{{val.price|get_price_format(\'2\')}}</span>{%endif%}\n								¥{{val.price|get_price_format(\'1\')}}\n							</div><div class=\"shop-item-buttons\" style=\"float: left;margin-left: 15px\">\n								<a class=\"btn btn-default\" href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\"><i class=\"fa fa-cart-plus\"></i> 立即购买</a>\n								<!-- replace data-item-id width the real item ID - used by js/view/demo.shop.js -->\n								<a class=\"btn btn-default add-wishlist\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"\" data-original-title=\"Add To Wishlist\"><i class=\"fa fa-heart nopadding\"></i></a>\n								<a class=\"btn btn-default add-compare\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"\" data-original-title=\"Add To Compare\"><i class=\"fa fa-bar-chart-o nopadding\" data-toggle=\"tooltip\"></i></a>\n							</div></p>\n							</div>\n\n						</div>\n						<!-- /POST ITEM -->\n						{%elif val.model_id === 10 %}\n						<!-- POST ITEM -->\n						<div class=\"blog-post-item padding-bottom-20 margin-bottom-20 clearfix\">\n							<div class=\"ribbon\">\n								<div class=\"ribbon-inner\">{% if null|getnow < val.create_time %}未开幕{%elif (null|getnow > val.create_time) and (null|getnow < val.deadline)%}展览中{%elif null|getnow > val.deadline%}已结束{%endif%}</div>\n							</div>\n							<!-- IMAGE -->\n							{% if val.cover_id>0 %}\n							<figure class=\"blog-item-small-image margin-bottom-0\">\n								<img class=\"img-responsive\" src=\"{{val.cover_id|get_pic(\'m=1,w=200,h=120\')}}\" alt=\"\">\n							</figure>\n							{% endif %}\n							<div class=\"blog-item-small-content\">\n								<h2><a href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\" title=\"{{val.title}}\">{{val.title|truncate(50, true, \"...\")}}</a></h2>\n\n								<ul class=\"blog-post-info list-inline padding-bottom-10\">\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-clock-o\"></i>\n											<span class=\"font-lato\">{{val.create_time|moment(\'YYYY-MM-DD HH:mm\')}}</span>\n										</a>\n									</li>\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-comment-o\"></i>\n											<span class=\"font-lato\">{{val.view}}</span>\n										</a>\n									</li>\n									<li>\n										<i class=\"fa fa-folder-open-o\"></i>\n\n										<a class=\"category\" href=\"{{categoryname.url}}\">\n											<span class=\"font-lato\">{{categoryname.name}}</span>\n										</a>\n									</li>\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-user\"></i>\n											<span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n										</a>\n									</li>\n								</ul>\n\n								<div class=\"alert alert-mini alert-success \"><!-- SUCCESS -->\n									<strong></strong> <span style=\"width: 60px\"><i class=\"fa fa-calendar-check-o\"></i> 开始:</span> {{ val.create_time|moment(\'YYYY-MM-DD\') }} <span style=\"width: 60px\"><i class=\"fa fa-calendar-times-o\"></i> 结束:</span> {{val.deadline|dateformat(\'Y-m-d\')}} <span style=\"width: 60px\"><i class=\"fa fa-map-marker\"></i> 地点:</span> {{val.id|getmodelfield(val.model_id,\'addr\')}}\n								</div>\n\n							</div>\n\n						</div>\n						<!-- /POST ITEM -->\n						{%else%}\n						<!-- POST ITEM -->\n						<div class=\"blog-post-item padding-bottom-20 margin-bottom-20 clearfix\">\n\n							<!-- IMAGE -->\n							{% if val.cover_id>0 %}\n							<figure class=\"blog-item-small-image margin-bottom-0\">\n								<img class=\"img-responsive\" src=\"{{val.cover_id|get_pic(\'m=1,w=200,h=120\')}}\" alt=\"\">\n							</figure>\n							{% endif %}\n							<div class=\"blog-item-small-content\">\n								<h2><a href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\" title=\"{{val.title}}\">{{val.title|truncate(50, true, \"...\")}}</a></h2>\n\n								<ul class=\"blog-post-info list-inline padding-bottom-10\">\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-clock-o\"></i>\n											<span class=\"font-lato\">{{val.create_time|moment(\'YYYY-MM-DD HH:mm\')}}</span>\n										</a>\n									</li>\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-comment-o\"></i>\n											<span class=\"font-lato\">{{val.view}}</span>\n										</a>\n									</li>\n									<li>\n										<i class=\"fa fa-folder-open-o\"></i>\n\n										<a class=\"category\" href=\"{{categoryname.url}}\">\n											<span class=\"font-lato\">{{categoryname.name}}</span>\n										</a>\n									</li>\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-user\"></i>\n											<span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n										</a>\n									</li>\n								</ul>\n\n								<p>{{val.description|truncate(90, true, \"...\")}}</p>\n							</div>\n\n						</div>\n						<!-- /POST ITEM -->\n						{%endif%}\n						{%endfor%}\n					</div>\n					<div class=\"tab-pane fade\" id=\"wenda\">\n						<div class=\"timeline\" style=\"margin-top: -25px\">\n							<div class=\"timeline-hline\"><!-- horizontal line cache=\'1000\'--></div>\n							{%model data=\"list\",table=\"question\",order=\"create_time DESC\",limit=\"10\"%}\n							{% for val in list %}\n							{% set categoryname = val.category_id|get_cate %}\n							{% set imgs= val.detail|img_text_view(200,120)%}\n							<!-- POST ITEM -->\n							<div class=\"blog-post-item blog-post-item-inverse padding-top-20 padding-bottom-20 margin-bottom-0 clearfix\">\n								<div class=\"timeline-entry\" ><!-- .rounded = entry -->\n									<a href=\"#\"><img src=\"/u/avatar/{{val.uid}}\" style=\"width: 58px;height: 58px; margin-top: -10px\"></a>\n									<div class=\"timeline-vline\"><!-- vertical line --></div>\n								</div>\n								<!-- IMAGE -->\n								{% if imgs|length==1 %}\n								<figure class=\"blog-item-small-image margin-bottom-0\">\n									<img class=\"img-responsive\" src=\"{{imgs[0]}}\" alt=\"\">\n								</figure>\n								{% endif %}\n								<div class=\"blog-item-small-content\">\n									<h2><a href=\"/q/{{val.id}}.html\" title=\"{{val.title}}\">{{val.title}}</a></h2>\n									<ul class=\"blog-post-info list-inline padding-bottom-10\">\n										<li>\n\n											<a class=\"\" href=\"{{categoryname.url}}\">\n												<span class=\"label label-light\">{{categoryname.name}}</span>\n											</a>\n										</li>\n										<li>{%if val.answer_users>0%}\n											<a href=\"#\">\n												<span class=\"font-lato\">{{val.answer_users|get_nickname}}</span>\n											</a>\n											<span class=\"font-lato\"> 回复了问题</span>\n											{%else%}\n											<a href=\"#\">\n												<span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n											</a>\n											<span class=\"font-lato\"> 发起了问题</span>\n											{%endif%}\n										</li>\n										<li>\n                                <span class=\"font-lato\">\n                                {{val.focus_count}} 人关注\n                                    </span>\n										</li>\n										<li>\n											<span class=\"font-lato\">{{val.answer_count}} 个回复</span>\n\n										</li>\n										<li>\n											<span class=\"font-lato\">{{val.view}} 次浏览</span>\n										</li>\n										<li>\n											<span class=\"font-lato\"> {{val.create_time|moment}}</span>\n										</li>\n\n									</ul>\n									{% if imgs|length>1 %}\n									<div class=\"row margin-bottom-10\">\n										{%set i = 0%}\n										{%for img in imgs%}\n										{%if i< 4 %}\n										<div class=\"col-md-3\"><img class=\"img-responsive\" src=\"{{img}}\" alt=\"\"></div>\n										{%endif%}\n										{%set i=i+1%}\n										{%endfor%}\n									</div>\n									{%endif%}\n									<p>{{val.detail|delhtmltags|truncate(90, true, \"...\")|safe}}</p>\n								</div>\n\n							</div>\n							<!-- /POST ITEM -->\n							{%endfor%}\n\n						</div>\n\n						<!-- PAGINATION -->\n\n						<a class=\"btn btn-featured btn-default btn-inverse\" href=\"/question.html\" >\n							<span>今天，你遇到了什么问题呢？进入社区寻求答案！</span>\n							<i class=\"et-happy\"></i>\n						</a>\n						<!-- /PAGINATION -->\n					</div>\n\n\n				</div>\n					</div>\n				</div>\n			</div>\n			<!-- LEFT -->\n			<div class=\"col-md-3 col-sm-3 margin-top-20\">\n\n				{% if controller.is_login %}\n				<div class=\"alert alert-info margin-bottom-20 box-shadow-4\"><!-- INFO -->\n					今天，你遇到了什么问题呢？\n					<a href=\"/mod/question/sys/add/?cid=123\" class=\"btn btn-primary btn-3d btn-block margin-top-10\">提问</a>\n				</div>\n				{%else%}\n				<div class=\"alert alert-info margin-bottom-20 box-shadow-4\"><!-- INFO -->\n					你好! 你还没有登录，请先登录.\n					<a class=\"btn btn-block btn-social btn-dropbox margin-top-10  \" href=\"/center/public/login\" data-toggle=\"ajaxModal\">\n						<i class=\"fa fa-sign-in\"></i> 登录{{config.setup.WEB_SITE_TITLE}}\n					</a>\n				</div>\n				{%endif%}\n				<div class=\"panel panel-default\">\n					<div class=\"panel-heading\">\n						<a class=\"btn btn-primary btn-xs pull-right\" href=\"/topic\" >更多</a>\n						<h2 class=\"panel-title\">热门话题</h2>\n					</div>\n					<div class=\"panel-body padding-10\">\n						{% keywords data =\"topic\",type=\"hot\",limit=\"5\" %}\n						{%for k in topic%}\n						<div class=\"clearfix margin-bottom-10\"><!-- discussion item -->\n							{%if k.pic==0%}\n							<i class=\"ico-light ico-60 ico-hover et-chat pull-left margin-bottom-0 margin-top-0\" ></i>\n							{%else%}\n							<img class=\"thumbnail pull-left\" src=\"{{k.pic|get_pic(\'m=1,w=100,h=100\')}}}}\" width=\"60\" height=\"60\" alt=\"\" />\n							{%endif%}\n							<h4 class=\" nomargin noborder nopadding bold\"><a class=\"tag \" href=\"{{k.url}}\">\n								<span class=\"txt\">{{k.keyname}}</span>\n								<span class=\"num\"><i class=\"fa fa-fire\"></i></span>\n							</a>\n							</h4>\n							<span class=\"size-13 text-muted\">\n                                {{k.videonum}} 个主题, {{k.focus_count}} 人关注\n												</span>\n						</div><!-- /discussion item -->\n						{%endfor%}\n					</div>\n				</div>\n\n				{%model data=\"data\",table=\"question_user\",order=\"question_count DESC\",alias=\"a\",limit=\"10\",join=\'{\"member\": {\"join\": \"left\",\"as\":\"b\",\"on\": [\"a.uid\", \"b.id\"]}}\',cache=\"1000\"%}\n				{%if data.length>0%}\n				<div class=\"panel panel-default\">\n					<div class=\"panel-heading\">\n						<a class=\"btn btn-primary btn-xs pull-right\">更多</a>\n						<h2 class=\"panel-title\">热门用户</h2>\n					</div>\n					<div class=\"panel-body padding-10\">\n						{%for user in data%}\n						<div class=\"clearfix margin-bottom-10\"><!-- squared item -->\n							<img class=\"thumbnail pull-left rounded\" src=\"/u/avatar/{{user.uid}}\" width=\"40\" height=\"40\" alt=\"\" />\n							<h4 class=\"size-14 nomargin noborder nopadding bold\"><a href=\"#\">{{user.username}}</a></h4>\n							<span class=\"size-12 text-muted\">{{user.question_count}} 个问题, {{user.agree_count}} 次赞同</span>\n						</div><!-- /squared item -->\n						{%endfor%}\n					</div>\n				</div>\n				{%endif%}\n\n				{{13|show_ad(\'code\')|safe}}\n			</div>\n\n\n		</div>\n\n\n	</div>\n	<div class=\"container\">\n		<div class=\"panel panel-default\">\n			<div class=\"panel-heading\">\n				<a class=\"btn btn-primary btn-xs pull-right\" href=\"/ext/link/index\">更多&gt;&gt;</a> <a class=\"btn btn-info btn-xs pull-right\" href=\"/ext/link/index/apply\" style=\"margin-right: 15px\">申请友情链接</a>\n				<h2 class=\"panel-title\">友情链接</h2>\n			</div>\n			<div class=\"panel-body link padding-10\">\n				<!--\ncontrolls-over		= navigation buttons over the image\nbuttons-autohide 	= navigation buttons visible on mouse hover only\n\ndata-plugin-options:\n\"singleItem\": true\n\"autoPlay\": true (or ms. eg: 4000)\n\"navigation\": true\n\"pagination\": true\n-->\n				<div class=\"owl-carousel nomargin\" data-plugin-options=\'{\"items\":6, \"singleItem\": false, \"autoPlay\": true}\'>\n					{% model data =\"link\",table=\"ext_link\",where=\'{\"passed\":1}\',limit=\"24\",order=\"sort ASC\"%}\n					{%for l in link%}\n					{%if l.linktype==0%}\n					<div class=\"linkbox alternate\">\n						<a href=\"{{l.url}}\" target=\"_blank\" title=\"{{l.name}}\">{{l.name}}</a>\n					</div>\n					{%else%}\n					<div class=\"linkbox alternate\">\n						<a href=\"{{l.url}}\" target=\"_blank\" title=\"{{l.name}}\">\n							<img class=\"img-responsive\" src=\"{{l.logo}}\" alt=\"{{l.name}}\">\n						</a>\n					</div>\n					{%endif%}\n					{%endfor%}\n				</div>\n			</div>\n		</div>\n	</div>\n</section>\n{% endblock %}\n{%block script%}\n\n{%endblock%}', 1, 1);
INSERT INTO `cmswing_temp` VALUES (2, 'home', 'index', 'index', '首页模版（移动版）', '{% extends \"./inc/base.html\" %}\n{% block content %}\n<header class=\"mui-bar mui-bar-nav\">\n    <!--<a class=\"mui-action-back mui-icon mui-icon-left-nav mui-pull-left\"></a>-->\n    <a href=\"/center/cart/index\" class=\"mui-icon iconfont icon-cart\"><span class=\"mui-badge\">{% if controller.cart.num %}{{controller.cart.num}}{%else%}0{%endif%}</span></a>\n    <h1 class=\"mui-title\"><img src=\"/static/mobile/img/logo.png\" ></h1>\n    <a class=\"mui-icon iconfont icon-search mui-pull-right\" href=\"/search\"></a>\n</header>\n{% include \"./inc/nav.html\" %}\n{% include \"./inc/sliderbar.html\" %}\n<!--下拉刷新容器-->\n<div id=\"pullrefresh\" class=\"mui-content mui-scroll-wrapper list-content\">\n    <div class=\"mui-scroll\">\n        <!--数据列表-->\n        <ul class=\"mui-table-view \">\n            {%for v in list.data%}\n            <!--如果有缩略图-->\n            {%if v.cover_id >0%}\n            {% if v.model_id == 5%}\n            <li class=\"mui-table-view-cell mui-media\">\n                <a href=\"{{v.name|get_url(v.id)}}\">\n\n\n                    <h4 class=\"dotdot line2 margin-bottom-6\">{{v.title}}</h4>\n\n\n                    <div class=\"mui-row\">\n                        <div class=\"mui-col-sm-12 mui-col-xs-12\">\n                            <img class=\"list_img_holder\" src=\"{{v.cover_id|get_pic(\'m=0,w=360\')}}\">\n                        </div>\n                    </div>\n                    <p class=\"item_info\">\n\n                        <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                        <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                        <span class=\"time\" >{{v.update_time|moment}}</span>\n\n                    </p>\n\n                </a>\n            </li>\n            {%else%}\n            <li class=\"mui-table-view-cell mui-media\">\n                <a href=\"{{v.name|get_url(v.id)}}\" >\n                    <div class=\"mui-row\">\n                        <div class=\"mui-col-sm-8 mui-col-xs-8\">\n\n                            <h4 class=\"dotdot line2 \">{{v.title}}</h4>\n                            <p class=\"item_info\">\n\n                                <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                                <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                                <span class=\"time\" >{{v.update_time|moment}}</span>\n\n                            </p>\n                        </div>\n                        <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                            <img class=\"list_img_holder\" src=\"{{v.cover_id|get_pic(\'m=1,w=123,h=82\')}}\" >\n\n                        </div>\n                    </div>\n                </a>\n            </li>\n            {%endif%}\n            {%elif v.pics%}\n            {% set pics = v.pics|strToArray%}\n            {% if pics|length >2%}\n            <li class=\"mui-table-view-cell mui-media\">\n                <a href=\"{{v.name|get_url(v.id)}}\">\n                    <h4 class=\"dotdot line2 margin-bottom-6\">{{v.title}}</h4>\n                    <div class=\"mui-row\">\n                        <div class=\"mui-col-sm-4 mui-col-xs-4\">\n                            <img class=\"list_img_holder\" src=\"{{pics[0]|get_pic(\'m=1,w=123,h=82\')}}\">\n                        </div>\n                        <div class=\"mui-col-sm-4 mui-col-xs-4\">\n                            <img class=\"list_img_holder\" src=\"{{pics[1]|get_pic(\'m=1,w=123,h=82\')}}\">\n                        </div>\n                        <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                            <img class=\"list_img_holder\" src=\"{{pics[2]|get_pic(\'m=1,w=123,h=82\')}}\" >\n\n                        </div>\n                    </div>\n                    <p class=\"item_info\">\n\n                        <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                        <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                        <span class=\"time\" >{{v.update_time|moment}}</span>\n\n                    </p>\n\n                </a>\n            </li>\n            {%else%}\n            <li class=\"mui-table-view-cell mui-media\">\n                <a href=\"{{v.name|get_url(v.id)}}\" >\n                    <div class=\"mui-row\">\n                        <div class=\"mui-col-sm-8 mui-col-xs-8\">\n\n                            <h4 class=\"dotdot line2 \">{{v.title}}</h4>\n                            <p class=\"item_info\">\n                                <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                                <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                                <span class=\"time\" >{{v.update_time|moment}}</span>\n\n                            </p>\n                        </div>\n                        <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                            <img class=\"list_img_holder\" src=\"{{pics[0]|get_pic(\'m=1,w=123,h=82\')}}\" >\n\n                        </div>\n                    </div>\n                </a>\n            </li>\n            {%endif%}\n            {%else%}\n            <!--无图-->\n            <li class=\"mui-table-view-cell mui-media\">\n                <a href=\"{{v.name|get_url(v.id)}}\">\n                    <h4 class=\"dotdot line2 margin-bottom-6\">{{v.title}}</h4>\n\n                    <p class=\"item_info\">\n\n                        <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                        <span class=\"cmt space\">评论&nbsp;0</span>\n\n                        <span class=\"time\" >{{v.update_time|moment}}</span>\n\n                    </p>\n\n                </a>\n            </li>\n            {%endif%}\n            {%endfor%}\n\n\n        </ul>\n    </div>\n</div>\n<script type=\"text/template\" id=\"tpl\">\n    {% raw %}\n    <%_.forEach(data,function(v,k){ %>\n    <!--如果有缩略图-->\n    <%if (v.cover_id){%>\n\n    <% if (v.model_id == 5){%>\n    <li class=\"mui-table-view-cell mui-media\">\n        <a href=\"<%=v.url%>\">\n\n\n            <h4 class=\"dotdot line2 margin-bottom-6\"><%=v.title%></h4>\n\n\n            <div class=\"mui-row\">\n                <div class=\"mui-col-sm-12 mui-col-xs-12\">\n                    <img class=\"list_img_holder\" src=\"<%=v.cover_id%>\">\n                </div>\n            </div>\n            <p class=\"item_info\">\n\n                <span class=\"src space\"><%=v.uid%></span>\n                <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                <span class=\"time\" title=\"2016-08-30 11:06\"><%=v.update_time%></span>\n\n            </p>\n\n        </a>\n    </li>\n    <%}else{%>\n    <li class=\"mui-table-view-cell mui-media\">\n        <a href=\"<%=v.url%>\" >\n            <div class=\"mui-row\">\n                <div class=\"mui-col-sm-8 mui-col-xs-8\">\n\n                    <h4 class=\"dotdot line2 \"><%=v.title%></h4>\n                    <p class=\"item_info\">\n\n                        <span class=\"src space\"><%=v.uid%></span>\n                        <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                        <span class=\"time\" title=\"2016-08-30 11:06\"><%=v.update_time%></span>\n\n                    </p>\n                </div>\n                <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                    <img class=\"list_img_holder\" src=\"<%=v.cover_id%>\" >\n\n                </div>\n            </div>\n        </a>\n    </li>\n    <%}%>\n    <%}else if(v.pics){%>\n    <% if (v.pics.length >2){%>\n    <li class=\"mui-table-view-cell mui-media\">\n        <a href=\"<%=v.url%>\">\n            <h4 class=\"dotdot line2 margin-bottom-6\"><%=v.title%></h4>\n            <div class=\"mui-row\">\n                <div class=\"mui-col-sm-4 mui-col-xs-4\">\n                    <img class=\"list_img_holder\" src=\"<%=v.pics[0]%>\">\n                </div>\n                <div class=\"mui-col-sm-4 mui-col-xs-4\">\n                    <img class=\"list_img_holder\" src=\"<%=v.pics[1]%>\">\n                </div>\n                <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                    <img class=\"list_img_holder\" src=\"<%=v.pics[2]%>\" >\n\n                </div>\n            </div>\n            <p class=\"item_info\">\n\n                <span class=\"src space\"><%=v.uid%></span>\n                <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                <span class=\"time\" title=\"2016-08-30 11:06\"><%=v.update_time%></span>\n\n            </p>\n\n        </a>\n    </li>\n    <%}else{%>\n    <li class=\"mui-table-view-cell mui-media\">\n        <a href=\"<%=v.url%>\" >\n            <div class=\"mui-row\">\n                <div class=\"mui-col-sm-8 mui-col-xs-8\">\n\n                    <h4 class=\"dotdot line2 \"><%=v.title%></h4>\n                    <p class=\"item_info\">\n                        <span class=\"src space\"><%=v.uid%></span>\n                        <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                        <span class=\"time\" title=\"2016-08-30 11:06\"><%=v.update_time%></span>\n\n                    </p>\n                </div>\n                <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                    <img class=\"list_img_holder\" src=\"<%=v.pics[0]%>\" >\n\n                </div>\n            </div>\n        </a>\n    </li>\n    <%}%>\n    <%}else{%>\n    <!--无图-->\n    <li class=\"mui-table-view-cell mui-media\">\n        <a href=\"<%=v.url%>\">\n            <h4 class=\"dotdot line2 margin-bottom-6\"><%=v.title%></h4>\n\n            <p class=\"item_info\">\n\n                <span class=\"src space\"><%=v.uid%></span>\n                <span class=\"cmt space\">评论&nbsp;0</span>\n\n                <span class=\"time\" title=\"2016-08-30 11:06\"><%=v.update_time%></span>\n\n            </p>\n\n        </a>\n    </li>\n    <%}%>\n    <%})%>\n    {% endraw %}\n</script>\n{% endblock%}\n\n{% block script %}\n<script>\n\n    //console.log();\n    var tplRender =  _.template(document.getElementById(\"tpl\").innerHTML);\n    mui.init({\n        pullRefresh: {\n            container: \'#pullrefresh\',\n            down: {\n                callback: pulldownRefresh\n            },\n            up: {\n                contentrefresh: \'正在加载...\',\n                callback: pullupRefresh\n            }\n        }\n    });\n    mui(\"#sliderSegmentedControl\").on(\"tap\",\"a.mui-control-item\",function(){\n        var url = this.getAttribute(\"data-url\");\n        //打开关于页面\n        //alert(url)\n        mui.openWindow({\n            url: url\n        });\n    })\n    mui(\"#pullrefresh .mui-table-view\").on(\"tap\",\"li.mui-table-view-cell > a\",function(){\n        var url = this.getAttribute(\"href\");\n        //打开关于页面\n        mui.openWindow({\n            url: url\n        });\n    })\n    var tops=parseInt(sessionStorage.getItem(\"{{ctx.url}}_top\"))||0;\n    var size=parseInt(sessionStorage.getItem(\"{{ctx.url}}_size\"))||0;\n    //记录位置\n    window.onscroll = function () {\n\n        var patt1 = /\\(.*?\\)/;\n        var srtY = document.querySelector(\'#pullrefresh .mui-scroll\').style.transform || document.querySelector(\'#pullrefresh .mui-scroll\').style[\"-webkit-transform\"];\n        if (srtY) {\n            var Y = parseInt(srtY.match(patt1)[0].split(\",\")[1])\n        } else {\n            var Y = 0;\n        }\n        console.log(Y)\n        var S = document.querySelector(\'#pullrefresh .mui-table-view\').querySelectorAll(\'li.mui-table-view-cell\').length;\n        console.log(Y);\n        if (window.sessionStorage) {\n            sessionStorage.setItem(\"{{ctx.url}}_top\", Y);\n            sessionStorage.setItem(\"{{ctx.url}}_size\", S);\n        }\n    }\n    /**\n     * 下拉刷新具体业务实现\n     */\n    function pulldownRefresh() {\n        setTimeout(function() {\n            var table = document.body.querySelector(\'.mui-table-view\');\n            var cells = document.body.querySelectorAll(\'.mui-table-view-cell\');\n            for (var i = cells.length, len = i + 3; i < len; i++) {\n                var li = document.createElement(\'li\');\n                li.className = \'mui-table-view-cell\';\n                li.innerHTML = \'<a class=\"mui-navigate-right\">Item \' + (i + 1) + \'</a>\';\n                //下拉刷新，新纪录插到最前面；\n                table.insertBefore(li, table.firstChild);\n            }\n            mui(\'#pullrefresh\').pullRefresh().endPulldownToRefresh(); //refresh completed\n        }, 1000);\n    }\n    /**\n     * 上拉加载具体业务实现\n     */\n    function pullupRefresh() {\n        setTimeout(function() {\n\n            var table = document.body.querySelector(\'#pullrefresh .mui-table-view\');\n            var cells = document.body.querySelectorAll(\'#pullrefresh .mui-table-view-cell\');\n            var count = \"{{list.count}}\";\n            console.log((parseInt(count) > cells.length));\n\n            if(parseInt(cells.length) < parseInt(count)){\n                createFragment(10)\n                mui(\'#pullrefresh\').pullRefresh().endPullupToRefresh();\n            }else {\n                mui(\'#pullrefresh\').pullRefresh().endPullupToRefresh(true); //参数为true代表没有更多数据了。\n            }\n\n        }, 1000);\n    }\n    var createFragment = function(count, page) {\n        //无限加载\n        var table = document.body.querySelector(\'#pullrefresh .mui-table-view\');\n        var lastIndex = table.querySelectorAll(\'li.mui-table-view-cell\').length;\n        console.log(lastIndex);\n        var pages = page || Math.ceil((lastIndex+count)/count);\n        console.log(pages);\n        mui.ajax(\"{{ctx.url}}\",{\n            data:{\n                page:pages\n            },\n            dataType:\'json\',//服务器返回json格式数据\n            type:\'get\',//HTTP请求类型\n            success:function(data){\n                $(\'#pullrefresh .mui-table-view\').append(tplRender(data));\n                //获得服务器响应\n//                console.log();\n//             var fragment = document.createDocumentFragment();\n//                var li;\n//                for (var i = 0; i < data.data.length; i++) {\n//                    li = document.createElement(\'li\');\n//                    li.className = \'mui-table-view-cell\';\n//                    li.innerHTML =\'<a href=\"javascript:;\">\'+\n//                            \'<img class=\"mui-media-object mui-pull-right\" src=\"/static/mobile/img/yuantiao.jpg\">\'+\n//                            \'<div class=\"mui-media-body\">\'+\n//                            data.data[i].title+\n//                            \'<p class=\"mui-ellipsis\">静静的看这个世界，最后终于疯了</p>\'+\n//                            \'</div>\'+\n//                            \'</a>\';\n//                    fragment.appendChild(li);\n//                }\n                //console.log($(tplRender(data)));\n                //document.getElementById(\"tpl\").innerHTML= tplRender(data)\n\n            }\n        });\n\n    };\n\n    mui.ready(function() {\n\n        //mui(\'#pullrefresh\').pullRefresh().pullupLoading();\n\n        var page = Math.ceil(size/10);\n        if(page>1 ){\n            for (var i = 1; i < (page+1); i++) {\n                createFragment(10,(i+1))\n            }\n        }\n        mui(\'#pullrefresh\').pullRefresh().scrollTo(0,tops);\n        //alert(1)\n        console.log()\n//            $(\'header\').on(\'doubleTap\',function () {\n//                alert(3)\n//            })\n        document.querySelector(\'header h1\').addEventListener(\'tap\',function () {\n            //内容区滚动到顶部\n            mui(\'#pullrefresh\').pullRefresh().scrollTo(0,0,100);\n        });\n        var btn = document.querySelectorAll(\".mui-bar-tab a.mui-tab-item\");\n        for(var i = 0;i<btn.length;i++){\n            btn[i].addEventListener(\"tap\",function () {\n                var href = this.getAttribute(\"href\");\n                if(href==\"#wait\"){\n                    //todo\n                    mui.toast(\"功能开发中。。。\")\n                    return\n                }\n                mui.openWindow({url: href})\n\n            })\n        }\n    });\n\n\n</script>\n\n{% endblock %}\n', 2, 1);
INSERT INTO `cmswing_temp` VALUES (3, 'home', 'cover', 'index', '默认封面模板', '{% extends \"../inc/base.html\" %}\n{% block content %}\n     <!--\n				PAGE HEADER\n\n				CLASSES:\n					.page-header-xs	= 20px margins\n					.page-header-md	= 50px margins\n					.page-header-lg	= 80px margins\n					.page-header-xlg= 130px margins\n					.dark			= dark page header\n\n					.shadow-before-1 	= shadow 1 header top\n					.shadow-after-1 	= shadow 1 header bottom\n					.shadow-before-2 	= shadow 2 header top\n					.shadow-after-2 	= shadow 2 header bottom\n					.shadow-before-3 	= shadow 3 header top\n					.shadow-after-3 	= shadow 3 header bottom\n			-->\n<section class=\"page-header page-header-xs\">\n				<div class=\"container\">\n{#\n					<h1>{{category.title}}</h1>\n#}\n					<!-- breadcrumbs -->\n					<ol class=\"breadcrumb breadcrumb-inverse\">\n                        <li><a href=\"/\">首页 </a></li>\n                        {%for val in breadcrumb %}\n                        {% if val.id == category.id %}\n						<li class=\"active\">{{val.title}}</li>\n                        {% else %}\n                        <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n						\n                        {% endif %}\n                        {% endfor %}\n					</ol><!-- /breadcrumbs -->\n\n				</div>\n			</section>\n			<!-- /PAGE HEADER -->\n            <!-- -->\n			<section>\n				<div class=\"container\">\n\n					<div class=\"row\">\n\n						<!-- LEFT -->\n\n						<div class=\"col-md-9 col-sm-9\">\n							<!--\n                                                        controlls-over		= navigation buttons over the image\n                                                        buttons-autohide 	= navigation buttons visible on mouse hover only\n\n                                                        data-plugin-options:\n                                                            \"singleItem\": true\n                                                            \"autoPlay\": true (or ms. eg: 4000)\n                                                            \"navigation\": true\n                                                            \"pagination\": true\n                                                            \"transitionStyle\":\"fadeUp\" (fade,backSlide,goDown,fadeUp)\n                                                    -->\n							<div class=\"owl-carousel buttons-autohide controlls-over\" data-plugin-options=\'{\"singleItem\": true, \"autoPlay\": true, \"navigation\": true, \"pagination\": true, \"transitionStyle\":\"fade\"}\'>\n								{% topic data = \"slider\",limit= \"5\",cid=category.id,position=\'2\',issub=\"1\",ispic=\"1\"%}\n								{% for v in slider%}\n								<a href=\"{{v.name|get_url(v.id)}}\">\n									<img class=\"img-responsive\" src=\"{{v.cover_id|get_pic(\'m=1,w=848,h=300\')}}\" alt=\"{{v.title}}\">\n								</a>\n								{%endfor%}\n							</div>\n\n							<ul class=\"nav nav-tabs nav-button-tabs\">\n								<!-- side navigation -->\n								{%column data=\"cates\",cid=category.id%}\n								<li class=\"active\"><a href=\"#taball\" data-toggle=\"tab\">全部</a></li>\n								{%for val in cates%}\n								<li ><a href=\"#tab{{val.id}}\" data-toggle=\"tab\">{{val.name}}</a></li>\n								{%endfor%}\n\n							</ul>\n\n							<div class=\"tab-content\">\n								<div class=\"tab-pane fade in active\" id=\"taball\">\n									{% topic data = \"list\",limit= \"10\",cid=category.id,issub=1%}\n									{% for val in list %}\n									{% set categoryname = val.category_id|get_cate %}\n									<!-- POST ITEM -->\n									<div class=\"blog-post-item padding-bottom-20 margin-bottom-20 clearfix\">\n\n										<!-- IMAGE -->\n										{% if val.cover_id>0 %}\n										<figure class=\"blog-item-small-image margin-bottom-0\">\n											<img class=\"img-responsive\" src=\"{{val.cover_id|get_pic(\'m=1,w=200,h=120\')}}\" alt=\"\">\n										</figure>\n										{% endif %}\n										<div class=\"blog-item-small-content\">\n											<h2><a href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\" title=\"{{val.title}}\">{{val.title}}</a></h2>\n\n											<ul class=\"blog-post-info list-inline padding-bottom-10\">\n												<li>\n													<a href=\"#\">\n														<i class=\"fa fa-clock-o\"></i>\n														<span class=\"font-lato\">{{val.update_time|dateformat(\'Y-m-d H:i\')}}</span>\n													</a>\n												</li>\n												<li>\n													<a href=\"#\">\n														<i class=\"fa fa-comment-o\"></i>\n														<span class=\"font-lato\">{{val.view}} </span>\n													</a>\n												</li>\n												<li>\n													<i class=\"fa fa-folder-open-o\"></i>\n\n													<a class=\"category\" href=\"{{categoryname.url}}\">\n														<span class=\"font-lato\">{{categoryname.name}}</span>\n													</a>\n													{#\n													<a class=\"category\" href=\"#\">\n														<span class=\"font-lato\">Photography</span>\n													</a>\n													#}\n												</li>\n												<li>\n													<a href=\"#\">\n														<i class=\"fa fa-user\"></i>\n														<span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n													</a>\n												</li>\n											</ul>\n\n											<p>{{val.description|truncate(90, true, \"...\")}}</p>\n										</div>\n\n									</div>\n									<!-- /POST ITEM -->\n									{%endfor%}\n\n								</div>\n								{%for v in cates%}\n								<div class=\"tab-pane fade  \" id=\"tab{{v.id}}\">\n										{% topic data = \"list\",limit= \"10\",cid=v.id%}\n										{% for val in list %}\n									{% set categoryname = val.category_id|get_cate %}\n										<!-- POST ITEM -->\n										<div class=\"blog-post-item padding-bottom-20 margin-bottom-20 clearfix\">\n\n											<!-- IMAGE -->\n											{% if val.cover_id>0 %}\n											<figure class=\"blog-item-small-image margin-bottom-0\">\n												<img class=\"img-responsive\" src=\"{{val.cover_id|get_pic(\'m=1,w=200,h=120\')}}\" alt=\"\">\n											</figure>\n											{% endif %}\n											<div class=\"blog-item-small-content\">\n												<h2><a href=\"{{val.name|get_url(val.id)}}\">{{val.title}}</a></h2>\n\n												<ul class=\"blog-post-info list-inline padding-bottom-10\">\n													<li>\n														<a href=\"#\">\n															<i class=\"fa fa-clock-o\"></i>\n															<span class=\"font-lato\">{{val.update_time|dateformat(\'Y-m-d H:i\')}}</span>\n														</a>\n													</li>\n													<li>\n														<a href=\"#\">\n															<i class=\"fa fa-comment-o\"></i>\n															<span class=\"font-lato\">{{val.view}}</span>\n														</a>\n													</li>\n													<li>\n														<i class=\"fa fa-folder-open-o\"></i>\n\n														<a class=\"category\" href=\"{{categoryname.url}}\">\n															<span class=\"font-lato\">{{categoryname.name}}</span>\n														</a>\n													</li>\n													<li>\n														<a href=\"#\">\n															<i class=\"fa fa-user\"></i>\n															<span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n														</a>\n													</li>\n												</ul>\n\n									<p>{{val.description|truncate(90, true, \"...\")}}</p>\n								</div>\n\n							</div>\n							<!-- /POST ITEM -->\n							{%endfor%}\n							<!-- PAGINATION -->\n							<div class=\"text-center\">\n								<a href=\"{{v.url}}\" class=\"btn btn-reveal btn-default\">\n									<i class=\"fa fa-plus\"></i>\n									<span>查看全部内容</span>\n								</a>\n							</div>\n							<!-- /PAGINATION -->\n								</div>\n								{%endfor%}\n\n							</div>\n\n						</div>\n						<!-- RIGHT -->\n\n						<div class=\"col-md-3 col-sm-3\">\n\n							<!-- INLINE SEARCH -->\n							<div class=\"inline-search clearfix margin-bottom-30\">\n								<form action=\"\" method=\"get\" class=\"widget_search\">\n									<input type=\"search\" placeholder=\"Start Searching...\" id=\"s\" name=\"s\" class=\"serch-input\">\n									<button type=\"submit\">\n										<i class=\"fa fa-search\"></i>\n									</button>\n								</form>\n							</div>\n							<!-- /INLINE SEARCH -->\n\n							<hr />\n\n							<!-- side navigation -->\n							{%column data=\"cate\",cid=category.id,isnum=\"1\"%}\n							{%if cate%}\n							<div class=\"side-nav margin-bottom-20 margin-top-20\">\n								<div class=\"side-nav-head\">\n									<button class=\"fa fa-bars\"></button>\n									<h4>{{category.title}} 子分类</h4>\n								</div>\n								<ul class=\"list-group list-group-bordered list-group-noicon uppercase\">\n									{%for val in cate%}\n									<li class=\"list-group-item\"><a href=\"{{val.url}}\"><span class=\"size-11 text-muted pull-right\">({{val.doc_num}})</span> {{val.name}}</a></li>\n									{%endfor%}\n								</ul>\n								<!-- /side navigation -->\n							</div>\n							{%endif%}\n\n\n\n							<!-- JUSTIFIED TAB -->\n							<div class=\"tabs nomargin-top hidden-xs margin-bottom-10\">\n\n								<!-- tabs -->\n								<ul class=\"nav nav-tabs nav-bottom-border nav-justified\">\n									<li class=\"active\">\n										<a href=\"#tab_1\" data-toggle=\"tab\">\n											最热\n										</a>\n									</li>\n									<li>\n										<a href=\"#tab_2\" data-toggle=\"tab\">\n											最新\n										</a>\n									</li>\n								</ul>\n\n								<!-- tabs content -->\n								<div class=\"tab-content margin-bottom-0 margin-top-10\">\n\n									<!-- POPULAR -->\n									<div id=\"tab_1\" class=\"tab-pane active\">\n										<!--topic tags test-->\n										{% topic data = \"hotlist\",limit= \"5\",cid=category.id,type=\"hot\"%}\n										<!--返回数据赋值给变量data,遍历data-->\n										{%for v in hotlist%}\n										<div class=\"row tab-post\"><!-- post -->\n											{%if v.cover_id%}\n											<div class=\"col-md-3 col-sm-3 col-xs-3\">\n												<a href=\"{{v.name|get_url(v.id)}}\">\n													<img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n												</a>\n											</div>\n											{%endif%}\n											<div class=\"col-md-9 col-sm-9 col-xs-9\">\n												<a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n												<small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n											</div>\n										</div><!-- /post -->\n										{%endfor%}\n\n									</div>\n									<!-- /POPULAR -->\n\n\n									<!-- RECENT -->\n									<div id=\"tab_2\" class=\"tab-pane\">\n\n										{% topic data = \"newlist\",limit= \"5\",cid=category.id%}\n										{%for v in newlist%}\n										<div class=\"row tab-post\"><!-- post -->\n											{%if v.cover_id%}\n											<div class=\"col-md-3 col-sm-3 col-xs-3\">\n												<a href=\"{{v.name|get_url(v.id)}}\">\n													<img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n												</a>\n											</div>\n											{%endif%}\n											<div class=\"col-md-9 col-sm-9 col-xs-9\">\n												<a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n												<small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n											</div>\n										</div><!-- /post -->\n										{%endfor%}\n										<!-- /post -->\n									</div>\n									<!-- /RECENT -->\n\n								</div>\n\n							</div>\n							<!-- JUSTIFIED TAB -->\n\n\n							<!-- TAGS -->\n							<h3 class=\"hidden-xs size-16 margin-bottom-10\">标签</h3>\n							<div class=\"hidden-xs margin-bottom-20\">\n								{% keywords data =\"kws\",type=\"hot\"%}\n								{%for key in kws%}\n								<a class=\"tag\" href=\"{{key.url}}\">\n									<span class=\"txt\">{{key.keyname}}</span>\n									<span class=\"num\">{{key.videonum}}</span>\n								</a>\n								{%endfor%}\n							</div>\n\n                            {#\n							<!-- TWIITER WIDGET -->\n							<h3 class=\"hidden-xs size-16 margin-bottom-10\">TWITTER FEED</h3>\n							<ul class=\"hidden-xs widget-twitter margin-bottom-60\" data-php=\"php/twitter/tweet.php\" data-username=\"stepofweb\" data-limit=\"3\">\n								<li></li>\n							</ul>\n\n							<!-- FEATURED VIDEO -->\n							<h3 class=\"hidden-xs size-16 margin-bottom-10\">FEATURED VIDEO</h3>\n							<div class=\"hidden-xs embed-responsive embed-responsive-16by9 margin-bottom-60\">\n								<iframe class=\"embed-responsive-item\" src=\"http://player.youku.com/embed/XMTQ0ODQ2Nzg4OA==\" width=\"800\" height=\"450\"></iframe>\n							</div>\n\n							<!-- FLICKR WIDGET -->\n							<h3 class=\"hidden-xs size-16 margin-bottom-10\">FLICKR PHOTO</h3>\n							<div class=\"hidden-xs widget-flickr clearfix lightbox margin-bottom-60\" data-id=\"37304598@N02\" data-limit=\"16\" data-plugin-options=\'{\"delegate\": \"a\", \"gallery\": {\"enabled\": true}}\'></div>\n\n\n							<!-- FACEBOOK -->\n							<iframe class=\"hidden-xs\" src=\"http://player.youku.com/embed/XOTA3NzI0NDI4\" style=\"border:none; overflow:hidden; width:263px; height:258px;\"></iframe>\n#}\n\n							<hr />\n\n\n							<!-- SOCIAL ICONS -->\n							<div class=\"hidden-xs margin-top-30 margin-bottom-60\">\n								<a href=\"#\" class=\"social-icon social-icon-border social-facebook pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n									<i class=\"icon-facebook\"></i>\n									<i class=\"icon-facebook\"></i>\n								</a>\n\n								<a href=\"#\" class=\"social-icon social-icon-border social-twitter pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n									<i class=\"icon-twitter\"></i>\n									<i class=\"icon-twitter\"></i>\n								</a>\n\n								<a href=\"#\" class=\"social-icon social-icon-border social-gplus pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n									<i class=\"icon-gplus\"></i>\n									<i class=\"icon-gplus\"></i>\n								</a>\n\n								<a href=\"#\" class=\"social-icon social-icon-border social-linkedin pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n									<i class=\"icon-linkedin\"></i>\n									<i class=\"icon-linkedin\"></i>\n								</a>\n\n								<a href=\"#\" class=\"social-icon social-icon-border social-rss pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Rss\">\n									<i class=\"icon-rss\"></i>\n									<i class=\"icon-rss\"></i>\n								</a>\n							</div>\n\n						</div>\n					</div>\n\n\n				</div>\n			</section>\n			<!-- / -->\n{% endblock %}\n', 1, 1);
INSERT INTO `cmswing_temp` VALUES (4, 'home', 'cover', 'index', '默认封面模板（手机）', '{% extends \"../inc/base.html\" %}\n{% block content %}\n\n<header class=\"mui-bar mui-bar-nav\">\n    <!--<a class=\"mui-action-back mui-icon mui-icon-left-nav mui-pull-left\"></a>-->\n    <span class=\"mui-icon iconfont icon-cart\"><span class=\"mui-badge\">9</span></span>\n    <h1 class=\"mui-title\"><img src=\"/static/webapp/img/logo.png\" ></h1>\n    <span class=\"mui-icon iconfont icon-search mui-pull-right\"></span>\n</header>\n\n{% include \"../inc/nav.html\" %}\n{% include \"../inc/sliderbar.html\" %}\n    <div class=\" mui-scroll-wrapper list-content\" >\n        <div class=\"mui-scroll\">\n            <!--这里放置真实显示的DOM内容-->\n\n{% topic data = \"slider\",limit= \"5\",cid=category.id,position=\'2\',issub=\"1\",ispic=\"1\"%}\n<div id=\"sliderpic\" class=\"mui-slider\">\n    <div class=\"mui-slider-group mui-slider-loop\">\n        <!-- 额外增加的一个节点(循环轮播：第一个节点是最后一张轮播) -->\n        {%set pnus = slider|length -1%}\n        <div class=\"mui-slider-item mui-slider-item-duplicate\">\n            <a href=\"{{slider[pnus].name|get_url(slider[pnus].id)}}\">\n                <img src=\"{{slider[pnus].cover_id|get_pic(\'m=1,w=360,h=130\')}}\">\n                <p class=\"mui-slider-title\">{{slider[pnus].title}}</p>\n            </a>\n        </div>\n        {% for v in slider%}\n        <div class=\"mui-slider-item\">\n            <a href=\"{{v.name|get_url(v.id)}}\">\n                <img src=\"{{v.cover_id|get_pic(\'m=1,w=360,h=130\')}}\">\n                <p class=\"mui-slider-title\">{{v.title}}</p>\n            </a>\n        </div>\n        {%endfor%}\n        <!-- 额外增加的一个节点(循环轮播：最后一个节点是第一张轮播) -->\n        <div class=\"mui-slider-item mui-slider-item-duplicate\">\n            <a href=\"{{slider[0].name|get_url(slider[0].id)}}\">\n                <img src=\"{{slider[0].cover_id|get_pic(\'m=1,w=360,h=130\')}}\">\n                <p class=\"mui-slider-title\">{{slider[0].title}}</p>\n            </a>\n        </div>\n    </div>\n    <div class=\"mui-slider-indicator mui-text-right\">\n        {%set isone = 0%}\n        {% for v in slider%}\n        <div class=\"mui-indicator {%if isone == 0 %}mui-active{%endif%}\"></div>\n        {%set isone = isone+1%}\n        {%endfor%}\n    </div>\n</div>\n    <ul class=\"mui-table-view \" >\n    {% topic data = \"list\",limit= \"5\",cid=category.id,issub=1%}\n        <li class=\"mui-table-view-divider\">最新</li>\n    {% for v in list %}\n    {% set categoryname = v.category_id|get_cate %}\n        <!--如果有缩略图-->\n        {%if v.cover_id >0%}\n        {% if v.model_id == 5%}\n        <li class=\"mui-table-view-cell mui-media\">\n            <a href=\"{{v.name|get_url(v.id)}}\">\n\n\n                <h4 class=\"dotdot line2 margin-bottom-6\">{{v.title}}</h4>\n\n\n                <div class=\"mui-row\">\n                    <div class=\"mui-col-sm-12 mui-col-xs-12\">\n                        <img class=\"list_img_holder\" src=\"{{v.cover_id|get_pic(\'m=0,w=360\')}}\">\n                    </div>\n                </div>\n                <p class=\"item_info\">\n\n                    <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                    <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                    <span class=\"time\" title=\"2016-08-30 11:06\">{{v.update_time|moment}}</span>\n\n                </p>\n\n            </a>\n        </li>\n        {%else%}\n        <li class=\"mui-table-view-cell mui-media\">\n            <a href=\"{{v.name|get_url(v.id)}}\" >\n                <div class=\"mui-row\">\n                    <div class=\"mui-col-sm-8 mui-col-xs-8\">\n\n                        <h4 class=\"dotdot line2 \">{{v.title}}</h4>\n                        <p class=\"item_info\">\n\n                            <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                            <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                            <span class=\"time\" title=\"2016-08-30 11:06\">{{v.update_time|moment}}</span>\n\n                        </p>\n                    </div>\n                    <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                        <img class=\"list_img_holder\" src=\"{{v.cover_id|get_pic(\'m=1,w=123,h=82\')}}\" >\n\n                    </div>\n                </div>\n            </a>\n        </li>\n        {%endif%}\n        {%elif v.pics%}\n        {% set pics = v.pics|strToArray%}\n        {% if pics|length >2%}\n        <li class=\"mui-table-view-cell mui-media\">\n            <a href=\"{{v.name|get_url(v.id)}}\">\n                <h4 class=\"dotdot line2 margin-bottom-6\">{{v.title}}</h4>\n                <div class=\"mui-row\">\n                    <div class=\"mui-col-sm-4 mui-col-xs-4\">\n                        <img class=\"list_img_holder\" src=\"{{pics[0]|get_pic(\'m=1,w=123,h=82\')}}\">\n                    </div>\n                    <div class=\"mui-col-sm-4 mui-col-xs-4\">\n                        <img class=\"list_img_holder\" src=\"{{pics[1]|get_pic(\'m=1,w=123,h=82\')}}\">\n                    </div>\n                    <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                        <img class=\"list_img_holder\" src=\"{{pics[2]|get_pic(\'m=1,w=123,h=82\')}}\" >\n\n                    </div>\n                </div>\n                <p class=\"item_info\">\n\n                    <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                    <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                    <span class=\"time\" title=\"2016-08-30 11:06\">{{v.update_time|moment}}</span>\n\n                </p>\n\n            </a>\n        </li>\n        {%else%}\n        <li class=\"mui-table-view-cell mui-media\">\n            <a href=\"{{v.name|get_url(v.id)}}\" >\n                <div class=\"mui-row\">\n                    <div class=\"mui-col-sm-8 mui-col-xs-8\">\n\n                        <h4 class=\"dotdot line2 \">{{v.title}}</h4>\n                        <p class=\"item_info\">\n                            <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                            <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                            <span class=\"time\" title=\"2016-08-30 11:06\">{{v.update_time|moment}}</span>\n\n                        </p>\n                    </div>\n                    <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                        <img class=\"list_img_holder\" src=\"{{pics[0]|get_pic(\'m=1,w=123,h=82\')}}\" >\n\n                    </div>\n                </div>\n            </a>\n        </li>\n        {%endif%}\n        {%else%}\n        <!--无图-->\n        <li class=\"mui-table-view-cell mui-media\">\n            <a href=\"{{v.name|get_url(v.id)}}\">\n                <h4 class=\"dotdot line2 margin-bottom-6\">{{v.title}}</h4>\n\n                <p class=\"item_info\">\n\n                    <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                    <span class=\"cmt space\">评论&nbsp;0</span>\n\n                    <span class=\"time\" title=\"2016-08-30 11:06\">{{v.update_time|moment}}</span>\n\n                </p>\n\n            </a>\n        </li>\n        {%endif%}\n    {%endfor%}\n        {%column data=\"cates\",cid=category.id%}\n        {%for val in cates%}\n        <li class=\"mui-table-view-divider\">{{val.name}}</li>\n        {% topic data = \"list\",limit= \"5\",cid=val.id%}\n        {% for v in list %}\n        {% set categoryname = v.category_id|get_cate %}\n        <!--如果有缩略图-->\n        {%if v.cover_id >0%}\n        {% if v.model_id == 5%}\n        <li class=\"mui-table-view-cell mui-media\">\n            <a href=\"{{v.name|get_url(v.id)}}\">\n\n\n                <h4 class=\"dotdot line2 margin-bottom-6\">{{v.title}}</h4>\n\n\n                <div class=\"mui-row\">\n                    <div class=\"mui-col-sm-12 mui-col-xs-12\">\n                        <img class=\"list_img_holder\" src=\"{{v.cover_id|get_pic(\'m=0,