INSERT INTO `cmswing_temp` VALUES (5, 'home', 'index', 'index', '首页模版', '{% extends \"./inc_base.html\" %}\n{% block style %}\n<link href=\"/static/assets/css/layout-question.css\" rel=\"stylesheet\" type=\"text/css\" />\n{% endblock %}\n{% block content %}\n<section class=\"alternate parallax-2\">\n	<div class=\"overlay dark-0\"><!-- dark overlay [1 to 9 opacity] --></div>\n	<div class=\"container \" style=\"position: relative;z-index: 10\">\n		{{11|show_ad(\"code\")|safe}}\n		<div class=\"row\">\n			<div class=\"col-md-9\">\n				<!-- REVOLUTION SLIDER -->\n				<div class=\"slider fullwidthbanner-container roundedcorners margin-bottom-0\">\n					<!--\n                        Navigation Styles:\n\n                            data-navigationStyle=\"\" theme default navigation\n\n                            data-navigationStyle=\"preview1\"\n                            data-navigationStyle=\"preview2\"\n                            data-navigationStyle=\"preview3\"\n                            data-navigationStyle=\"preview4\"\n\n                        Bottom Shadows\n                            data-shadow=\"1\"\n                            data-shadow=\"2\"\n                            data-shadow=\"3\"\n\n                        Slider Height (do not use on fullscreen mode)\n                            data-height=\"300\"\n                            data-height=\"350\"\n                            data-height=\"400\"\n                            data-height=\"450\"\n                            data-height=\"500\"\n                            data-height=\"550\"\n                            data-height=\"600\"\n                            data-height=\"650\"\n                            data-height=\"700\"\n                            data-height=\"750\"\n                            data-height=\"800\"\n                    -->\n					<div class=\"fullwidthbanner\" data-height=\"550\" data-shadow=\"\" data-navigationStyle=\"\">\n						<ul class=\"hide\">\n							{% topic data = \"slider\",limit= \"10\",position=\'2\',issub=\"1\"%}\n							{%for val in slider%}\n							{%if val.model_id==4%} {% set pic = val.pics|strToArray%}{%else%}{%set pic = val.cover_id%}{%endif%}\n							<!-- SLIDE  -->\n							<li data-transition=\"fade\" data-slotamount=\"7\" data-masterspeed=\"300\"  data-saveperformance=\"off\" >\n								<img src=\"/static/assets/images/1x1.png\" data-lazyload=\"{{pic|get_pic(\'m=2,w=848,h=400\')}}\" alt=\"\" data-bgfit=\"cover\" data-bgposition=\"center center\" data-bgrepeat=\"no-repeat\" />\n\n								<div class=\"tp-caption lft start\"\n									 data-x=\"0\"\n									 data-y=\"0\"\n									 data-speed=\"750\"\n									 data-start=\"750\"\n									 data-easing=\"easeOutExpo\"\n									 data-elementdelay=\"0.1\"\n									 data-endelementdelay=\"0.1\"\n									 data-endspeed=\"300\">\n									<img src=\"/static/assets/images/1x1.png\" alt=\"\" data-lazyload=\"/static/assets/images/caption_bg.png\">\n								</div>\n\n								<a class=\"tp-caption medium_light_white lfb start\" href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\"\n								   data-x=\"left\" data-hoffset=\"70\"\n								   data-y=\"96\"\n								   data-speed=\"300\"\n								   data-start=\"1200\"\n								   data-easing=\"easeOutExpo\"\n								   data-splitin=\"none\"\n								   data-splitout=\"none\"\n								   data-elementdelay=\"0.1\"\n								   data-endelementdelay=\"0.1\"\n								   data-endspeed=\"300\">\n									{{val.title|truncate(20, true, \"\")|insert_flg(\"<br>\",10)|safe}}\n								</a>\n\n								<div class=\"tp-caption block_black lfl start\"\n									 data-x=\"left\" data-hoffset=\"70\"\n									 data-y=\"195\"\n									 data-speed=\"300\"\n									 data-start=\"1500\"\n									 data-easing=\"easeOutExpo\"\n									 data-splitin=\"none\"\n									 data-splitout=\"none\"\n									 data-elementdelay=\"0.1\"\n									 data-endelementdelay=\"0.1\"\n									 data-endspeed=\"300\" >\n									{{val.update_time|dateformat(\'Y-m-d H:i\')}}\n								</div>\n\n								<div class=\"tp-caption small_light_white lfl start\"\n									 data-x=\"left\" data-hoffset=\"70\"\n									 data-y=\"250\"\n									 data-speed=\"1000\"\n									 data-start=\"1800\"\n									 data-easing=\"easeOutExpo\"\n									 data-splitin=\"none\"\n									 data-splitout=\"none\"\n									 data-elementdelay=\"0.1\"\n									 data-endelementdelay=\"0.1\"\n									 data-endspeed=\"300\" >\n									{{val.description|truncate(90, true, \"...\")|insert_flg(\"<br>\",20)|safe}}\n								</div>\n							</li>\n							{%endfor%}\n\n\n						</ul>\n\n						<div class=\"tp-bannertimer\"><!-- progress bar --></div>\n					</div>\n				</div>\n				<!-- /REVOLUTION SLIDER -->\n			</div>\n			<div class=\"col-md-3\">\n				<div class=\"box-light padding-10 \">\n					<div class=\"box-inner\">\n						<h3>\n							<a class=\"pull-right size-11 text-warning\" href=\"#\"></a>\n							热点\n						</h3>\n						<div class=\"slimscroll\" style=\"height: 247px\" data-always-visible=\"true\" data-size=\"5px\" data-position=\"right\" data-opacity=\"0.4\" disable-body-scroll=\"true\">\n							{%model data=\"authorlist\",table=\"document\",order=\"update_time DESC\",limit=\"10\"%}\n							{% for val in authorlist %}\n							<div class=\"clearfix margin-bottom-10\"><!-- post item -->\n								<img class=\"thumbnail pull-left margin-right-10\" src=\"{{val.cover_id|get_pic(\'m=1,w=100,h=100\')}}\" width=\"60\" height=\"60\" alt=\"\" />\n								<h4 class=\"size-14 nomargin noborder nopadding\"><a href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\">{{val.title}}</a></h4>\n								<span class=\"size-12 text-muted\">{{val.description|truncate(20, true, \"...\")}}</span>\n							</div><!-- /post item -->\n							{%endfor%}\n\n						</div>\n					</div>\n\n					<div class=\"box-footer\">\n						<!-- INLINE SEARCH -->\n						<div class=\"inline-search clearfix\">\n							<form action=\"#\" method=\"get\" class=\"widget_search nomargin\">\n								<input type=\"search\" placeholder=\"Search Post...\" name=\"s\" class=\"serch-input\">\n								<button type=\"submit\">\n									<i class=\"fa fa-search\"></i>\n								</button>\n								<div class=\"clear\"></div>\n							</form>\n						</div>\n						<!-- /INLINE SEARCH -->\n\n					</div>\n\n				</div>\n			</div>\n\n		</div>\n\n		{%if 14|show_ad(\'code\')%}\n		<!-- INFO BAR -->\n		<div class=\"margin-top-20\">\n			{{14|show_ad(\'code\')|safe}}\n		</div>\n		<!-- /INFO BAR -->\n		{%endif%}\n	</div>\n</section>\n<section>\n	<div class=\"container\">\n\n		<div class=\"row\">\n\n			<!-- RIGHT -->\n			<div class=\"col-sm-9\">\n				<ul class=\"nav nav-tabs nav-justified\">\n					<li class=\"active\"><a href=\"#new\" data-toggle=\"tab\">最新</a></li>\n					<li ><a href=\"#hot\" data-toggle=\"tab\">最热</a></li>\n					<li><a href=\"#wenda\" data-toggle=\"tab\">问答</a></li>\n					{#\n					<li class=\"dropdown\">\n						<a href=\"#\" class=\"dropdown-toggle\" data-toggle=\"dropdown\">Dropdown <span class=\"caret\"></span></a>\n						<ul class=\"dropdown-menu\">\n							<li><a href=\"#dropdown1\" tabindex=\"-1\" data-toggle=\"tab\">@fat</a></li>\n							<li><a href=\"#dropdown2\" tabindex=\"-1\" data-toggle=\"tab\">@mdo</a></li>\n						</ul>\n					</li>\n					#}\n				</ul>\n\n				<div class=\"tab-content\">\n					<div class=\"tab-pane fade in active\" id=\"new\">\n						{% topic data = \"newlist\",limit=\"10\"%}\n						{% for val in newlist %}\n						{% set categoryname = val.category_id|get_cate %}\n						{%if val.model_id==4%}\n						{% set pic = val.pics|strToArray%}\n						<!-- POST ITEM -->\n						<div class=\"blog-post-item padding-bottom-20 margin-bottom-20 clearfix\">\n\n							<!-- IMAGE -->\n							{% if pic[0]>0 %}\n							<figure class=\"blog-item-small-image margin-bottom-0\">\n								<img class=\"img-responsive\" src=\"{{pic[0]|get_pic(\'m=1,w=200,h=120\')}}\" alt=\"\">\n							</figure>\n							{% endif %}\n							<div class=\"blog-item-small-content\">\