INSERT INTO `cmswing_temp` VALUES (6, 'home', 'inc', 'base', '基础模板(PC)', '<!DOCTYPE html>\n<!--[if IE 8]>			<html class=\"ie ie8\"> <![endif]-->\n<!--[if IE 9]>			<html class=\"ie ie9\"> <![endif]-->\n<!--[if gt IE 9]><!-->\n<html>\n<!--<![endif]-->\n\n<head>\n	<meta charset=\"utf-8\" />\n	<meta name=\"renderer\" content=\"webkit\" />\n	<title>{{controller.meta_title}}| {{controller.setup.WEB_SITE_TITLE}}</title>\n	<meta name=\"keywords\" content=\"{{controller.keywords}}\" />\n	<meta name=\"description\" content=\"{{controller.description}}\" />\n	<meta name=\"Author\" content=\"Arterli [www.cmswing.com]\" />\n	{%if controller.setup.QQ_VALIDATION%}\n	<!--qq互联网站验证-->\n	{{controller.setup.QQ_VALIDATION|safe}}\n	<!--qq互联网站验证结束-->\n	{%endif%}\n	{%if controller.setup.SINA_VALIDATION%}\n	<!--新浪微博网站验证-->\n	{{controller.setup.SINA_VALIDATION|safe}}\n	<!--新浪微博网站验证结束-->\n	{%endif%}\n	<link rel=\"shortcut icon\" type=\"image/x-icon\" href=\"/favicon.ico\" />\n	<!-- mobile settings -->\n	<meta name=\"viewport\" content=\"width=device-width, maximum-scale=1, initial-scale=1, user-scalable=0\" />\n	<!--[if IE]><meta http-equiv=\'X-UA-Compatible\' content=\'IE=edge,chrome=1\'><![endif]-->\n\n	<!-- WEB FONTS : use %7C instead of | (pipe) -->\n	<!--<link href=\"http://fonts.useso.com/css?family=Open+Sans:300,400%7CRaleway:300,400,500,600,700%7CLato:300,400,400italic,600,700\" rel=\"stylesheet\" type=\"text/css\" />-->\n\n	<!-- CORE CSS -->\n	<link href=\"/static/assets/plugins/bootstrap/css/bootstrap.min.css\" rel=\"stylesheet\" type=\"text/css\" />\n\n	<!-- REVOLUTION SLIDER -->\n	<link href=\"/static/assets/plugins/slider.revolution/css/extralayers.css\" rel=\"stylesheet\" type=\"text/css\" />\n	<link href=\"/static/assets/plugins/slider.revolution/css/settings.css\" rel=\"stylesheet\" type=\"text/css\" />\n\n	<!-- THEME CSS -->\n	<link href=\"/static/assets/css/essentials.css\" rel=\"stylesheet\" type=\"text/css\" />\n	<link href=\"/static/assets/css/layout.css\" rel=\"stylesheet\" type=\"text/css\" />\n\n	<!-- PAGE LEVEL SCRIPTS -->\n	<link href=\"/static/assets/css/header-1.css\" rel=\"stylesheet\" type=\"text/css\" />\n	<link href=\"/static/assets/css/color_scheme/blue.css\" rel=\"stylesheet\" type=\"text/css\" id=\"color_scheme\" />\n	{% block style %}{% endblock %}\n	{%if controller.setup.WEB_TONGJI%}\n	<!--统计代码-->\n	{{controller.setup.WEB_TONGJI|safe}}\n	<!--统计-->\n	{%endif%}\n</head>\n\n<!--\n		AVAILABLE BODY CLASSES:\n\n		smoothscroll 			= create a browser smooth scroll\n		enable-animation		= enable WOW animations\n\n		bg-grey					= grey background\n		grain-grey				= grey grain background\n		grain-blue				= blue grain background\n		grain-green				= green grain background\n		grain-blue				= blue grain background\n		grain-orange			= orange grain background\n		grain-yellow			= yellow grain background\n\n		boxed 					= boxed layout\n		pattern1 ... patern11	= pattern background\n		menu-vertical-hide		= hidden, open on click\n\n		BACKGROUND IMAGE [together with .boxed class]\n		data-background=\"assets/images/boxed_background/1.jpg\"\n	-->\n\n<body class=\"smoothscroll enable-animation \">\n\n	<!-- SLIDE TOP -->\n	{% include \"./inc_slidetop.html\" %}\n	<!-- /SLIDE TOP -->\n\n\n	<!-- wrapper -->\n	<div id=\"wrapper\" >\n\n		<!-- Top Bar -->\n		{% include \"./inc_topbar.html\" %}\n		<!-- /Top Bar -->\n\n		<!--HEADER-->\n		{% include \"./inc_header.html\" %}\n		<!--/HEADER-->\n		<!-- REVOLUTION SLIDER -->\n		{% block content %}{% endblock %}\n		<!-- FOOTER -->\n		<footer id=\"footer\" class=\"footer-light\" >\n			<div class=\"container\">\n\n				<div class=\"row\">\n\n					<div class=\"col-md-3\">\n						<!-- Footer Logo -->\n						<img class=\"footer-logo\" src=\"/static/assets/images/logo-footer-dark.png\" alt=\"\" />\n\n						<!-- Contact Address -->\n						<address>\n								<ul class=\"list-unstyled\">\n									<li class=\"footer-sprite address\">\n										中国 陕西\n										西安雁塔区高新路\n										望庭国际1号楼2单元24层\n									</li>\n									<!--<li class=\"footer-sprite phone\">-->\n										<!--电话: 86-029-88365435-->\n									<!--</li>-->\n									<li class=\"footer-sprite email\">\n										<a href=\"mailto:arterli@cmswing.com\">arterli@cmswing.com</a>\n									</li>\n								</ul>\n							</address>\n						<!-- /Contact Address -->\n\n					</div>\n\n					<div class=\"col-md-3\">\n\n						<!-- Latest Blog Post -->\n						<h4 class=\"letter-spacing-1\">功能更新</h4>\n						<ul class=\"footer-posts list-unstyled\">\n							{% topic data = \"data\",limit= \"3\",cid=\"118\"%}\n							{%for t in data%}\n							<li>\n								<a href=\"{{t.name|get_url(t.id)}}\">{{t.title}}</a>\n								<small>{{t.update_time|moment(\'lll\')}}</small>\n							</li>\n							{%endfor%}\n						</ul>\n						<!-- /Latest Blog Post -->\n\n					</div>\n\n					<div class=\"col-md-2\">\n\n						<!-- Links -->\n						<h4 class=\"letter-spacing-1\">认识我们</h4>\n						<ul class=\"footer-links list-unstyled\">\n							<li><a href=\"/\">网站首页</a></li>\n							<li><a href=\"/about.html\">关于我们</a></li>\n							<li><a href=\"/documentation\">二次开发</a></li>\n							<li><a href=\"/donate.html\">捐助我们</a></li>\n							<li><a href=\"/disclaimer.html\">免责声明</a></li>\n							<li><a href=\"/licence.html\">授权协议</a></li>\n							<li><a href=\"/contribute.html\">贡献者名单</a></li>\n						</ul>\n						<!-- /Links -->\n\n					</div>\n\n					<div class=\"col-md-4\">\n\n						<!-- Newsletter Form -->\n						<h4 class=\"letter-spacing-1\">邮件订阅</h4>\n						<p>订阅我们的邮件，以获得重要版本升级信息</p>\n\n						<form class=\"validate\" action=\"php/newsletter.php\" method=\"post\" data-success=\"Subscribed! Thank you!\" data-toastr-position=\"bottom-right\">\n							<div class=\"input-group\">\n								<span class=\"input-group-addon\"><i class=\"fa fa-envelope\"></i></span>\n								<input type=\"email\" id=\"email\" name=\"email\" class=\"form-control required\" placeholder=\"请输入您的邮箱\">\n								<span class=\"input-group-btn\">\n										<button class=\"btn btn-primary\" type=\"submit\">订阅</button>\n									</span>\n							</div>\n						</form>\n						<!-- /Newsletter Form -->\n\n						<!-- Social Icons -->\n						<div class=\"margin-top-20\">\n							<a href=\"#\" class=\"social-icon social-icon-border social-facebook pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n\n								<i class=\"icon-facebook\"></i>\n								<i class=\"icon-facebook\"></i>\n							</a>\n\n							<a href=\"#\" class=\"social-icon social-icon-border social-twitter pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n								<i class=\"icon-twitter\"></i>\n								<i class=\"icon-twitter\"></i>\n							</a>\n\n							<a href=\"#\" class=\"social-icon social-icon-border social-gplus pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n								<i class=\"icon-gplus\"></i>\n								<i class=\"icon-gplus\"></i>\n							</a>\n\n							<a href=\"#\" class=\"social-icon social-icon-border social-linkedin pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n								<i class=\"icon-linkedin\"></i>\n								<i class=\"icon-linkedin\"></i>\n							</a>\n\n							<a href=\"#\" class=\"social-icon social-icon-border social-rss pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Rss\">\n								<i class=\"icon-rss\"></i>\n								<i class=\"icon-rss\"></i>\n							</a>\n\n						</div>\n						<!-- /Social Icons -->\n\n					</div>\n\n				</div>\n\n			</div>\n\n			<div class=\"copyright\">\n				<div class=\"container\">\n					<ul class=\"pull-right nomargin list-inline mobile-block\">\n						<li><a href=\"#\">条款与条件</a></li>\n						<li>&bull;</li>\n						<li><a href=\"#\">隐私</a></li>\n					</ul>\n					&copy; 2015 - <script>document.write(new Date().getFullYear())</script> cmswing.com  版权所有 <!--{{controller.setup.WEB_SITE_TITLE}}--> <a href=\"http://www.miitbeian.gov.cn/\" target=\"_blank\">{{controller.setup.WEB_SITE_ICP}}</a>\n					<noscript><a href=\"http://www.51.la/?19065555\" target=\"_blank\"><img alt=\"&#x6211;&#x8981;&#x5566;&#x514D;&#x8D39;&#x7EDF;&#x8BA1;\" src=\"http://img.users.51.la/19065555.asp\" style=\"border:none\" /></a></noscript>\n				</div>\n			</div>\n		</footer>\n		<!-- /FOOTER -->\n\n	</div>\n	<!-- /wrapper -->\n\n\n	<!--\n			SIDE PANEL\n\n				sidepanel-dark 			= dark color\n				sidepanel-light			= light color (white)\n				sidepanel-theme-color		= theme color\n\n				sidepanel-inverse		= By default, sidepanel is placed on right (left for RTL)\n								If you add \"sidepanel-inverse\", will be placed on left side (right on RTL).\n		-->\n	<div id=\"sidepanel\" class=\"sidepanel-light\">\n		<a id=\"sidepanel_close\" href=\"#\">\n			<!-- close -->\n			<i class=\"glyphicon glyphicon-remove\"></i>\n		</a>\n\n		<div class=\"sidepanel-content\">\n			<h2 class=\"sidepanel-title\">网站导航</h2>\n\n			<!-- SIDE NAV -->\n			<ul class=\"list-group\">\n\n                 {% channel data=\'channel\', num=\'4\'%} {% for val in channel%}\n						<li class=\"list-group-item {% if val.children %}list-toggle{% endif %} {% if http.url == val.url %}active{% endif %} \">\n\n							<a {% if val.children%}data-toggle=\"collapse\" data-parent=\"#sidebar-nav\" href=\"#collapse-{{val.id}}\" class=\"collapsed\"{% else %} href=\"{{val.url}}\" {% endif %}>\n                                {% if val.children%}<i class=\"ico-dd icon-angle-down\"><!-- Drop Down Indicator --></i>{% endif %}\n                                <i class=\"ico-category et-heart\"></i> {{val.title}}</a> \n                                \n                                {% if val.children%}\n							<ul id=\"collapse-{{val.id}}\" class=\"list-unstyled collapse\">\n								{% for _val in val.children%}\n								<li class=\"{% if _val.children%}{% endif %}\">\n									<a {% if _val.children%}class=\"\" {% endif %} href=\"{{_val.url}}\"><i class=\"fa fa-angle-right\"> </i>{{_val.title}}</a> {% if _val.children%}\n									<ul class=\"list-unstyled\">\n										{% for __val in _val.children%}\n                                        <span class=\"badge hide\">New</span>\n										<a href=\"{{__val.url}}\"> <li><i class=\"fa fa-angle-right\"> </i> {{__val.title}}</a></li>\n										{% endfor %}\n									</ul>\n									{% endif %}\n								</li>\n								{% endfor %}\n							</ul>\n							{% endif %}\n						</li>\n						{% endfor%}\n				\n				\n			</ul>\n			<!-- /SIDE NAV -->\n\n			<!-- social icons -->\n			<div class=\"text-center margin-bottom-30\">\n\n				<a href=\"#\" class=\"social-icon social-icon-sm social-facebook\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n					<i class=\"icon-facebook\"></i>\n					<i class=\"icon-facebook\"></i>\n				</a>\n\n				<a href=\"#\" class=\"social-icon social-icon-sm social-twitter\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n					<i class=\"icon-twitter\"></i>\n					<i class=\"icon-twitter\"></i>\n				</a>\n\n				<a href=\"#\" class=\"social-icon social-icon-sm social-linkedin\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n					<i class=\"icon-linkedin\"></i>\n					<i class=\"icon-linkedin\"></i>\n				</a>\n\n				<a href=\"#\" class=\"social-icon social-icon-sm social-rss\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"RSS\">\n					<i class=\"icon-rss\"></i>\n					<i class=\"icon-rss\"></i>\n				</a>\n\n			</div>\n			<!-- /social icons -->\n\n		</div>\n\n	</div>\n	<!-- /SIDE PANEL -->\n\n\n	<!-- SCROLL TO TOP -->\n	<a href=\"#\" id=\"toTop\"></a>\n\n{#\n	<!-- PRELOADER -->\n	<div id=\"preloader\">\n		<div class=\"inner\">\n			<span class=\"loader\"></span>\n		</div>\n	</div>\n	<!-- /PRELOADER -->\n#}\n\n	<!-- JAVASCRIPT FILES -->\n\n	<script type=\"text/javascript\" src=\"/static/assets/plugins/jquery/jquery-2.1.4.min.js\"></script>\n	<script type=\"text/javascript\">\n		var plugin_path = \'/static/assets/plugins/\';\n		$(function () {\n			if($(\"#topMain.nav-pills>li.active>a\")){\n				var w = $(\"#topMain.nav-pills>li.active>a\").width();\n				$(\"#topMain.nav-pills>li.active>.aline\").width(w);\n			}\n\n		})\n	</script>\n	<script type=\"text/javascript\" src=\"/static/assets/js/scripts.js\"></script>\n{#\n	<!-- STYLESWITCHER - REMOVE -->\n	<script async type=\"text/javascript\" src=\"/static/assets/plugins/styleswitcher/styleswitcher.js\"></script>\n#}\n	<!-- REVOLUTION SLIDER -->\n	<script type=\"text/javascript\" src=\"/static/assets/plugins/slider.revolution/js/jquery.themepunch.tools.min.js\"></script>\n	<script type=\"text/javascript\" src=\"/static/assets/plugins/slider.revolution/js/jquery.themepunch.revolution.min.js\"></script>\n	<script type=\"text/javascript\" src=\"/static/assets/js/view/demo.revolution_slider.js\"></script>\n	{% block script %}{% endblock %}\n    <!--[if lte IE 9]>\n    <script src=\"/static/assets/plugins/jquery-fly/requestAnimationFrame.js\"></script>\n    <![endif]-->\n</body>\n\n</html>\n', 1, 1);
INSERT INTO `cmswing_temp` VALUES (7, 'home', 'list', 'index', '默认列表模板', '{% extends \"./inc_base.html\" %}\n{% block content %}\n{%set cate = category.id|get_cate()%}\n{%if category.pid !=0%}\n{%column data=\"column\",pid=category.pid%}\n{%else%}\n{%column data=\"column\",cid=category.id%}\n{%endif%}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header page-header-xs\" >\n    <div class=\"container\">\n        {%if column|length>0%}\n        <h1>{{category.title}}</h1>\n        {%endif%}\n        <!-- breadcrumbs -->\n        <ol class=\"breadcrumb {%if column|length==0%} breadcrumb-inverse {%endif%}\">\n            <li><a href=\"/\">首页</a></li>\n            {%for val in breadcrumb %}\n            {% if val.id == category.id %}\n            <li class=\"active\">{{val.title}}</li>\n            {% else %}\n            <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n\n            {% endif %}\n            {% endfor %}\n        </ol><!-- /breadcrumbs -->\n        <!-- page tabs -->\n\n        {%if column|length>0%}\n        <ul class=\"page-header-tabs list-inline\">\n\n            <li {%if breadcrumb[0].id == category.id %}class=\"active\"{%endif%}><a href=\"{{breadcrumb[0].url}}\">全部</a></li>\n            {%for val in column%}\n            <li  {%if val.id == category.id %}class=\"active\"{%endif%}><a href=\"{{val.url}}\">{{val.name}}</a></li>\n            {%endfor%}\n        </ul>\n        {%endif%}\n        <!-- /page tabs -->\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section >\n    <div class=\"container\">\n\n        <!--分类信息-->\n        {% include \"./inc_sort.html\" %}\n        <!--/分类信息-->\n        <div class=\"row\">\n\n            <!-- LEFT -->\n            <div class=\"col-md-9 col-sm-9\">\n                <div class=\"panel panel-default\">\n                    <div class=\"panel-body padding-10\">\n                        <div class=\"text-left\">\n                            <ul class=\"nav nav-pills mix-filter\">\n\n                                <li class=\"filter {%if group_id==0%}active{%endif%}\"><a href=\"{{cate.url}}\">全部</a></li>\n                                {% groups data=\"groups\",cid=category.id%}\n                                {%for v in groups%}\n                                <li class=\"filter {%if group_id==v.id%}active{%endif%}\"><a href=\"{{cate.url}}-{{order}}-{{v.id}}{%if sort%}-{{sortid}}{%endif%}{%if sorturl %}-{%endif%}{{sorturl}}\">{{v.name}}</a></li>\n                                {%endfor%}\n                            </ul>\n                            {%if category.id|priv(controller.user.roleid,\'add\',0,false)%}\n                            <!--投稿按钮-->\n                            <div class=\"btn-group btn-group-sm pull-right\">\n                                <a {%if model|length> 1 %} data-toggle=\"dropdown\" {% endif %} class=\"btn btn-primary btn-sm document_add\n                                    dropdown-toggle {% if allow == 0 %}disabled{% endif %}\" {%if model|length\n                                    == 1 %} href=\"/center/publish/add/model_id/{{modellist[0].id}}/pid/0/cate_id/{{cate.id}}/group_id/{{group_id}}/sortid/{{sortid}}\"{%endif%}\n                                    ><span class=\"fa fa-pencil-square-o\"></span> 投稿 {%if model|length > 1 %}<span class=\"caret\"></span>{% endif %}</a>\n                                {%if model|length > 1 %}\n                                <ul class=\"dropdown-menu\">\n\n                                    {% for val in modellist%}\n                                    <li> <a href=\"/center/publish/add/model_id/{{val.id}}/pid/0/cate_id/{{cate.id}}/group_id/{{group_id}}/sortid/{{sortid}}\"\n                                            class=\"text-info\">添加{{val.title}}</a></li>\n                                    {% endfor %}\n\n                                    <!--<li class=\"divider\"></li>-->\n                                    <!--<li><a href=\"#\">添加内容</a></li>-->\n                                </ul>\n                                {% endif %}\n                            </div>\n                            <!--//投稿按钮-->\n                            {%endif%}\n                            <div class=\"btn-group btn-group-sm pull-right\">\n                                <a class=\"btn btn-default height-auto {%if order<2%}active{%endif%}\" href=\"{{cate.url}}-{%if order<2%}{{1-order}}{%else%}0{%endif%}{%if group_id or sort%}-{{group_id}}{%endif%}{%if sort%}-{{sortid}}{%endif%}{%if sorturl %}-{%endif%}{{sorturl}}\" >按时间 <span class=\"glyphicon {%if order ==0%}glyphicon-arrow-down{%elif order==1%}glyphicon-arrow-up{%else%}glyphicon-arrow-down{%endif%}\"></span> </a>\n                                <a class=\"btn btn-default height-auto {%if order>=2%}active{%endif%}\" href=\"{{cate.url}}-{%if order>=2%}{%if order ==2%}3{%else%}2{%endif%}{%else%}2{%endif%}{%if group_id or sort%}-{{group_id}}{%endif%}{%if sort%}-{{sortid}}{%endif%}{%if sorturl %}-{%endif%}{{sorturl}}\" style=\"height: auto\">按人气<span class=\"glyphicon  {%if order ==2%}glyphicon-arrow-down{%elif order==3%}glyphicon-arrow-up{%else%}glyphicon-arrow-down{%endif%}\"></span></a>\n                            </div>\n\n                            <div class=\"divider divider-circle divider-center margin-top-0\"><!-- divider -->\n                                <a href=\"#\"><i class=\"fa fa-rss\"></i></a>\n                            </div>\n                        </div>\n\n                        {% for val in list %}\n                        {% set categoryname = val.category_id|get_cate %}\n                        <!-- POST ITEM -->\n                        <div class=\"blog-post-item padding-bottom-20 margin-bottom-20 clearfix\">\n\n                            <!-- IMAGE -->\n                            {% if val.cover_id>0 %}\n                            <figure class=\"blog-item-small-image margin-bottom-0\">\n                                <img class=\"img-responsive\" src=\"{{val.cover_id|get_pic(\'m=1,w=200,h=120\')}}\" alt=\"\">\n                            </figure>\n                            {% endif %}\n                            <div class=\"blog-item-small-content\">\n                                <h2><a href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\" title=\"{{val.title}}\">{{val.title}}</a></h2>\n\n                                <ul class=\"blog-post-info list-inline padding-bottom-10\">\n                                    <li>\n                                        <a href=\"#\">\n                                            <i class=\"fa fa-clock-o\"></i>\n                                            <span class=\"font-lato\">{{val.create_time|moment(\'YYYY-MM-DD HH:mm\')}}</span>\n                                        </a>\n                                    </li>\n                                    <li>\n                                        <a href=\"#\">\n                                            <i class=\"fa fa-comment-o\"></i>\n                                            <span class=\"font-lato\">{{val.view}}</span>\n                                        </a>\n                                    </li>\n                                    <li>\n                                        <i class=\"fa fa-folder-open-o\"></i>\n\n                                        <a class=\"category\" href=\"{{categoryname.url}}\">\n                                            <span class=\"font-lato\">{{categoryname.name}}</span>\n                                        </a>\n                                    </li>\n                                    <li>\n                                        <a href=\"#\">\n                                            <i class=\"fa fa-user\"></i>\n                                            <span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n                                        </a>\n                                    </li>\n                                </ul>\n\n                                <p>{{val.description|truncate(90, true, \"...\")}}</p>\n                            </div>\n\n                        </div>\n                        <!-- /POST ITEM -->\n                        {%endfor%}\n                    </div>\n                    <div class=\"panel-footer\">\n                        <!-- PAGINATION -->\n                        <div class=\"text-right\">\n                            <!-- Pagination Default -->\n                            {{pagination | safe}}\n                            <!-- /Pagination Default -->\n                        </div>\n                        <!-- /PAGINATION -->\n                    </div>\n                </div>\n\n\n\n\n\n            </div>\n\n            <!-- RIGHT -->\n\n            {% include \"./inc_right.html\"%}\n        </div>\n\n    </div>\n</section>\n<!-- / -->\n{% endblock %}', 1, 1);
INSERT INTO `cmswing_temp` VALUES (8, 'home', 'list', 'shop', '商城列表模板', '{% extends \"../inc/base.html\" %}\n{% block style%}\n<link href=\"/static/assets/css/layout-shop.css\" rel=\"stylesheet\" type=\"text/css\" />\n{% endblock %}\n{% block content %}\n{%set cate = category.id|get_cate()%}\n<!-- \nPAGE HEADER \n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header dark page-header-xs parallax parallax-2\" style=\"background-image:url(\'/static/assets/images/demo/thematics/lawyer/slider_bg_4-min.jpg\')\">\n<div class=\"overlay dark-2\"><!-- dark overlay [1 to 9 opacity] --></div>\n<div class=\"container\">\n\n<h1>{{category.title}}</h1>\n\n<!-- breadcrumbs -->\n<ol class=\"breadcrumb f-14\">\n<li><a href=\"/\">首页</a></li>\n{%for val in breadcrumb %}\n{% if val.id == category.id %}\n<li class=\"active\">{{val.title}}</li>\n{% else %}\n<li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n\n{% endif %}\n{% endfor %}\n</ol><!-- /breadcrumbs -->\n<!-- page tabs -->\n{%if category.pid ==0%}\n    {%column data=\"column\",pid=category.id%}\n    {%else%}\n{%column data=\"column\",pid=category.pid%}\n    {%endif%}\n{%if column%}\n<ul class=\"page-header-tabs list-inline\">\n\n<!--<li class=\"active\"><a href=\"portfolio-masonry-3-columns.html\">With Gutter</a></li>-->\n{%for val in column%}\n<li  {%if val.id == category.id %}class=\"active\"{%endif%}><a href=\"{{val.url}}{{http.config(\'view.file_ext\')}}\">{{val.name}}</a></li>\n{%endfor%}\n</ul>\n{%endif%}\n<!-- /page tabs -->\n</div>\n</section>\n<!-- /PAGE HEADER -->\n\n\n\n\n<!-- -->\n<section>\n<div class=\"container\">\n    <!--分类信息-->\n    {%if sort%}\n    {%if sort.types|length >1%}\n    <ul class=\"nav nav-tabs\">\n        {%for val in sort.types%}\n        <li class=\"{%if sortid == val.enable%}active{%endif%}\"><a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{val.enable}}{{http.config(\'view.file_ext\')}}\" >{{val.name}}</a></li>\n        {%endfor%}\n\n    </ul>\n    {%endif%}\n    <div class=\"tab-content table-bordered\" style=\"padding: 0; margin-bottom: 20px; margin-top: -1px\">\n        <table class=\"table table-hover  m-b-none\" style=\"margin-top: -1px\">\n            <tbody>\n            {% set typenum = 1%}\n            {%for val in typevar%}\n            {%if val.search > 1%}\n            <tr {% if typenum > 3 %} class=\"type_tr_m\"  style=\"display:none\"{%endif%} >\n                <td style=\"width: 120px\" class=\"text-right\">\n                    {{val.option.title}}:\n                </td>\n                <td>\n                    {%if 0 == nsobj[val.option.identifier] or null == nsobj[val.option.identifier]%}\n                    <span class=\"btn btn-danger btn-xs relative\">全部</span>\n                    {%else%}\n                    <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{0|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\" >全部</a>\n                    {%endif%}\n                    {%for v in val.rules %}\n                    {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                    <span class=\"btn btn-danger btn-xs relative\" >{{v.name}}{{val.option.unit}}</span>\n                    {%else%}\n                    <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v.id|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\" >{{v.name}}{{val.option.unit}}</a>\n                    {%endif%}\n                    {%endfor%}\n                    <!--子栏目-->\n                    {%for v in val.rules %}\n                    {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                    {% if v.children%}\n                    <div class=\"subtsm\">\n\n                        {% for v_ in v.children %}\n                        {%if v_.id|sort_act(nsobj[val.option.identifier])%}\n                        <span class=\"btn btn-danger btn-xs relative\" >{{v_.name}}{{val.option.unit}}</span>\n                        {%else%}\n                        <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v_.id|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\">{{v_.name}}{{val.option.unit}}</a>\n                        {%endif%}\n                        {% endfor %}\n                    </div>\n                    {%endif%}\n                    {%endif%}\n                    {%endfor%}\n                    <!--子栏目-->\n                    {%for v in val.rules %}\n                    {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                    {% if v.children%}\n\n                    {% for v_ in v.children %}\n                    {%if v_.id|sort_act(nsobj[val.option.identifier])%}\n                    {% if v_.children%}\n                    <div class=\"subtsm\">\n                        {% for v__ in v_.children %}\n\n                        {%if v__.id|sort_act(nsobj[val.option.identifier])%}\n\n                        <span class=\"btn btn-danger btn-xs relative\" >{{v__.name}}{{val.option.unit}}</span>\n                        {%else%}\n                        <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v__.id|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\" >{{v__.name}}{{val.option.unit}}</a>\n\n                        {%endif%}\n                        {%endfor%}\n                    </div>\n                    {%endif%}\n                    {%endif%}\n                    {% endfor %}\n\n                    {%endif%}\n                    {%endif%}\n                    {%endfor%}\n                </td>\n\n            </tr>\n            {% set typenum = typenum+1%}\n            {%endif%}\n            {%endfor%}\n\n            </tbody>\n        </table>\n        {% if typenum > 4%}\n        <a href=\"javascript:;\" class=\"center-block btn btn-xs btn-white type_tr_b1\" style=\"border-top: 1px solid #cfd8e1; display: block\"><span class=\"fa fa-chevron-down\"></span></a>\n        <a href=\"javascript:;\" class=\"center-block btn btn-xs btn-white type_tr_b2\" style=\"border-top: 1px solid #cfd8e1; display: none\"><span class=\"fa fa-chevron-up\"></span></a>\n        {%endif%}\n    </div>\n\n    {%endif%}\n    <!--/分类信息-->\n<div class=\"row\">\n\n<!-- RIGHT -->\n<div class=\"col-lg-10 col-md-10 col-sm-10 col-lg-push-2 col-md-push-2 col-sm-push-2\">\n\n{#\n<!-- CAROUSEL -->\n<div class=\"owl-carousel buttons-autohide controlls-over margin-bottom-30 radius-4\" data-plugin-options=\'{\"singleItem\": true, \"autoPlay\": 6000, \"navigation\": true, \"pagination\": true, \"transitionStyle\":\"fade\"}\'>\n<!-- item -->\n<div>\n<div class=\"caption-slider-default\">\n<div class=\"display-table\">\n<div class=\"display-table-cell vertical-align-middle\">\n<div class=\"caption-container text-left\">\n<h2>全场 <strong>限时折扣</strong> &ndash; 50% OFF</h2>\n<p>\n这是一个商城分类页面的广告位<br />\n你可以放置您的推广营销内容.\n</p>\n</div>\n</div>\n</div>\n</div>\n\n<img class=\"img-responsive radius-4\" src=\"/static/assets/images/demo/shop/banners/top_2.png\" width=\"851\" height=\"335\" alt=\"\">\n</div>\n<!-- /item -->\n\n<!-- item -->\n<div>\n\n<div class=\"caption-slider-default\">\n<div class=\"display-table\">\n<div class=\"display-table-cell vertical-align-middle\">\n<div class=\"caption-container text-left\">\n<h2>演示广告位置 <strong>就是这里</strong></h2>\n<p>\n这是一个商城分类页面的广告位<br />\n你可以放置您的推广营销内容.\n</p>\n</div>\n</div>\n</div>\n</div>\n\n<img class=\"img-responsive radius-4\" src=\"/static/assets/images/demo/shop/banners/top_1.png\" width=\"851\" height=\"335\" alt=\"\">\n</div>\n<!-- /item -->\n\n</div>\n<!-- /CAROUSEL -->\n#}\n\n\n<!-- LIST OPTIONS -->\n<div class=\"clearfix shop-list-options margin-bottom-20\">\n<div class=\"nomargin pull-right\">\n{{pagination | safe}}\n</div>\n\n\n<div class=\"options-left\">\n<select>\n<option value=\"pos_asc\">默认排序 高</option>\n<option value=\"pos_desc\">默认排序 低</option>\n<option value=\"name_asc\">销量排序 高</option>\n<option value=\"name_desc\">销量排序 低</option>\n<option value=\"price_asc\">价格排序 高</option>\n<option value=\"price_desc\">价格排序 低</option>\n</select>\n\n<a class=\"btn active fa fa-th\" href=\"shop-4col-left.html\"><!-- grid --></a>\n<a class=\"btn fa fa-list\" href=\"shop-1col-left.html\"><!-- list --></a>\n</div>\n\n</div>\n<!-- /LIST OPTIONS -->\n\n\n<ul class=\"shop-item-list1 row list-inline nomargin\">\n{% for item in list%}\n<!-- ITEM -->\n<li class=\"col-xs-6 col-lg-3 col-md-3 col-sm-3 \">\n\n<div class=\"shop-item\">\n{% set pic = item.pics|strToArray%}\n<div class=\"thumbnail\">\n<!-- product image(s) -->\n<a class=\"shop-item-image\" href=\"{{item.name|get_url(item.id)}}\" title=\"{{item.title}}\" target=\"_blank\">\n<img class=\"img-responsive\" src=\"{{pic[0]|get_pic(\'m=1,w=260,h=260\')}}\" alt=\"shop first image\" />\n{% if pic|length >1%}\n<img class=\"img-responsive\" src=\"{{pic[1]|get_pic(\'m=1,w=260,h=260\')}}\" alt=\"shop hover image\" />\n{% endif %}\n</a>\n<!-- /product image(s) -->\n\n<!-- hover buttons -->\n<div class=\"shop-option-over\"><!-- replace data-item-id width the real item ID - used by js/view/demo.shop.js -->\n\n<a class=\"btn btn-default add-wishlist\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"Add To Wishlist\"><i class=\"fa fa-heart nopadding\"></i></a>\n<a class=\"btn btn-default add-compare\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"Add To Compare\"><i class=\"fa fa-bar-chart-o nopadding\" data-toggle=\"tooltip\"></i></a>\n</div>\n<!-- /hover buttons -->\n<!-- countdown -->\n<div class=\"shop-item-counter\">\n<div class=\"countdown\" data-from=\"January 31, 2018 15:03:26\" data-labels=\"年,月,周,天,时,分,秒\"><!-- Example Date From: December 31, 2018 15:03:26 --></div>\n</div>\n<!-- /countdown -->\n<!-- product more info -->\n<div class=\"shop-item-info\">\n<span class=\"label label-success\">新品</span>\n<span class=\"label label-danger\">热卖</span>\n</div>\n<!-- /product more info -->\n</div>\n\n<div class=\"shop-item-summary text-center\">\n<h2 class=\"text-intercept\"><a class=\"\"  href=\"{{item.name|get_url(item.id)}}\" title=\"{{item.title}}\" target=\"_blank\">{{item.title}}</a></h2>\n\n<!-- rating -->\n<div class=\"shop-item-rating-line\">\n<div class=\"rating rating-5 size-13\"><!-- rating-0 ... rating-5 --></div>\n</div>\n<!-- /rating -->\n\n<!-- price -->\n<div class=\"shop-item-price text-danger f-16\">\n{% if item.price|get_price_format(\'2\') %}<span class=\"line-through f-14\">¥{{item.price|get_price_format(\'2\')}}</span>{%endif%}\n¥{{item.price|get_price_format(\'1\')}}\n</div>\n<!-- /price -->\n</div>\n{% set stock = item.id|getmodelfield(item.model_id,\'total_stock\') %}\n{% if stock == 0%}\n<!-- buttons -->\n<div class=\"shop-item-buttons text-center\">\n<span class=\"out-of-stock\">已售罄</span><!-- add .clean to remove css characteres -->\n</div>\n<!-- /buttons -->\n{% else %}\n<!-- buttons -->\n<div class=\"shop-item-buttons text-center\">\n<a class=\"btn btn-default\" href=\"shop-cart.html\"><i class=\"fa fa-cart-plus\"></i> 添加到购物车 </a>\n</div>\n<!-- /buttons -->\n{% endif %}\n</div>\n\n</li>\n<!-- /ITEM -->\n{%endfor%}\n\n</ul>\n\n<hr />\n\n<!-- Pagination Default -->\n<div class=\"text-center\">\n{{pagination | safe}}\n</div>\n<!-- /Pagination Default -->\n\n</div>\n\n\n<!-- LEFT -->\n<div class=\"col-lg-2 col-md-2 col-sm-2 col-lg-pull-10 col-md-pull-10 col-sm-pull-10\">\n\n<!-- CATEGORIES -->\n<div class=\"side-nav margin-bottom-40\">\n\n<div class=\"s