INSERT INTO `cmswing_temp` VALUES (9, 'home', 'list', 'index', '默认列表模板（手机）', '{% extends \"../inc/base.html\" %}\n{% block content %}\n<header class=\"mui-bar mui-bar-nav\">\n    <!--<a class=\"mui-action-back mui-icon mui-icon-left-nav mui-pull-left\"></a>-->\n    <a href=\"/uc/cart/index\" class=\"mui-icon iconfont icon-cart\"><span class=\"mui-badge\">{% if controller.cart.num %}{{controller.cart.num}}{%else%}0{%endif%}</span></a>\n    <h1 class=\"mui-title\"><img src=\"/static/webapp/img/logo.png\" ></h1>\n    <a class=\"mui-icon iconfont icon-search mui-pull-right\"></a>\n</header>\n{% include \"../inc/nav.html\" %}\n{% include \"../inc/sliderbar.html\" %}\n<!--下拉刷新容器-->\n<div id=\"pullrefresh\" class=\"mui-content mui-scroll-wrapper list-content\">\n    <div class=\"mui-scroll\">\n        <!--数据列表-->\n        <ul class=\"mui-table-view \">\n            {%for v in list%}\n            <!--如果有缩略图-->\n            {%if v.cover_id >0%}\n            {% if v.model_id == 5%}\n            <li class=\"mui-table-view-cell mui-media\">\n                <a href=\"{{v.name|get_url(v.id)}}\">\n\n\n                    <h4 class=\"dotdot line2 margin-bottom-6\">{{v.title}}</h4>\n\n\n                    <div class=\"mui-row\">\n                        <div class=\"mui-col-sm-12 mui-col-xs-12\">\n                            <img class=\"list_img_holder\" src=\"{{v.cover_id|get_pic(\'m=0,w=360\')}}\">\n                        </div>\n                    </div>\n                    <p class=\"item_info\">\n\n                        <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                        <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                        <span class=\"time\" title=\"2016-08-30 11:06\">{{v.update_time|moment}}</span>\n\n                    </p>\n\n                </a>\n            </li>\n            {%else%}\n            <li class=\"mui-table-view-cell mui-media\">\n                <a href=\"{{v.name|get_url(v.id)}}\" >\n                    <div class=\"mui-row\">\n                        <div class=\"mui-col-sm-8 mui-col-xs-8\">\n\n                            <h4 class=\"dotdot line2 \">{{v.title}}</h4>\n                            <p class=\"item_info\">\n\n                                <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                                <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                                <span class=\"time\" title=\"2016-08-30 11:06\">{{v.update_time|moment}}</span>\n\n                            </p>\n                        </div>\n                        <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                            <img class=\"list_img_holder\" src=\"{{v.cover_id|get_pic(\'m=1,w=123,h=82\')}}\" >\n\n                        </div>\n                    </div>\n                </a>\n            </li>\n            {%endif%}\n            {%elif v.pics%}\n            {% set pics = v.pics|strToArray%}\n            {% if pics|length >2%}\n            <li class=\"mui-table-view-cell mui-media\">\n                <a href=\"{{v.name|get_url(v.id)}}\">\n                    <h4 class=\"dotdot line2 margin-bottom-6\">{{v.title}}</h4>\n                    <div class=\"mui-row\">\n                        <div class=\"mui-col-sm-4 mui-col-xs-4\">\n                            <img class=\"list_img_holder\" src=\"{{pics[0]|get_pic(\'m=1,w=123,h=82\')}}\">\n                        </div>\n                        <div class=\"mui-col-sm-4 mui-col-xs-4\">\n                            <img class=\"list_img_holder\" src=\"{{pics[1]|get_pic(\'m=1,w=123,h=82\')}}\">\n                        </div>\n                        <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                            <img class=\"list_img_holder\" src=\"{{pics[2]|get_pic(\'m=1,w=123,h=82\')}}\" >\n\n                        </div>\n                    </div>\n                    <p class=\"item_info\">\n\n                        <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                        <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                        <span class=\"time\" title=\"2016-08-30 11:06\">{{v.update_time|moment}}</span>\n\n                    </p>\n\n                </a>\n            </li>\n            {%else%}\n            <li class=\"mui-table-view-cell mui-media\">\n                <a href=\"{{v.name|get_url(v.id)}}\" >\n                    <div class=\"mui-row\">\n                        <div class=\"mui-col-sm-8 mui-col-xs-8\">\n\n                            <h4 class=\"dotdot line2 \">{{v.title}}</h4>\n                            <p class=\"item_info\">\n                                <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                                <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                                <span class=\"time\" title=\"2016-08-30 11:06\">{{v.update_time|moment}}</span>\n\n                            </p>\n                        </div>\n                        <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                            <img class=\"list_img_holder\" src=\"{{pics[0]|get_pic(\'m=1,w=123,h=82\')}}\" >\n\n                        </div>\n                    </div>\n                </a>\n            </li>\n            {%endif%}\n            {%else%}\n            <!--无图-->\n            <li class=\"mui-table-view-cell mui-media\">\n                <a href=\"{{v.name|get_url(v.id)}}\">\n                    <h4 class=\"dotdot line2 margin-bottom-6\">{{v.title}}</h4>\n\n                    <p class=\"item_info\">\n\n                        <span class=\"src space\">{{v.uid|get_nickname}}</span>\n                        <span class=\"cmt space\">评论&nbsp;0</span>\n\n                        <span class=\"time\" title=\"2016-08-30 11:06\">{{v.update_time|moment}}</span>\n\n                    </p>\n\n                </a>\n            </li>\n            {%endif%}\n            {%endfor%}\n        </ul>\n    </div>\n</div>\n<script type=\"text/template\" id=\"tpl\">\n    {% raw %}\n    <%_.forEach(data,function(v,k){ %>\n    <!--如果有缩略图-->\n    <%if (v.cover_id){%>\n\n    <% if (v.model_id == 5){%>\n    <li class=\"mui-table-view-cell mui-media\">\n        <a href=\"<%=v.url%>\">\n\n\n            <h4 class=\"dotdot line2 margin-bottom-6\"><%=v.title%></h4>\n\n\n            <div class=\"mui-row\">\n                <div class=\"mui-col-sm-12 mui-col-xs-12\">\n                    <img class=\"list_img_holder\" src=\"<%=v.cover_id%>\">\n                </div>\n            </div>\n            <p class=\"item_info\">\n\n                <span class=\"src space\"><%=v.uid%></span>\n                <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                <span class=\"time\" title=\"2016-08-30 11:06\"><%=v.update_time%></span>\n\n            </p>\n\n        </a>\n    </li>\n    <%}else{%>\n    <li class=\"mui-table-view-cell mui-media\">\n        <a href=\"<%=v.url%>\" >\n            <div class=\"mui-row\">\n                <div class=\"mui-col-sm-8 mui-col-xs-8\">\n\n                    <h4 class=\"dotdot line2 \"><%=v.title%></h4>\n                    <p class=\"item_info\">\n\n                        <span class=\"src space\"><%=v.uid%></span>\n                        <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                        <span class=\"time\" title=\"2016-08-30 11:06\"><%=v.update_time%></span>\n\n                    </p>\n                </div>\n                <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                    <img class=\"list_img_holder\" src=\"<%=v.cover_id%>\" >\n\n                </div>\n            </div>\n        </a>\n    </li>\n    <%}%>\n    <%}else if(v.pics){%>\n    <% if (v.pics.length >2){%>\n    <li class=\"mui-table-view-cell mui-media\">\n        <a href=\"<%=v.url%>\">\n            <h4 class=\"dotdot line2 margin-bottom-6\"><%=v.title%></h4>\n            <div class=\"mui-row\">\n                <div class=\"mui-col-sm-4 mui-col-xs-4\">\n                    <img class=\"list_img_holder\" src=\"<%=v.pics[0]%>\">\n                </div>\n                <div class=\"mui-col-sm-4 mui-col-xs-4\">\n                    <img class=\"list_img_holder\" src=\"<%=v.pics[1]%>\">\n                </div>\n                <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                    <img class=\"list_img_holder\" src=\"<%=v.pics[2]%>\" >\n\n                </div>\n            </div>\n            <p class=\"item_info\">\n\n                <span class=\"src space\"><%=v.uid%></span>\n                <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                <span class=\"time\" title=\"2016-08-30 11:06\"><%=v.update_time%></span>\n\n            </p>\n\n        </a>\n    </li>\n    <%}else{%>\n    <li class=\"mui-table-view-cell mui-media\">\n        <a href=\"<%=v.url%>\" >\n            <div class=\"mui-row\">\n                <div class=\"mui-col-sm-8 mui-col-xs-8\">\n\n                    <h4 class=\"dotdot line2 \"><%=v.title%></h4>\n                    <p class=\"item_info\">\n                        <span class=\"src space\"><%=v.uid%></span>\n                        <span class=\"cmt space\">评论&nbsp;2323</span>\n\n                        <span class=\"time\" title=\"2016-08-30 11:06\"><%=v.update_time%></span>\n\n                    </p>\n                </div>\n                <div class=\"mui-col-sm-4 mui-col-xs-4 \">\n                    <img class=\"list_img_holder\" src=\"<%=v.pics[0]%>\" >\n\n                </div>\n            </div>\n        </a>\n    </li>\n    <%}%>\n    <%}else{%>\n    <!--无图-->\n    <li class=\"mui-table-view-cell mui-media\">\n        <a href=\"<%=v.url%>\">\n            <h4 class=\"dotdot line2 margin-bottom-6\"><%=v.title%></h4>\n\n            <p class=\"item_info\">\n\n                <span class=\"src space\"><%=v.uid%></span>\n                <span class=\"cmt space\">评论&nbsp;0</span>\n\n                <span class=\"time\" title=\"2016-08-30 11:06\"><%=v.update_time%></span>\n\n            </p>\n\n        </a>\n    </li>\n    <%}%>\n    <%})%>\n    {% endraw %}\n</script>\n{% endblock%}\n\n{% block script %}\n<script>\n    var tplRender =  _.template(document.getElementById(\"tpl\").innerHTML);\n    mui.init({\n        pullRefresh: {\n            container: \'#pullrefresh\',\n            down: {\n                callback: pulldownRefresh\n            },\n            up: {\n                contentrefresh: \'正在加载...\',\n                callback: pullupRefresh\n            }\n        }\n    });\n\n    mui(\"#pullrefresh .mui-table-view\").on(\"tap\",\"li.mui-table-view-cell > a\",function(){\n        var url = this.getAttribute(\"href\");\n        //打开关于页面\n        //alert(url)\n        mui.openWindow({\n            url: url\n        });\n    })\n    var tops=parseInt(sessionStorage.getItem(\"{{http.url}}_top\"))||0;\n    var size=parseInt(sessionStorage.getItem(\"{{http.url}}_size\"))||0;\n    //记录位置\n    document.getElementById(\"pullrefresh\").onscroll = function () {\n\n        var patt1 = /\\(.*?\\)/;\n        var srtY = document.querySelector(\'#pullrefresh .mui-scroll\').style.transform || document.querySelector(\'#pullrefresh .mui-scroll\').style[\"-webkit-transform\"];\n        if (srtY) {\n            var Y = parseInt(srtY.match(patt1)[0].split(\",\")[1])\n        } else {\n            var Y = 0;\n        }\n        console.log(Y)\n        var S = document.querySelector(\'#pullrefresh .mui-table-view\').querySelectorAll(\'li.mui-table-view-cell\').length;\n        console.log(Y);\n        if (window.sessionStorage) {\n            sessionStorage.setItem(\"{{http.url}}_top\", Y);\n            sessionStorage.setItem(\"{{http.url}}_size\", S);\n        }\n    }\n    /**\n     * 下拉刷新具体业务实现\n     */\n    function pulldownRefresh() {\n        setTimeout(function() {\n            var table = document.body.querySelector(\'.mui-table-view\');\n            var cells = document.body.querySelectorAll(\'.mui-table-view-cell\');\n            for (var i = cells.length, len = i + 3; i < len; i++) {\n                var li = document.createElement(\'li\');\n                li.className = \'mui-table-view-cell\';\n                li.innerHTML = \'<a class=\"mui-navigate-right\">Item \' + (i + 1) + \'</a>\';\n                //下拉刷新，新纪录插到最前面；\n                table.insertBefore(li, table.firstChild);\n            }\n            mui(\'#pullrefresh\').pullRefresh().endPulldownToRefresh(); //refresh completed\n        }, 1500);\n    }\n\n    /**\n     * 上拉加载具体业务实现\n     */\n    function pullupRefresh() {\n        setTimeout(function() {\n\n            var table = document.body.querySelector(\'#pullrefresh .mui-table-view\');\n            var cells = document.body.querySelectorAll(\'#pullrefresh .mui-table-view-cell\');\n            var count = \"{{count}}\";\n            console.log((parseInt(count) > cells.length));\n\n            if(parseInt(cells.length) < parseInt(count)){\n                createFragment(10)\n                mui(\'#pullrefresh\').pullRefresh().endPullupToRefresh();\n            }else {\n                mui(\'#pullrefresh\').pullRefresh().endPullupToRefresh(true); //参数为true代表没有更多数据了。\n            }\n\n        }, 1000);\n    }\n    var createFragment = function(count, page) {\n        //无限加载\n        var table = document.body.querySelector(\'#pullrefresh .mui-table-view\');\n        var lastIndex = table.querySelectorAll(\'li.mui-table-view-cell\').length;\n        console.log(lastIndex);\n        var pages = page || Math.ceil((lastIndex+count)/count);\n        console.log(pages);\n        mui.ajax(\"{{http.url}}\",{\n            data:{\n                page:pages\n            },\n            dataType:\'json\',//服务器返回json格式数据\n            type:\'get\',//HTTP请求类型\n            success:function(data){\n//                //获得服务器响应\n//                console.log();\n//                var fragment = document.createDocumentFragment();\n//                var li;\n//                for (var i = 0; i < data.data.length; i++) {\n//                    li = document.createElement(\'li\');\n//                    li.className = \'mui-table-view-cell\';\n//                    li.innerHTML =\'<a href=\"javascript:;\">\'+\n//                            \'<img class=\"mui-media-object mui-pull-right\" src=\"/static/webapp/img/yuantiao.jpg\">\'+\n//                            \'<div class=\"mui-media-body\">\'+\n//                            data.data[i].title+\n//                            \'<p class=\"mui-ellipsis\">静静的看这个世界，最后终于疯了</p>\'+\n//                            \'</div>\'+\n//                            \'</a>\';\n//                    fragment.appendChild(li);\n//                }\n//                table.appendChild(fragment);\n                $(\'#pullrefresh .mui-table-view\').append(tplRender(data));\n            }\n        });\n\n    };\n    //alert(mui.os.plus)\n\n\n        mui.ready(function() {\n\n            //mui(\'#pullrefresh\').pullRefresh().pullupLoading();\n\n            var page = Math.ceil(size/10);\n            if(page>1 ){\n                for (var i = 1; i < (page+1); i++) {\n                    createFragment(10,(i+1))\n                }\n            }\n            mui(\'#pullrefresh\').pullRefresh().scrollTo(0,tops);\n            //alert(1)\n            document.querySelector(\'header h1\').addEventListener(\'tap\',function () {\n                //内容区滚动到顶部\n                mui(\'#pullrefresh\').pullRefresh().scrollTo(0,0,100);\n            });\n            var btn = document.querySelectorAll(\".mui-bar-tab a.mui-tab-item\");\n            for(var i = 0;i<btn.length;i++){\n                btn[i].addEventListener(\"tap\",function () {\n                    var href = this.getAttribute(\"href\");\n                    if(href==\"#wait\"){\n                        //todo\n                        mui.toast(\"功能开发中。。。\")\n                        return\n                    }\n                    mui.openWindow({url: href})\n\n                })\n            }\n        });\n</script>\n{% endblock %}', 2, 1);
INSERT INTO `cmswing_temp` VALUES (10, 'home', 'list', 'shop', '手机版商城列表模板', '{% extends \"../inc/base.html\" %}\n{% block page%}\n<div class=\"page page-current\" id=\"list_shop\">\n<header class=\"bar bar-nav\">\n<a class=\"button button-link button-nav pull-left\" href=\"/\" ><img src=\"/static/mobile/img/logo.png\" /></a>\n<a class=\"button button-link button-nav pull-right open-panel\" href=\"#\" data-panel=\'#panel-left-menu\'>\n<span class=\"icon icon-menu\"></span>\n导航\n</a>\n<h1 class=\"title\">{{controller.meta_title}}</h1>\n</header>\n\n<!--nav-->\n{% include \"../inc/nav.html\" %}\n<!--end_nav-->\n<div class=\"bar bar-header-secondary\">\n<div class=\"searchbar\">\n<a class=\"searchbar-cancel\">取消</a>\n<div class=\"search-input\">\n<label for=\"search\" class=\"icon icon-search\"></label>\n<input type=\"search\" placeholder=\"输入关键字...\" id=\"search\">\n</div>\n</div>\n</div>\n<!-- 添加 class infinite-scroll 和 data-distance  向下无限滚动可不加infinite-scroll-bottom类，这里加上是为了和下面的向上无限滚动区分-->\n<div class=\"content infinite-scroll native-scroll\" data-distance=\"100\" data-url=\"/column/{{http.get(\'category\')}}\">\n<div class=\"swiper-container\" data-space-between=\'10\' data-pagination=\".swiper-pagination\" data-loop=\"true\">\n<div class=\"swiper-wrapper\">\n<div class=\"swiper-slide\"><img src=\"/static/assets/images/demo/shop/banners/top_1.png\" alt=\"\" style=\'width: 100%\'></div>\n<div class=\"swiper-slide\"><img src=\"/static/assets/images/demo/shop/banners/top_2.png\" alt=\"\" style=\'width: 100%\'></div>\n</div>\n<div class=\"swiper-pagination\"></div>\n</div>\n<div class=\"row no-gutter list-container\">\n{% for item in list%}\n<a href=\"{{item.name|get_url(item.id)}}\" class=\"col-50 external\"><div  class=\"card demo-card-header-pic margin-5\">\n<div class=\"card-header color-white no-border no-padding\" valign=\"bottom\">\n{% set pic = item.pics|strToArray%}\n<img alt=\"\" src=\"{{pic[0]|get_pic(\'m=1,w=200\')}}\" class=\"card-cover\">\n</div>\n<div class=\"card-content tex-black\">\n<div class=\"card-content-inner padding-6\">\n<p class=\"color-danger margin-bottom-0\">{% if item.price|get_price_format(\'2\') %}<span class=\"color-gray line-through\">¥{{item.price|get_price_format(\'2\')}}</span>{%endif%}\n¥{{item.price|get_price_format(\'1\')}}</p>\n<p class=\"margin-bottom-0 margin-top-0\" style=\"overflow: hidden; height: 40px\">{{item.title}}</p>\n</div>\n</div>\n<div class=\"card-footer\">\n<span>有货</span>\n<span>5 评论</span>\n</div>\n</div></a>\n{% endfor %}\n</div>\n{#\n<div class=\"content-block-title\">列表式后面完善</div>\n<div class=\"list-block media-list\">\n<ul class=\"list-container\">\n{% for item in list%}\n<li>\n<a class=\"item-link item-content\" href=\"#\">\n{% set pic = item.pics|strToArray%}\n<div class=\"item-media\"><img width=\"80\" src=\"{{pic[0]|get_pic(\'m=1,w=80\')}}\"></div>\n<div class=\"item-inner\">\n<div class=\"item-title-row\">\n<div class=\"item-title color-danger\">{% if item.price|get_price_format(\'2\') %}<span class=\"\">¥{{item.price|get_price_format(\'2\')}}</span>{%endif%}\n¥{{item.price|get_price_format(\'1\')}}</div>\n<div class=\"item-after\"></div>\n</div>\n\n<div class=\"item-text\">{{item.title}}</div>\n</div>\n</a>\n</li>\n{% endfor %}\n\n</ul>\n</div>\n#}\n<!-- 加载提示符 -->\n<div class=\"infinite-scroll-preloader\">\n<div class=\"preloader\"></div>\n</div>\n</div>\n</div>\n{% endblock %}\n{% block pageout%}\n<div class=\"panel-overlay\"></div>\n<!-- Left Panel with Reveal effect -->\n<div class=\"panel panel-left panel-reveal theme-dark\" id=\'panel-left-menu\'>\n<div class=\"content-block\">\n<p>这是一个侧栏</p>\n<p>你可以在这里放用户设置页面</p>\n<p><a href=\"#\" class=\"close-panel\">关闭</a></p>\n</div>\n<div class=\"list-block\">\n<!-- .... -->\n</div>\n</div>\n\n{% endblock%}\n{% block script %}\n{% endblock %}', 2, 1);
INSERT INTO `cmswing_temp` VALUES (11, 'home', 'detail', 'article', '文档内容页模版(PC)', '{% extends \"../inc/base.html\" %}\n{% block style %}\n<script src=\"/static/admin/js/udeitor/ueditor.parse.min.js\"></script>\n<style>\n   .active > .text{\n        display: block !important;\n    }\n</style>\n{% endblock %}\n{% block content %}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header page-header-xs\">\n<div class=\"container\">\n\n<h1>{{category.title}}</h1>\n\n<!-- breadcrumbs -->\n<ol class=\"breadcrumb f-14\">\n<li><a href=\"/\">首页</a></li>\n{%for val in breadcrumb %}\n<li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n{% endfor %}\n</ol><!-- /breadcrumbs -->\n\n<!-- page tabs -->\n{%column data=\"cate\",pid=category.pid%}\n{%if cate%}\n<ul class=\"page-header-tabs list-inline\">\n\n<!--<li class=\"active\"><a href=\"portfolio-masonry-3-columns.html\">With Gutter</a></li>-->\n{%for val in cate%}\n<li  {%if val.id == category.id %}class=\"active\"{%endif%}><a href=\"{{val.url}}\">{{val.name}}</a></li>\n{%endfor%}\n</ul>\n{%endif%}<!-- /page tabs -->\n</div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n<div class=\"container\">\n\n<div class=\"row\">\n\n<!-- LEFT -->\n\n    <div class=\"col-md-9 col-sm-9\">\n\n        <h1 class=\"blog-post-title\">{{info.title}}</h1>\n        <ul class=\"blog-post-info list-inline\">\n            <li>\n                <a href=\"#\">\n                    <i class=\"fa fa-clock-o\"></i>\n                    <span class=\"font-lato\">{{ info.update_time|dateformat(\'Y-m-d H:i\') }}</span>\n                </a>\n            </li>\n            <li>\n                <a href=\"#\">\n                    <i class=\"fa fa-comment-o\"></i>\n                    <span class=\"font-lato\">28 Comments</span>\n                </a>\n            </li>\n            <li>\n                <i class=\"fa fa-folder-open-o\"></i>\n\n                <a class=\"category\" href=\"#\">\n                    <span class=\"font-lato\">{{info.category_id}}</span>\n                </a>\n                <a class=\"category\" href=\"#\">\n                    <span class=\"font-lato\">Photography</span>\n                </a>\n            </li>\n            <li>\n                <a href=\"#\">\n                    <i class=\"fa fa-user\"></i>\n                    <span class=\"font-lato\">{{info.uid|get_nickname}}</span>\n                </a>\n            </li>\n        </ul>\n\n        <!-- OWL SLIDER -->\n        <!--\n        <div class=\"owl-carousel buttons-autohide controlls-over\" data-plugin-options=\'{\"items\": 1, \"autoPlay\": 4500, \"autoHeight\": false, \"navigation\": true, \"pagination\": true, \"transitionStyle\":\"fadeUp\", \"progressBar\":\"false\"}\'>\n        <a class=\"lightbox\" href=\"assets/images/demo/1200x800/10-min.jpg\" data-plugin-options=\'{\"type\":\"image\"}\'>\n        <img class=\"img-responsive\" src=\"assets/images/demo/content_slider/10-min.jpg\" alt=\"\" />\n        </a>\n        <a class=\"lightbox\" href=\"assets/images/demo/1200x800/3-min.jpg\" data-plugin-options=\'{\"type\":\"image\"}\'>\n        <img class=\"img-responsive\" src=\"assets/images/demo/content_slider/3-min.jpg\" alt=\"\" />\n        </a>\n        <a class=\"lightbox\" href=\"assets/images/demo/1200x800/21-min.jpg\" data-plugin-options=\'{\"type\":\"image\"}\'>\n        <img class=\"img-responsive\" src=\"assets/images/demo/content_slider/21-min.jpg\" alt=\"\" />\n        </a>\n        </div>\n        -->\n        <!-- /OWL SLIDER -->\n\n        <!-- IMAGE -->\n        {#\n        {% if info.cover_id>0 %}\n        <figure class=\"margin-bottom-20\">\n            <img class=\"img-responsive\" src=\"{{info.cover_id|get_pic(\'m=2,w=847\')}}\" alt=\"{{info.title}}\">\n        </figure>\n        {% endif %}\n        #}\n        <!-- /IMAGE -->\n\n        <!-- VIDEO -->\n        <!--\n        <div class=\"margin-bottom-20 embed-responsive embed-responsive-16by9\">\n        <iframe class=\"embed-responsive-item\" src=\"http://player.youku.com/embed/XMTQ0ODQ2Nzg4OA==\" width=\"800\" height=\"450\"></iframe>\n        </div>\n        -->\n        <!-- /VIDEO -->\n\n\n        <!-- article content -->\n        <div id=\"contentcmswing\">\n\n            <div class=\"tab-content\">\n                {%set p = 1%}\n                {%for val in info.content%}\n                <div class=\"tab-pane fade {% if p ==1 %} in active{%endif%}\" id=\"page{{p}}\">\n                    {{val|safe}}\n                </div>\n                {% set p = p+1%}\n               {%endfor%}\n            </div>\n   {%if info.content|length >1%}\n            <!-- Pagination Default -->\n            <div class=\"text-center\">\n            <ul class=\"pagination\">\n                {%set o = 1%}\n                {%for val in info.content%}\n                <li {%if o == 1 %}class=\"active\"{%endif%}><a href=\"#page{{o}}\" data-toggle=\"tab\">{{o}}</a></li>\n                {% set o = o+1%}\n                {%endfor%}\n            </ul>\n            </div>\n            {%endif%}\n        </div>\n        <!-- /article content -->\n        <div class=\"divider\"></div>\n        <!--打赏-->\n        <div id=\"cyReward\" role=\"cylabs\" data-use=\"reward\" sourceid=\"{{info.id}}\" cid=\"{{category.id}}\" class=\"text-center\"></div>\n        <!--打赏-->\n        <!--评论表情-->\n        <div id=\"cyEmoji\" role=\"cylabs\" data-use=\"emoji\" sourceid=\"{{info.id}}\" cid=\"{{category.id}}\"></div>\n        <!--评论表情-->\n        <div class=\"divider divider-dotted\"><!-- divider --></div>\n        <!-- TAGS -->\n        {% rkeywords data =\"tags\",type=\"0\",mod_id=info.model_id,id=info.id%}\n        {%set color = [\"btn-primary\",\"btn-success\",\"btn-info\",\"btn-warning\",\"btn-danger\"]%}\n        {%for k in tags%}\n        <a href=\"{{k.url}}\" class=\"btn {{color|random}} btn-xs relative\">\n            {{k.keyname}}\n        </a>\n        {%endfor%}\n        <!-- /TAGS -->\n\n\n\n        <!-- SHARE POST -->\n        <div class=\"clearfix margin-top-20\">\n\n<span class=\"pull-left margin-top-6 bold hidden-xs margin-right-10\">\n分享到:\n</span>\n            <div class=\"bdsharebuttonbox\"><a href=\"#\" class=\"bds_more\" data-cmd=\"more\"></a><a href=\"#\" class=\"bds_qzone\" data-cmd=\"qzone\" title=\"分享到QQ空间\"></a><a href=\"#\" class=\"bds_tsina\" data-cmd=\"tsina\" title=\"分享到新浪微博\"></a><a href=\"#\" class=\"bds_tqq\" data-cmd=\"tqq\" title=\"分享到腾讯微博\"></a><a href=\"#\" class=\"bds_renren\" data-cmd=\"renren\" title=\"分享到人人网\"></a><a href=\"#\" class=\"bds_weixin\" data-cmd=\"weixin\" title=\"分享到微信\"></a></div>\n            <script>window._bd_share_config={\"common\":{\"bdSnsKey\":{},\"bdText\":\"\",\"bdMini\":\"2\",\"bdMiniList\":false,\"bdPic\":\"\",\"bdStyle\":\"0\",\"bdSize\":\"24\"},\"share\":{},\"image\":{\"viewList\":[\"qzone\",\"tsina\",\"tqq\",\"renren\",\"weixin\"],\"viewText\":\"分享到：\",\"viewSize\":\"16\"},\"selectShare\":{\"bdContainerClass\":null,\"bdSelectMiniList\":[\"qzone\",\"tsina\",\"tqq\",\"renren\",\"weixin\"]}};with(document)0[(getElementsByTagName(\'head\')[0]||body).appendChild(createElement(\'script\')).src=\'http://bdimg.share.baidu.com/static/api/js/share.js?v=89860593.js?cdnversion=\'+~(-new Date()/36e5)];</script>\n            {#\n            <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-facebook pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n                <i class=\"icon-facebook\"></i>\n                <i class=\"icon-facebook\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-twitter pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n                <i class=\"icon-twitter\"></i>\n                <i class=\"icon-twitter\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-gplus pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n                <i class=\"icon-gplus\"></i>\n                <i class=\"icon-gplus\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-linkedin pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n                <i class=\"icon-linkedin\"></i>\n                <i class=\"icon-linkedin\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-pinterest pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Pinterest\">\n                <i class=\"icon-pinterest\"></i>\n                <i class=\"icon-pinterest\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-call pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Email\">\n                <i class=\"icon-email3\"></i>\n                <i class=\"icon-email3\"></i>\n            </a>\n#}\n        </div>\n        <!-- /SHARE POST -->\n\n\n        <div class=\"divider\"><!-- divider --></div>\n\n\n        <ul class=\"pager\">\n            {%if previous.id%}\n            <li class=\"previous\"><a class=\"noborder\" href=\"{{previous.name|get_url(previous.id)}}\" title=\"{{previous.title}}\">&larr; 上一篇:{{previous.title|block(20)}}</a></li>\n            {%endif%}\n            {%if next.id%}\n            <li class=\"next\"><a class=\"noborder\" href=\"{{next.name|get_url(next.id)}}\" title=\"{{next.title}}\">下一篇:{{next.title|block(20)}} &rarr;</a></li>\n            {%endif%}\n        </ul>\n\n\n        <div class=\"divider\"><!-- divider --></div>\n\n\n        <!-- COMMENTS -->\n        <div id=\"comments\" class=\"comments margin-top-20\">\n            <!--打分版，用户可以对当前新闻、游戏或者应用等进行1-5级的评分-->\n            <div id=\"SOHUCS\" sid=\"{{info.id}}\"></div>\n            <script charset=\"utf-8\" type=\"text/javascript\" src=\"//changyan.sohu.com/upload/changyan.js\" ></script>\n            <script type=\"text/javascript\">\n                window._config = { showScore: true };\n                window.changyan.api.config({\n                    appid: \'{{controller.setup.CY_APPID}}\',\n                    conf: \'{{controller.setup.CY_APPKEY}}\'\n                });\n            </script>\n        </div>\n        <!-- /COMMENTS -->\n\n\n    </div>\n<!-- RIGHT -->\n    <div class=\"col-md-3 col-sm-3\">\n\n        <!-- INLINE SEARCH -->\n        <div class=\"inline-search clearfix margin-bottom-30\">\n            <form action=\"\" method=\"get\" class=\"widget_search\">\n                <input type=\"search\" placeholder=\"Start Searching...\" id=\"s\" name=\"s\" class=\"serch-input\">\n                <button type=\"submit\">\n                    <i class=\"fa fa-search\"></i>\n                </button>\n            </form>\n        </div>\n        <!-- /INLINE SEARCH -->\n\n        <hr />\n\n        <!-- side navigation -->\n        {% column data=\"cate\",cid=category.id %}\n        {% if cate|length >0 %}\n        <div class=\"side-nav margin-bottom-20 margin-top-20\">\n            <div class=\"side-nav-head\">\n                <button class=\"fa fa-bars\"></button>\n                <h4>CATEGORIES</h4>\n            </div>\n            <ul class=\"list-group list-group-bordered list-group-noicon uppercase\">\n                {%for val in cate%}\n                <li class=\"list-group-item\"><a href=\"{{val.url}}\"><span class=\"size-11 text-muted pull-right\">(12)</span> {{val.name}}</a></li>\n                {%endfor%}\n            </ul>\n            <!-- /side navigation -->\n        </div>\n        {%endif%}\n\n        <!-- JUSTIFIED TAB -->\n        <div class=\"tabs nomargin-top hidden-xs margin-bottom-10\">\n\n            <!-- tabs -->\n            <ul class=\"nav nav-tabs nav-bottom-border nav-justified\">\n                <li class=\"active\">\n                    <a href=\"#tab_1\" data-toggle=\"tab\">\n                        最热\n                    </a>\n                </li>\n                <li>\n                    <a href=\"#tab_2\" data-toggle=\"tab\">\n                        最新\n                    </a>\n                </li>\n            </ul>\n\n            <!-- tabs content -->\n            <div class=\"tab-content margin-bottom-0 margin-top-10\">\n\n                <!-- POPULAR -->\n                <div id=\"tab_1\" class=\"tab-pane active\">\n                    <!-- /post -->\n                    {% topic data = \"hotlist\",limit= \"5\",cid=category.id,type=\"hot\"%}\n                    {%for v in hotlist%}\n                    <div class=\"row tab-post\"><!-- post -->\n                        {%if v.cover_id%}\n                        <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                            <a href=\"{{v.name|get_url(v.id)}}\">\n                                <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                            </a>\n                        </div>\n                        {%endif%}\n                        <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                            <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                            <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                        </div>\n                    </div><!-- /post -->\n                    {%endfor%}\n\n                </div>\n                <!-- /POPULAR -->\n\n\n                <!-- RECENT -->\n                <div id=\"tab_2\" class=\"tab-pane\">\n                    {% topic data = \"newlist\",limit= \"5\",cid=category.id%}\n                    {%for v in newlist%}\n                    <div class=\"row tab-post\"><!-- post -->\n                        {%if v.cover_id%}\n                        <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                            <a href=\"{{v.name|get_url(v.id)}}\">\n                                <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                            </a>\n                        </div>\n                        {%endif%}\n                        <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                            <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                            <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                        </div>\n                    </div><!-- /post -->\n                    {%endfor%}\n                    <!-- /post -->\n                </div>\n                <!-- /RECENT -->\n\n            </div>\n        </div>\n        <!-- JUSTIFIED TAB -->\n\n\n        <!-- TAGS -->\n        <h3 class=\"hidden-xs size-16 margin-bottom-10\">标签</h3>\n        <div class=\"hidden-xs margin-bottom-20\">\n            {% keywords data =\"kws\",type=\"hot\"%}\n           {%for key in kws%}\n            <a class=\"tag\" href=\"{{key.url}}\">\n                <span class=\"txt\">{{key.keyname}}</span>\n                <span class=\"num\">{{key.videonum}}</span>\n            </a>\n           {%endfor%}\n        </div>\n\n\n\n        <hr />\n\n\n        <!-- SOCIAL ICONS -->\n        <div class=\"hidden-xs margin-top-20 margin-bottom-20\">\n            <a href=\"#\" class=\"social-icon social-icon-border social-facebook pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n                <i class=\"icon-facebook\"></i>\n                <i class=\"icon-facebook\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-border social-twitter pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n                <i class=\"icon-twitter\"></i>\n                <i class=\"icon-twitter\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-border social-gplus pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n                <i class=\"icon-gplus\"></i>\n                <i class=\"icon-gplus\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-border social-linkedin pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n                <i class=\"icon-linkedin\"></i>\n                <i class=\"icon-linkedin\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-border social-rss pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Rss\">\n                <i class=\"icon-rss\"></i>\n                <i class=\"icon-rss\"></i>\n            </a>\n        </div>\n\n    </div>\n\n</div>\n\n\n</div>\n</section>\n<!-- / -->\n{% endblock %}\n\n{% block script %}\n<script type=\"text/javascript\" charset=\"utf-8\" src=\"//changyan.itc.cn/js/??lib/jquery.js,changyan.labs.js?appid={{controller.setup.CY_APPID}}\"></script>\n<script>\nuParse(\'#contentcmswing\', {\nrootPath: \'/static/admin/js/udeitor/\'\n})\n</script>\n{% endblock %}\n', 1, 1);
INSERT INTO `cmswing_temp` VALUES (12, 'home', 'detail', 'shop', '商城内容页模板(PC)', '{% extends \"./inc_base.html\" %}\n{% block style %}\n<script src=\"/static/admin/js/udeitor/ueditor.parse.min.js\"></script>\n<link href=\"/static/assets/css/layout-shop.css\" rel=\"stylesheet\" type=\"text/css\" />\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/share/css/share.min.css\">\n{% endblock %}\n{% block content %}\n{%if category.pid !=0%}\n{%column data=\"column\",pid=category.pid%}\n{%else%}\n{%column data=\"column\",cid=category.id%}\n{%endif%}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header page-header-xs\">\n    <div class=\"container\">\n        {%if column|length>0%}\n        <h1>{{category.title}}</h1>\n        {%endif%}\n        <!-- breadcrumbs -->\n        <ol class=\"breadcrumb {%if column|length==0%} breadcrumb-inverse {%endif%}\">\n            <li><a href=\"/\">首页</a></li>\n            {%for val in breadcrumb %}\n            <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n            {%if column|length==0%} <li>{{info.title}} </li>{%endif%}\n            {% endfor %}\n        </ol> <!-- /breadcrumbs -->\n\n        <!-- page tabs -->\n        {%if column|length >0%}\n        <ul class=\"page-header-tabs list-inline\">\n            <li {%if breadcrumb[0].id == category.id %}class=\"active\"{%endif%}><a href=\"{{breadcrumb[0].url}}\">全部</a></li>\n            {%for val in column%}\n            <li  {%if val.id == category.id %}class=\"active\"{%endif%}><a href=\"{{val.url}}\">{{val.name}}</a></li>\n            {%endfor%}\n        </ul>\n        {%endif%}<!-- /page tabs -->\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n\n        <div class=\"row\">\n\n            <!-- RIGHT -->\n            <div class=\"col-lg-10 col-md-10 col-sm-10\">\n                <div class=\"panel panel-default tabs\">\n                    <div class=\"panel-heading panel-heading-transparent\">\n                        <h2 class=\"panel-title\">{{info.title}}</h2>\n                    </div>\n\n                    <div class=\"panel-body\">\n                        <div class=\"row\">\n\n                            <!-- IMAGE -->\n                            <div class=\"col-lg-6 col-sm-6\">\n                                {% set pic = info.pics|strToArray%}\n                                <div class=\"thumbnail relative margin-bottom-3 \">\n\n\n                                    <!--\n                                    IMAGE ZOOM\n\n                                    data-mode=\"mouseover|grab|click|toggle\"\n                                    -->\n                                    <figure id=\"zoom-primary\" class=\"zoom\" data-mode=\"mouseover\">\n                                        <!--\n                                        zoom buttton\n\n                                        positions available:\n                                        .bottom-right\n                                        .bottom-left\n                                        .top-right\n                                        .top-left\n                                        -->\n                                        <a class=\"lightbox bottom-right\" href=\"{{pic[0]|get_pic(\'m=0\')}}\" data-plugin-options=\'{\"type\":\"image\"}\'><i class=\"glyphicon glyphicon-search\"></i></a>\n\n                                        <!--\n                                        image\n\n                                        Extra: add .image-bw class to force black and white!\n                                        -->\n                                        <img class=\"img-responsive\" src=\"{{pic[0]|get_pic(\'m=1,w=800,h=800\')}}\"  alt=\"This is the product title\" />\n                                    </figure>\n\n                                </div>\n\n                                <!-- Thumbnails (required height:100px) -->\n                                {% if pic|length >1%}\n                                <div data-for=\"zoom-primary\" class=\"zoom-more owl-carousel owl-padding-3 featured nomargin-bottom\"\n                                     data-plugin-options=\'{\"singleItem\": false, \"autoPlay\": false, \"navigation\": true, \"pagination\": false,\"itemsMobile\":[479,5]}\'>\n                                    {%set i = 0%}\n                                    {% for val in pic%}\n                                    <a class=\"thumbnail nomargin-bottom {%if i == 0%}active{%endif%}\" href=\"{{val|get_pic(\'m=1,w=800,h=800\')}}\">\n                                        <img src=\"{{val|get_pic(\'m=2,w=100,h=100\')}}\" height=\"100\" alt=\"\" />\n                                    </a>\n                                    {%set i=i+1%}\n                                    {% endfor %}\n\n                                </div>\n                                {% endif %}\n                                <!-- /Thumbnails -->\n\n                            </div>\n                            <!-- /IMAGE -->\n\n                            <!-- ITEM DESC -->\n                            <div class=\"col-lg-6 col-sm-6\">\n\n                                <!-- buttons -->\n                                <div class=\"pull-right\">\n                                    <!-- replace data-item-id width the real item ID - used by js/view/demo.shop.js -->\n                                    <a class=\"btn btn-default add-wishlist\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"Add To Wishlist\"><i class=\"fa fa-heart nopadding\"></i></a>\n                                    <a class=\"btn btn-default add-compare\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"Add To Compare\"><i class=\"fa fa-bar-chart-o nopadding\" data-toggle=\"tooltip\"></i></a>\n                                </div>\n                                <!-- /buttons -->\n\n                                <!-- price -->\n                                <div class=\"shop-item-price text-danger\">\n                                    {% if info.price|get_price_format(\'2\') %}<span class=\"line-through nopadding-left\">¥{{info.price|get_price_format(\'2\')}}</span>{%endif%}\n                                    ¥<price>{{info.price|get_price_format(\'1\')}}</price>\n                                </div>\n                                <!-- /price -->\n\n                                <div class=\"divider margin-top-10 margin-bottom-10\"><!-- divider --></div>\n\n                                <div class=\"clearfix margin-bottom-30\" >\n                                    <div class=\"pull-right\" id=\"stock\">\n                                        {% if info.total_stock != 0%}\n\n                                        <span class=\" text-success\" ><i class=\"fa fa-check\"></i> 有货 <span class=\"badge badge-aqua btn-xs \">{{info.total_stock}}</span></span>\n                                        {% else%}\n                                        <span class=\"pull-right text-danger\"><i class=\"glyphicon glyphicon-remove\"></i> 无货</span>\n                                        {% endif %}\n                                    </div>\n                                    <strong>商家编码:</strong> {{info.goods_no}}\n                                </div>\n                                {#\n                                <div class=\"divider margin-top-10 margin-bottom-10\"><!-- divider --></div>\n                                <!-- countdown -->\n                                <div class=\"text-center\">\n                                    <h5>折扣时间</h5>\n                                    <div class=\"countdown\" data-from=\"January 31, 2018 15:03:26\" data-labels=\"年,月,周,天,时,分,秒\"><!-- Example Date From: December 31, 2018 15:03:26 --></div>\n                                </div>\n                                <!-- /countdown -->\n                                #}\n                                <div class=\"divider margin-top-10 margin-bottom-10\"><!-- divider --></div>\n\n                                <!-- FORM -->\n                                <form class=\"clearfix form-inline nomargin ichecks\"   data-icheck-info =\"{{info.suk}}\">\n                                    <!--{{info.suk}}-->\n                                    {% if info.suk %}\n                                    {% set suk = info.suk|strToJson%}\n                                    {% set i = 0 %}\n                                    {% for item in suk.type%}\n                                    <div class=\"icheck m-b clearfix shop-sku\" >\n                                        <div class=\"item dt pull-left text-justify text-right\">\n                                            {{item}}:\n                                        </div>\n                                        <div class=\"itemnbox\">\n                                            {% if i == 0 %}\n                                            {% for _item in suk.data %}\n                                            <!--{{suk.data[0].name}}-->\n\n                                            <div class=\"item\">\n                                                <input type=\"radio\"  name=\"size1\" value=\"{{_item.name}}\" >\n                                                <label >{%if suk.is_pic == 1%}<img class=\"m-r-xs\" height=\"30\" alt=\"\" data-src=\"{{_item.pic|get_pic(\'m=1,w=800,h=800\')}}\" src=\"{{_item.pic|get_pic(\'m=2,w=30,h=30\')}}\">{%endif%}{{_item.name}}</label>\n                                            </div>\n\n                                            {% endfor %}\n\n                                            {% elif i==1 %}\n                                            {% for _item in suk.data[0].ch %}\n                                            <!--{{suk.data[0].name}}-->\n                                            <div class=\"item\">\n                                                <input type=\"radio\"  name=\"size2\" value=\"{{_item.name}}\" >\n                                                <label >{{_item.name}}</label>\n                                            </div>\n                                            {% endfor %}\n                                            {% else %}\n                                            {% for _item in suk.data[0].ch[0].ch %}\n                                            <!--{{suk.data[0].name}}-->\n                                            <div class=\"item\">\n                                                <input type=\"radio\"  name=\"size3\" value=\"{{_item.name}}\" >\n                                                <label >{{_item.name}}</label>\n                                            </div>\n                                            {% endfor %}\n                                            {% endif %}\n                                        </div>\n                                    </div>\n                                    {% set i = i+1%}\n                                    {% endfor %}\n                                    <div class=\"divider margin-top-10 margin-bottom-10\"><!-- divider --></div>\n                                    {% endif %}\n                                    <input type=\"hidden\" name=\"product_id\" value=\"{{info.id}}\" />\n                                    <input type=\"hidden\" id=\"type\" name=\"type\" value=\"\" />\n                                    <!-- see assets/js/view/demo.shop.js -->\n\n                                    <input type=\"hidden\" id=\"qty\" name=\"qty\" value=\"1\" />\n\n                                    <!-- see assets/js/view/demo.shop.js -->\n                                    <div  id=\"out-of-stock\" class=\"{% if info.total_stock != 0%}hide{% endif %}\">\n\n                                        <!-- buttons -->\n                                        <div class=\"shop-item-buttons text-center \">\n                                            <span class=\"out-of-stock\">已售罄</span><!-- add .clean to remove css characteres -->\n                                        </div>\n                                        <!-- /buttons -->\n                                    </div>\n                                    <div id=\"in-of-stock\" class=\"{% if info.total_stock == 0%}hide{% endif %}\">\n                                        <div class=\"btn-group pull-left product-opt-qty {% if info.total_stock == 0%}hide{% endif %}\">\n                                            <button type=\"button\" class=\"btn btn-default dropdown-toggle product-qty-dd noradius\" data-toggle=\"dropdown\">\n                                                <span class=\"caret\"></span>\n                                                数量 <small id=\"product-selected-qty\">(<span>1</span>)</small>\n                                            </button>\n\n                                            <ul id=\"product-qty-dd\" class=\"dropdown-menu clearfix\" role=\"menu\">\n                                                <li class=\"active\"><a data-val=\"1\" href=\"#\">1</a></li>\n                                                <li><a data-val=\"2\" href=\"#\">2</a></li>\n                                                <li><a data-val=\"3\" href=\"#\">3</a></li>\n                                                <li><a data-val=\"4\" href=\"#\">4</a></li>\n                                                <li><a data-val=\"5\" href=\"#\">5</a></li>\n                                                <li><a data-val=\"6\" href=\"#\">6</a></li>\n                                                <li><a data-val=\"7\" href=\"#\">7</a></li>\n                                                <li><a data-val=\"8\" href=\"#\">8</a></li>\n                                                <li><a data-val=\"9\" href=\"#\">9</a></li>\n                                                <li><a data-val=\"10\" href=\"#\">10</a></li>\n                                            </ul>\n                                        </div><!-- /btn-group -->\n\n                                        <button class=\"btn btn-danger pull-left product-add-cart noradius \"> <i class=\"fa fa-cart-plus \"></i> 添加到购物车</button>\n                                    </div>\n\n                                </form>\n                                <!-- /FORM -->\n\n\n                                <div class=\"divider margin-top-10 margin-bottom-10\"><!-- divider --></div>\n                                {% if info.description %}\n                                <!-- short description -->\n                                <small class=\"text-muted\">{{info.description}}</small>\n                                <div class=\"divider margin-top-10 margin-bottom-10\"><!-- divider --></div>\n                                <!-- /short description -->\n                                {% endif %}\n\n\n\n                                <!-- Share -->\n                                <div class=\"pull-right\">\n                                    <div class=\"social-share\" data-disabled=\"google,twitter,facebook,diandian\" ></div>\n                                </div>\n                                <!-- /Share -->\n\n\n                                <!-- rating -->\n                                <div class=\"rating rating-5 size-13 margin-top-10 width-100\"><!-- rating-0 ... rating-5 --></div>\n                                <!-- /rating -->\n\n                            </div>\n                            <!-- /ITEM DESC -->\n\n                        </div>\n                    </div>\n                    {% rkeywords data =\"tags\",type=\"0\",mod_id=info.model_id,id=info.id%}\n                    {%if tags%}\n                    <div class=\"panel-footer panel-footer-transparent\">\n                        <!-- TAGS -->\n\n                        {%set color = [\"btn-primary\",\"btn-success\",\"btn-info\",\"btn-warning\",\"btn-danger\"]%}\n                        {%for k in tags%}\n                        <a href=\"{{k.url}}\" class=\"btn {{color|random}} btn-xs relative\">\n                            {{k.keyname}}\n                        </a>\n                        {%endfor%}\n                        <!-- /TAGS -->\n                    </div>\n                    {%endif%}\n                    <div class=\"panel-footer\">\n                        <ul class=\"pager nomargin\">\n                            {%if previous.id%}\n                            <li class=\"previous\"><a class=\"noborder\" href=\"{{previous.name|get_url(previous.id)}}\" title=\"{{previous.title}}\">&larr; 上一个:{{previous.title|truncate(30)}}</a></li>\n                            {%endif%}\n                            {%if next.id%}\n                            <li class=\"next\"><a class=\"noborder\" href=\"{{next.name|get_url(next.id)}}\" title=\"{{next.title}}\">下一个:{{next.title|truncate(30)}} &rarr;</a></li>\n                            {%endif%}\n                        </ul>\n                    </div>\n                    <div class=\"panel-footer nopadding\">\n                        <ul id=\"myTab\" class=\"nav nav-tabs \" role=\"tablist\" style=\"margin: -1px\">\n                            <li role=\"presentation\" class=\"active\"><a href=\"#description\" role=\"tab\" data-toggle=\"tab\">商品介绍</a></li>\n                            {#\n                            <li role=\"presentation\"><a href=\"#specs\" role=\"tab\" data-toggle=\"tab\">规格参数</a></li>\n                         