INSERT INTO `cmswing_temp` VALUES (19, 'home', 'inc', 'nav', '公共底部菜单(手机)', '<nav class=\"mui-bar mui-bar-tab\">\n    <a class=\"mui-tab-item \" href=\"/index\">\n        <span class=\"mui-icon iconfont icon-home\"></span>\n        <span class=\"mui-tab-label\">首页</span>\n    </a>\n    <a class=\"mui-tab-item {% if 8 == category.model %}mui-active{%endif%}\" href=\"/question\">\n        <span class=\"mui-icon iconfont icon-community\"></span>\n        <span class=\"mui-tab-label\">社区</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#wait\">\n        <span class=\"mui-icon mui-icon-contact\"></span>\n        <span class=\"mui-tab-label\">招聘</span>\n    </a>\n    <a class=\"mui-tab-item {% if 4 == category.model %}mui-active{%endif%}\" href=\"/shangchengceshi\">\n        <span class=\"mui-icon iconfont icon-shoplight\"></span>\n        <span class=\"mui-tab-label\">购物</span>\n    </a>\n    {#\n    <a class=\"mui-tab-item\" href=\"#tabbar-with-chat\">\n        <span class=\"mui-icon iconfont icon-gouwuche\"><span class=\"mui-badge\">9</span></span>\n        <span class=\"mui-tab-label\">购物车</span>\n    </a>\n    #}\n    {% if controller.is_login %}\n    <a class=\"mui-tab-item\" href=\"/uc\">\n        <span class=\"mui-icon iconfont icon-my\"></span>\n        <span class=\"mui-tab-label\">我的</span>\n    </a>\n    {%else%}\n    <a class=\"mui-tab-item\" href=\"/uc/public/login\">\n        <span class=\"mui-icon iconfont icon-myfill\"></span>\n        <span class=\"mui-tab-label\">登录</span>\n    </a>\n    {%endif%}\n</nav>', 2, 1);
INSERT INTO `cmswing_temp` VALUES (20, 'home', 'cover', 'download', '下载封面模版', '{% extends \"./inc_base.html\" %}\n{% block content %}\n<!--\n           PAGE HEADER\n\n           CLASSES:\n               .page-header-xs	= 20px margins\n               .page-header-md	= 50px margins\n               .page-header-lg	= 80px margins\n               .page-header-xlg= 130px margins\n               .dark			= dark page header\n\n               .shadow-before-1 	= shadow 1 header top\n               .shadow-after-1 	= shadow 1 header bottom\n               .shadow-before-2 	= shadow 2 header top\n               .shadow-after-2 	= shadow 2 header bottom\n               .shadow-before-3 	= shadow 3 header top\n               .shadow-after-3 	= shadow 3 header bottom\n       -->\n<section class=\"page-header page-header-xs\">\n\n    <div class=\"container\">\n        <!-- breadcrumbs -->\n        <ol class=\"breadcrumb breadcrumb-inverse\">\n            <li><a href=\"/\">首页 </a></li>\n            {%for val in breadcrumb %}\n            {% if val.id == category.id %}\n            <li class=\"active\">{{val.title}}</li>\n            {% else %}\n            <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n\n            {% endif %}\n            {% endfor %}\n        </ol><!-- /breadcrumbs -->\n\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n\n        <div class=\"row\">\n\n            <!--中间-->\n            <div class=\" col-md-9 col-sm-9\">\n                <div class=\"row\">\n                    <div class=\"col-md-9 col-sm-9\">\n\n                        <!-- REVOLUTION SLIDER -->\n                        <div class=\"slider fullwidthbanner-container roundedcorners margin-bottom-20\">\n                            <!--\n                                Navigation Styles:\n\n                                    data-navigationStyle=\"\" theme default navigation\n\n                                    data-navigationStyle=\"preview1\"\n                                    data-navigationStyle=\"preview2\"\n                                    data-navigationStyle=\"preview3\"\n                                    data-navigationStyle=\"preview4\"\n\n                                Bottom Shadows\n                                    data-shadow=\"1\"\n                                    data-shadow=\"2\"\n                                    data-shadow=\"3\"\n\n                                Slider Height (do not use on fullscreen mode)\n                                    data-height=\"300\"\n                                    data-height=\"350\"\n                                    data-height=\"400\"\n                                    data-height=\"450\"\n                                    data-height=\"500\"\n                                    data-height=\"550\"\n                                    data-height=\"600\"\n                                    data-height=\"650\"\n                                    data-height=\"700\"\n                                    data-height=\"750\"\n                                    data-height=\"800\"\n                            -->\n                            <div class=\"fullwidthbanner\" data-height=\"500\" data-shadow=\"0\" data-navigationStyle=\"\">\n                                <ul class=\"hide\">\n                                    {% topic data = \"slider\",limit=\n                                    \"5\",cid=category.id,position=\'2\',issub=\"1\",ispic=\"1\",isstu=\"1\"%}\n                                    {% for v in slider%}\n                                    {% set pic = v.fileimgs|strToArray%}\n                                    <!-- SLIDE  -->\n                                    <li data-transition=\"fade\" data-slotamount=\"7\" data-masterspeed=\"300\"\n                                        data-saveperformance=\"off\">\n                                        <img src=\"/static/assets/images/1x1.png\"\n                                             data-lazyload=\"{{pic[0]|get_pic(\'m=1,w=555,h=237\')}}\" alt=\"\"\n                                             data-bgfit=\"cover\" data-bgposition=\"center center\"\n                                             data-bgrepeat=\"no-repeat\"/>\n\n                                        <div class=\"tp-caption lft start\"\n                                             data-x=\"0\"\n                                             data-y=\"0\"\n                                             data-speed=\"750\"\n                                             data-start=\"750\"\n                                             data-easing=\"easeOutExpo\"\n                                             data-elementdelay=\"0.1\"\n                                             data-endelementdelay=\"0.1\"\n                                             data-endspeed=\"300\">\n                                            <img src=\"/static/assets/images/1x1.png\" alt=\"\"\n                                                 data-lazyload=\"/static/assets/images/caption_bg.png\">\n                                        </div>\n\n                                        <div class=\"tp-caption medium_light_white lfb start\"\n                                             data-x=\"left\" data-hoffset=\"40\"\n                                             data-y=\"96\"\n                                             data-speed=\"300\"\n                                             data-start=\"1200\"\n                                             data-easing=\"easeOutExpo\"\n                                             data-splitin=\"none\"\n                                             data-splitout=\"none\"\n                                             data-elementdelay=\"0.1\"\n                                             data-endelementdelay=\"0.1\"\n                                             data-endspeed=\"300\">\n                                            {{v.title|insert_flg(\"<br>\",12)|safe}}\n                                        </div>\n\n                                        <div class=\"tp-caption block_black lfl start\"\n                                             data-x=\"left\" data-hoffset=\"180\"\n                                             data-y=\"195\"\n                                             data-speed=\"300\"\n                                             data-start=\"1500\"\n                                             data-easing=\"easeOutExpo\"\n                                             data-splitin=\"none\"\n                                             data-splitout=\"none\"\n                                             data-elementdelay=\"0.1\"\n                                             data-endelementdelay=\"0.1\"\n                                             data-endspeed=\"300\">\n                                            <a href=\"{{v.name|get_url(v.id)}}\">查看详情</a>\n                                        </div>\n                                        {#\n                                        <div class=\"tp-caption small_light_white lfl start\"\n                                             data-x=\"left\" data-hoffset=\"40\"\n                                             data-y=\"250\"\n                                             data-speed=\"1000\"\n                                             data-start=\"1800\"\n                                             data-easing=\"easeOutExpo\"\n                                             data-splitin=\"none\"\n                                             data-splitout=\"none\"\n                                             data-elementdelay=\"0.1\"\n                                             data-endelementdelay=\"0.1\"\n                                             data-endspeed=\"300\">\n                                            {{v.description|insert_flg(\"<br>\",20)|safe}}\n                                        </div>\n                                        #}\n                                    </li>\n                                    {%endfor%}\n\n\n                                </ul>\n\n                                <div class=\"tp-bannertimer\"><!-- progress bar --></div>\n                            </div>\n                        </div>\n                        <div class=\"box-light clearfix margin-bottom-20\" style=\"padding: 1px\">\n                            <!-- POPULAR POSTS -->\n\n                            <div class=\"box-inner\">\n                                <h3>\n                                    <a class=\"pull-right size-11 text-warning\" href=\"#\">VIEW ALL</a>\n\n                                    热门软件\n                                </h3>\n                                <div class=\" slimscroll row\" style=\"height: 412px\" data-always-visible=\"true\"\n                                     data-size=\"5px\" data-position=\"right\" data-opacity=\"0.4\"\n                                     disable-body-scroll=\"true\">\n                                    {% topic data = \"hotdown\",limit=\n                                    \"20\",cid=category.id,issub=\"1\",ispic=\"1\",isstu=\"2\",type=\"hot\"%}\n                                    {%for val in hotdown%}\n                                    <div class=\"margin-bottom-10 col-sm-6\"><!-- post item -->\n                                        <img class=\"thumbnail pull-left\" src=\"{{val.cover_id|get_pic(\'m=1,w=60,h=60\')}}\"\n                                             width=\"60\" height=\"60\" alt=\"\"/>\n                                        <h4 class=\"size-13 nomargin noborder nopadding\" style=\"height: 40px\"><a\n                                                href=\"{{val.name|get_url(val.id)}}\">{{val.title}}</a></h4>\n                                        <span class=\"size-11 text-muted\"><a href=\"{{val.name|get_url(val.id)}}\"\n                                                                            class=\"btn btn-3d btn-xs btn-reveal btn-white\">\n									<i class=\"fa fa-download\"></i>\n									<span>立即下载</span>\n								</a></span>\n                                    </div><!-- /post item -->\n                                    {%endfor%}\n\n\n                                </div>\n\n\n                            </div>\n                            <!-- /POPULAR POSTS -->\n                        </div>\n\n\n                    </div>\n                    <!--中间-->\n                    <!--右边-->\n                    <div class=\"col-md-3 col-sm-3 hiddex-xs\">\n                        <div class=\"panel panel-default\">\n                            <div class=\"panel-body padding-6\" style=\"height: 270px\">\n                                <div class=\" margin-bottom-6\">\n                                    <a href=\"#\"><img class=\"img-responsive\" src=\"//odhs9iog7.qnssl.com/2016022317481056cc2ada3247a.jpg\" style=\"height: 100px\"></a>\n                                </div>\n                                <div class=\" margin-bottom-0\">\n                                    <a href=\"#\"><img class=\"img-responsive\" src=\"//odhs9iog7.qnssl.com/20150325144205551258bd5416c.jpg\" style=\"height: 100px\"></a>\n                                </div>\n                            </div>\n                        </div>\n                        <div class=\"panel panel-default\">\n                            <div class=\"panel-heading\">\n                                <h2 class=\"panel-title\">软件推荐</h2>\n                            </div>\n                            <div class=\"panel-body padding-10\">\n                                <!--topic tags test-->\n                                {% topic data = \"hotlist\",limit= \"6\",cid=category.id,type=\"hot\",position=\'1\'%}\n                                <!--返回数据赋值给变量data,遍历data-->\n                                {%for v in hotlist%}\n                                <div class=\"row tab-post\"><!-- post -->\n                                    {%if v.cover_id%}\n                                    <div class=\"col-md-3 col-sm-3 col-xs-3 padding-0\">\n                                        <a href=\"{{v.name|get_url(v.id)}}\">\n                                            <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\"/>\n                                        </a>\n                                    </div>\n                                    {%endif%}\n                                    <div class=\"col-md-9 col-sm-9 col-xs-9\" >\n                                        <a href=\"{{v.name|get_url(v.id)}}\" style=\"overflow: hidden; height: 16px ; display: block\" class=\"tab-post-link margin-bottom-10\">{{v.title}}</a>\n                                        <small><a href=\"{{v.name|get_url(v.id)}}\"\n                                                  class=\"btn btn-3d btn-xs btn-reveal btn-white\">\n                                            <i class=\"fa fa-download\"></i>\n                                            <span>立即下载</span>\n                                        </a></small>\n                                    </div>\n                                </div><!-- /post -->\n                                {%endfor%}\n                            </div>\n                        </div>\n                    </div>\n                    <!--右边-->\n                    <div class=\"col-md-12\">\n                        <div class=\"panel panel-default\">\n                            <div class=\"panel-heading\">\n                                <a class=\"btn btn-danger btn-xs pull-right\">Header Button</a>\n                                <h2 class=\"panel-title\">常用软件</h2>\n                            </div>\n                            <div class=\"panel-body padding-top-0 padding-bottom-0\">\n                                <div class=\"boxTab row\" style=\"margin-right: -16px\">\n                                    {%column data=\"cates\",cid=category.id%}\n                                    {%for val in cates%}\n                                    <ul class=\"col-md-2\">\n                                        <p>{{val.name}}</p>\n                                        {% topic data =\"list\",limit=\"3\",cid=val.id,type=\"level\"%}\n                                        {% for v in list %}\n                                        <li class=\"mix\"><a href=\"#\" target=\"_blank\" class=\"rank-img\"> <img\n                                                src=\"{{v.cover_id|get_pic(\'m=0,w=50,h=50\')}}\"\n                                                alt=\"{{v.title}}\"> </a> <a href=\"{{v.name|get_url(v.id)}}\"\n                                                                           target=\"_blank\" class=\"rank-name\">{{v.title}}</a><a href=\"{{v.name|get_url(v.id)}}\"\n                                                                                                                               class=\"btn btn-3d btn-xs btn-reveal btn-white rank-dl\">\n                                            <i class=\"fa fa-download\"></i>\n                                            <span>立即下载</span>\n                                        </a></li>\n                                        {%endfor%}\n\n                                    </ul>\n                                    {%endfor%}\n\n\n\n                                </div>\n                            </div>\n                        </div>\n\n\n                    </div>\n                </div>\n            </div>\n            <!--左边-->\n            <div class=\"col-md-3 col-sm-3\">\n                <!-- INLINE SEARCH -->\n                <div class=\"inline-search clearfix margin-bottom-20\">\n                    <form action=\"/search\" method=\"get\" class=\"widget_search\" target=\"_blank\">\n                        <input type=\"search\" placeholder=\"请输入关键词...\" id=\"q\" name=\"q\" class=\"serch-input\">\n                        <button type=\"submit\">\n                            <i class=\"fa fa-search\"></i>\n                        </button>\n                    </form>\n                </div>\n                <!-- /INLINE SEARCH -->\n\n                <!-- side navigation -->\n                {%column data=\"cate\",cid=category.id,isnum=\"1\"%}\n                {%if cate%}\n                <div class=\"panel panel-default side-nav\">\n                    <div class=\"panel-heading\">\n                        <h2 class=\"panel-title\">{{category.title}}分类</h2>\n                    </div>\n                    <div class=\"panel-body\" style=\"padding: 10px 0 0 10px\">\n                        <ul class=\"list-group list-group-bordered  uppercase\">\n                            {%for val in cate%}\n                            <li class=\"list-group-item\"><a href=\"{{val.url}}\"><span class=\"badge badge-light pull-right\">{{val.doc_num}}</span>\n                                {{val.name}}</a></li>\n                            {%endfor%}\n                        </ul>\n                    </div>\n                </div>\n                {%endif%}\n\n                <div class=\"panel panel-default tabs\">\n                    <div class=\"panel-heading\">\n                        <ul class=\"nav nav-tabs nav-justified\">\n                            <li class=\"active\">\n                                <a href=\"#tab_1\" data-toggle=\"tab\">\n                                    最热\n                                </a>\n                            </li>\n                            <li>\n                                <a href=\"#tab_2\" data-toggle=\"tab\">\n                                    最新\n                                </a>\n                            </li>\n                        </ul>\n                    </div>\n                    <div class=\"panel-body tab-content\">\n                        <!-- POPULAR -->\n                        <div id=\"tab_1\" class=\"tab-pane active\">\n                            <!--topic tags test-->\n                            {% topic data = \"hotlist\",limit= \"5\",cid=category.id,type=\"hot\"%}\n                            <!--返回数据赋值给变量data,遍历data-->\n                            {%for v in hotlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\"/>\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small><a href=\"{{v.name|get_url(v.id)}}\"\n                                              class=\"btn btn-3d btn-xs btn-reveal btn-white\">\n                                        <i class=\"fa fa-download\"></i>\n                                        <span>立即下载</span>\n                                    </a></small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n\n                        </div>\n                        <!-- /POPULAR -->\n\n\n                        <!-- RECENT -->\n                        <div id=\"tab_2\" class=\"tab-pane\">\n\n                            {% topic data = \"newlist\",limit= \"5\",cid=category.id%}\n                            {%for v in newlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\"/>\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small><a href=\"{{val.name|get_url(val.id)}}\"\n                                              class=\"btn btn-3d btn-xs btn-reveal btn-white\">\n                                        <i class=\"fa fa-download\"></i>\n                                        <span>立即下载</span>\n                                    </a></small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n                            <!-- /post -->\n                        </div>\n                        <!-- /RECENT -->\n                    </div>\n                </div>\n            </div>\n            <!-- 左边 -->\n\n        </div>\n</section>\n<!-- / -->\n{% endblock %}\n', 1, 1);
INSERT INTO `cmswing_temp` VALUES (21, 'home', 'list', 'download', '下载列表模版', '{% extends \"../inc/base.html\" %}\n{% block content %}\n{%set cate = category.id|get_cate()%}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header dark page-header-xs parallax parallax-2\" style=\"background-image:url(\'/static/assets/images/demo/panorama/2-min.jpg\')\">\n    <div class=\"overlay dark-2\"><!-- dark overlay [1 to 9 opacity] --></div>\n    <div class=\"container\">\n\n        <h1>{{category.title}}</h1>\n\n        <!-- breadcrumbs -->\n        <ol class=\"breadcrumb f-14\">\n            <li><a href=\"/\">首页</a></li>\n            {%for val in breadcrumb %}\n            {% if val.id == category.id %}\n            <li class=\"active\">{{val.title}}</li>\n            {% else %}\n            <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n\n            {% endif %}\n            {% endfor %}\n        </ol><!-- /breadcrumbs -->\n        <!-- page tabs -->\n        {#\n        {%column data=\"column\",pid=category.pid%}\n\n        {%if column%}\n        <ul class=\"page-header-tabs list-inline\">\n\n            <!--<li class=\"active\"><a href=\"portfolio-masonry-3-columns.html\">With Gutter</a></li>-->\n            {%for val in cate%}\n            <li  {%if val.id == category.id %}class=\"active\"{%endif%}><a href=\"{{val.url}}\">{{val.name}}</a></li>\n            {%endfor%}\n        </ul>\n        {%endif%}\n        #}\n        <!-- /page tabs -->\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n        <!--分类信息-->\n        {%if sort%}\n        {%if sort.types|length >1%}\n        <ul class=\"nav nav-tabs\">\n            {%for val in sort.types%}\n            <li class=\"{%if sortid == val.enable%}active{%endif%}\"><a href=\"{{cate.url}}/{{group_id}}/{{val.enable}}\" >{{val.name}}</a></li>\n            {%endfor%}\n\n        </ul>\n        {%endif%}\n        <div class=\"tab-content table-bordered\" style=\"padding: 0; margin-bottom: 20px; margin-top: -1px\">\n            <table class=\"table table-hover  m-b-none\" style=\"margin-top: -1px\">\n                <tbody>\n                {% set typenum = 1%}\n                {%for val in typevar%}\n                {%if val.search > 1%}\n                <tr {% if typenum > 3 %} class=\"type_tr_m\"  style=\"display:none\"{%endif%} >\n                    <td style=\"width: 120px\" class=\"text-right\">\n                        {{val.option.title}}:\n                    </td>\n                    <td>\n                        {%if 0 == nsobj[val.option.identifier] or null == nsobj[val.option.identifier]%}\n                        <span class=\"btn btn-danger btn-xs relative\">全部</span>\n                        {%else%}\n                        <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{0|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\" >全部</a>\n                        {%endif%}\n                        {%for v in val.rules %}\n                        {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                        <span class=\"btn btn-danger btn-xs relative\" >{{v.name}}{{val.option.unit}}</span>\n                        {%else%}\n                        <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v.id|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\" >{{v.name}}{{val.option.unit}}</a>\n                        {%endif%}\n                        {%endfor%}\n                        <!--子栏目-->\n                        {%for v in val.rules %}\n                        {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                        {% if v.children%}\n                        <div class=\"subtsm\">\n\n                            {% for v_ in v.children %}\n                            {%if v_.id|sort_act(nsobj[val.option.identifier])%}\n                            <span class=\"btn btn-danger btn-xs relative\" >{{v_.name}}{{val.option.unit}}</span>\n                            {%else%}\n                            <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v_.id|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\">{{v_.name}}{{val.option.unit}}</a>\n                            {%endif%}\n                            {% endfor %}\n                        </div>\n                        {%endif%}\n                        {%endif%}\n                        {%endfor%}\n                        <!--子栏目-->\n                        {%for v in val.rules %}\n                        {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                        {% if v.children%}\n\n                        {% for v_ in v.children %}\n                        {%if v_.id|sort_act(nsobj[val.option.identifier])%}\n                        {% if v_.children%}\n                        <div class=\"subtsm\">\n                            {% for v__ in v_.children %}\n\n                            {%if v__.id|sort_act(nsobj[val.option.identifier])%}\n\n                            <span class=\"btn btn-danger btn-xs relative\" >{{v__.name}}{{val.option.unit}}</span>\n                            {%else%}\n                            <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v__.id|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\" >{{v__.name}}{{val.option.unit}}</a>\n\n                            {%endif%}\n                            {%endfor%}\n                        </div>\n                        {%endif%}\n                        {%endif%}\n                        {% endfor %}\n\n                        {%endif%}\n                        {%endif%}\n                        {%endfor%}\n                    </td>\n\n                </tr>\n                {% set typenum = typenum+1%}\n                {%endif%}\n                {%endfor%}\n\n                </tbody>\n            </table>\n            {% if typenum > 4%}\n            <a href=\"javascript:;\" class=\"center-block btn btn-xs btn-white type_tr_b1\" style=\"border-top: 1px solid #cfd8e1; display: block\"><span class=\"fa fa-chevron-down\"></span></a>\n            <a href=\"javascript:;\" class=\"center-block btn btn-xs btn-white type_tr_b2\" style=\"border-top: 1px solid #cfd8e1; display: none\"><span class=\"fa fa-chevron-up\"></span></a>\n            {%endif%}\n        </div>\n\n        {%endif%}\n        <!--/分类信息-->\n        <div class=\"row\">\n            <!-- LEFT -->\n            <div class=\"col-md-3 col-sm-3\">\n\n                <!-- INLINE SEARCH -->\n                <div class=\"inline-search clearfix margin-bottom-30\">\n                    <form action=\"\" method=\"get\" class=\"widget_search\">\n                        <input type=\"search\" placeholder=\"Start Searching...\" id=\"s\" name=\"s\" class=\"serch-input\">\n                        <button type=\"submit\">\n                            <i class=\"fa fa-search\"></i>\n                        </button>\n                    </form>\n                </div>\n                <!-- /INLINE SEARCH -->\n\n                <hr />\n\n                <!-- side navigation -->\n                {%column data=\"cates\",cid=category.pid,isnum=\"1\"%}\n                {%if cates%}\n                <div class=\"side-nav margin-bottom-20 margin-top-20\">\n                    <div class=\"side-nav-head\">\n                        <button class=\"fa fa-bars\"></button>\n                        <h4>{{category.title}}</h4>\n                    </div>\n                    <ul class=\"list-group list-group-bordered list-group-noicon uppercase\">\n                        {%for val in cates%}\n                        <li class=\"list-group-item {%if val.id == category.id %}active{%endif%}\"><a href=\"{{val.url}}\"><span class=\"size-11 text-muted pull-right\">({{val.doc_num}})</span> {{val.name}}</a></li>\n                        {%endfor%}\n                    </ul>\n                    <!-- /side navigation -->\n                </div>\n                {%endif%}\n\n\n                <!-- JUSTIFIED TAB -->\n                <div class=\"tabs nomargin-top hidden-xs margin-bottom-10\">\n\n                    <!-- tabs -->\n                    <ul class=\"nav nav-tabs nav-bottom-border nav-justified\">\n                        <li class=\"active\">\n                            <a href=\"#tab_1\" data-toggle=\"tab\">\n                                最热\n                            </a>\n                        </li>\n                        <li>\n                            <a href=\"#tab_2\" data-toggle=\"tab\">\n                                最新\n                            </a>\n                        </li>\n                    </ul>\n\n                    <!-- tabs content -->\n                    <div class=\"tab-content margin-bottom-0 margin-top-10\">\n\n                        <!-- POPULAR -->\n                        <div id=\"tab_1\" class=\"tab-pane active\">\n                            <!-- /post -->\n                            {% topic data = \"hotlist\",limit= \"5\",cid=category.id,type=\"hot\"%}\n                            {%for v in hotlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n\n                        </div>\n                        <!-- /POPULAR -->\n\n\n                        <!-- RECENT -->\n                        <div id=\"tab_2\" class=\"tab-pane\">\n                            {% topic data = \"newlist\",limit= \"5\",cid=category.id%}\n                            {%for v in newlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n                            <!-- /post -->\n                        </div>\n                        <!-- /RECENT -->\n\n                    </div>\n\n                </div>\n                <!-- JUSTIFIED TAB -->\n\n\n                <!-- TAGS -->\n                <h3 class=\"hidden-xs size-16 margin-bottom-10\">标签</h3>\n                <div class=\"hidden-xs margin-bottom-20\">\n                    {% keywords data =\"kws\",type=\"hot\"%}\n                    {%for key in kws%}\n                    <a class=\"tag\" href=\"{{key.url}}\">\n                        <span class=\"txt\">{{key.keyname}}</span>\n                        <span class=\"num\">{{key.videonum}}</span>\n                    </a>\n                    {%endfor%}\n                </div>\n\n\n\n                <hr />\n\n\n                <!-- SOCIAL ICONS -->\n                <div class=\"hidden-xs margin-top-30 margin-bottom-60\">\n                    <a href=\"#\" class=\"social-icon social-icon-border social-facebook pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n                        <i class=\"icon-facebook\"></i>\n                        <i class=\"icon-facebook\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-border social-twitter pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n                        <i class=\"icon-twitter\"></i>\n                        <i class=\"icon-twitter\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-border social-gplus pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n                        <i class=\"icon-gplus\"></i>\n                        <i class=\"icon-gplus\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-border social-linkedin pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n                        <i class=\"icon-linkedin\"></i>\n                        <i class=\"icon-linkedin\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-border social-rss pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Rss\">\n                        <i class=\"icon-rss\"></i>\n                        <i class=\"icon-rss\"></i>\n                    </a>\n                </div>\n\n            </div>\n\n            <!-- RIGHT -->\n            <div class=\"col-md-9 col-sm-9\">\n                <div class=\"text-left\">\n                    <ul class=\"nav nav-pills mix-filter\">\n\n                        <li class=\"filter {%if group_id==0%}active{%endif%}\"><a href=\"{{cate.url}}{{http.config(\'view.file_ext\')}}\">全部</a></li>\n                        {% groups data=\"groups\",cid=category.id%}\n                        {%for v in groups%}\n                        <li class=\"filter {%if group_id==v.id%}active{%endif%}\"><a href=\"{{cate.url}}-{{order}}-{{v.id}}{%if sort%}-{{sortid}}{%endif%}{%if sorturl %}-{%endif%}{{sorturl}}{{http.config(\'view.file_ext\')}}\">{{v.name}}</a></li>\n                        {%endfor%}\n                    </ul>\n                    <!--投稿按钮-->\n                    <div class=\"btn-group btn-group-sm pull-right\">\n                        <a {%if model|length> 1 %} data-toggle=\"dropdown\" {% endif %} class=\"btn btn-primary btn-sm document_add\n                            dropdown-toggle {% if allow == 0 %}disabled{% endif %}\" {%if model|length\n                            == 1 %} href=\"/uc/publish/add/model_id/{{modellist[0].id}}/pid/0/cate_id/{{cate.id}}/group_id/{{group_id}}/sortid/{{sortid}}\"{%endif%}\n                            ><span class=\"fa fa-pencil-square-o\"></span> 投稿 {%if model|length > 1 %}<span class=\"caret\"></span>{% endif %}</a>\n                        {%if model|length > 1 %}\n                        <ul class=\"dropdown-menu\">\n\n                            {% for val in modellist%}\n                            <li> <a href=\"/uc/publish/add/model_id/{{val.id}}/pid/0/cate_id/{{cate.id}}/group_id/{{group_id}}/sortid/{{sortid}}\"\n                                    class=\"text-info\">添加{{val.title}}</a></li>\n                            {% endfor %}\n\n                            <!--<li class=\"divider\"></li>-->\n                            <!--<li><a href=\"#\">添加内容</a></li>-->\n                        </ul>\n                        {% endif %}\n                    </div>\n                    <!--//投稿按钮-->\n                    <div class=\"btn-group btn-group-sm pull-right\">\n                        <a class=\"btn btn-default height-auto {%if order<2%}active{%endif%}\" href=\"{{cate.url}}-{%if order<2%}{{1-order}}{%else%}0{%endif%}{%if group_id or sort%}-{{group_id}}{%endif%}{%if sort%}-{{sortid}}{%endif%}{%if sorturl %}-{%endif%}{{sorturl}}{{http.config(\'view.file_ext\')}}\" >按时间 <span class=\"glyphicon {%if order ==0%}glyphicon-arrow-down{%elif order==1%}glyphicon-arrow-up{%else%}glyphicon-arrow-down{%endif%}\"></span> </a>\n                        <a class=\"btn btn-default height-auto {%if order>=2%}active{%endif%}\" href=\"{{cate.url}}-{%if order>=2%}{%if order ==2%}3{%else%}2{%endif%}{%else%}2{%endif%}{%if group_id or sort%}-{{group_id}}{%endif%}{%if sort%}-{{sortid}}{%endif%}{%if sorturl %}-{%endif%}{{sorturl}}{{http.config(\'view.file_ext\')}}\" style=\"height: auto\">按人气<span class=\"glyphicon  {%if order ==2%}glyphicon-arrow-down{%elif order==3%}glyphicon-arrow-up{%else%}glyphicon-arrow-down{%endif%}\"></span></a>\n                    </div>\n                    <div class=\"divider divider-circle divider-center margin-top-0\"><!-- divider -->\n                        <a href=\"#\"><i class=\"fa fa-rss\"></i></a>\n                    </div>\n                </div>\n\n                {% for val in list %}\n                <!-- POST ITEM -->\n                <div class=\"blog-post-item padding-bottom-10 margin-bottom-10 clearfix\">\n\n                    <!-- IMAGE -->\n\n                    <figure class=\"download-item-image margin-bottom-0\">\n                        <img class=\"thumbnail margin-bottom-0\" src=\"{{val.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" height=\"50\" alt=\"\">\n                    </figure>\n\n                    <div class=\"download-item-content\">\n                        <h2 class=\"size-18 margin-bottom-6\"><a href=\"{{val.name|get_url(val.id)}}\">{{val.title}}</a></h2>\n                        <ul class=\"blog-post-info list-inline padding-bottom-0 margin-bottom-6\" >\n                            <li>\n                                <a href=\"#\">\n                                    <i class=\"fa fa-clock-o\"></i>\n                                    <span class=\"font-lato\">{{val.update_time|dateformat(\'Y-m-d H:i\')}}</span>\n                                </a>\n                            </li>\n                            <li>\n                                <a href=\"#\">\n                                    <i class=\"fa fa-comment-o\"></i>\n                                    <span class=\"font-lato\">{{val.view}} Comments</span>\n                                </a>\n                            </li>\n                            <li>\n                                <a href=\"#\">\n                                    <i class=\"fa fa-user\"></i>\n                                    <span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n                                </a>\n                            </li>\n                        </ul>\n\n                        <p>{{val.description|truncate(90, true, \"...\")}}</p>\n                        <a href=\"{{val.id|getmodelfield(val.model_id,\'file_id\')|get_file(\'savename\',true)}}\" class=\"btn btn-reveal btn-default donwload-btn\">\n                            <i class=\"glyphicon glyphicon-download-alt\"></i>\n                            <span>立即下载</span>\n                        </a>\n                    </div>\n\n                </div>\n                <!-- /POST ITEM -->\n                {%endfor%}\n\n\n                <!-- PAGINATION -->\n                <div class=\"text-left\">\n                    <!-- Pagination Default -->\n                    {{pagination | safe}}\n                    <!-- /Pagination Default -->\n                </div>\n                <!-- /PAGINATION -->\n\n            </div>\n\n\n\n        </div>\n\n    </div>\n</section>\n<!-- / -->\n{% endblock %}', 1, 1);
INSERT INTO `cmswing_temp` VALUES (22, 'home', 'detail', 'download', '下载详情页模版', '{% extends \"../inc/base.html\" %}\n{% block style %}\n<script src=\"/static/admin/js/udeitor/ueditor.parse.min.js\"></script>\n{% endblock %}\n{% block content %}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header page-header-xs \">\n    <div class=\"container\">\n\n        <h1>{{category.title}}</h1>\n\n        <!-- breadcrumbs -->\n        <ol class=\"breadcrumb f-14\">\n            <li><a href=\"/\">首页</a></li>\n            {%for val in breadcrumb %}\n            <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n            {% endfor %}\n        </ol><!-- /breadcrumbs -->\n        {#\n        <!-- page tabs -->\n        {%column data=\"cate\",pid=category.pid%}\n        {%if cate%}\n        <ul class=\"page-header-tabs list-inline\">\n\n            <!--<li class=\"active\"><a href=\"portfolio-masonry-3-columns.html\">With Gutter</a></li>-->\n            {%for val in cate%}\n            <li  {%if val.id == category.id %}class=\"active\"{%endif%}><a href=\"{{val.url}}\">{{val.name}}</a></li>\n            {%endfor%}\n        </ul>\n        {%endif%}<!-- /page tabs -->\n        #}\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n\n        <div class=\"row\">\n\n            <!-- LEFT -->\n\n\n            <div class=\"col-md-9 col-sm-9\">\n                <div class=\"row\">\n                    <!--img-->\n                    <div class=\"col-lg-5 col-sm-5\">\n                        <!-- OWL SLIDER -->\n                        {% if info.fileimgs %}\n                        {%set pics = info.fileimgs|strToArray%}\n                        <div class=\"owl-carousel buttons-autohide controlls-over\" data-plugin-options=\'{\"items\": 1, \"autoPlay\": 3000, \"autoHeight\": false, \"navigation\": true, \"pagination\": true, \"transitionStyle\":\"fadeUp\", \"progressBar\":\"false\"}\'>\n                            {%for v in pics%}\n                            <div>\n                                <img class=\"img-responsive\" src=\" {{v|get_pic(\'m=1,w=336,h=336\')}}\" alt=\"\">\n                            </div>\n                            {%endfor%}\n\n                        </div>\n                        {%else%}\n                        <div>\n                            <img class=\"img-responsive\" src=\" {{null|get_pic(\'m=1,w=336,h=336\')}}\" alt=\"\">\n                        </div>\n                        {%endif%}\n                        <!-- /OWL SLIDER -->\n                    </div>\n                    <div class=\"col-log-7 col-sm-7\">\n                        <!-- buttons -->\n                        <div class=\"pull-right\">\n                            <!-- replace data-item-id width the real item ID - used by js/view/demo.shop.js -->\n                            <a class=\"btn btn-default add-wishlist\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"Add To Wishlist\"><i class=\"fa fa-heart nopadding\"></i></a>\n                            <a class=\"btn btn-default add-compare\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"Add To Compare\"><i class=\"fa fa-bar-chart-o nopadding\" data-toggle=\"tooltip\"></i></a>\n                        </div>\n                        <!-- /buttons -->\n\n                        <!-- price -->\n                        <div class=\"shop-item-price text-danger\">\n                            <h1 class=\"blog-post-title\">{{info.title}}</h1>\n                        </div>\n                        <!-- /price -->\n\n                        <hr />\n                        <div class=\"row\">\n                            <div class=\"clearfix margin-bottom-10 col-md-6\" >\n\n                                <strong>大小:</strong> {{info.size|format_bytes()}}\n                            </div>\n                            <div class=\"clearfix margin-bottom-10 col-md-6\" >\n\n                                <strong>更新:</strong> {{info.update_time|dateformat(\'Y-m-d H:i:s\')}}\n                            </div>\n                            <div class=\"clearfix margin-bottom-10 col-md-6\" >\n\n                                <strong>下载:</strong> {{info.download}}\n                            </div>\n                            <div class=\"clearfix margin-bottom-10 col-md-6\" >\n\n                                <strong>评论:</strong> <a href=\"#SOHUCS\" id=\"changyan_count_unit\"></a>\n                            </div>\n                        </div>\n                        <hr />\n                        {% if info.description !=null %}\n                        <!-- short description -->\n                        <small class=\"text-muted\">{{info.description}}</small>\n                        <hr />\n                        <!-- /short description -->\n                        {% endif %}\n                        {%if info.file_id >0%}\n                        <a href=\"/dlink/{{info.id}}||1\" target=\"_blank\" class=\"btn btn-3d btn-reveal btn-blue lightbox\" data-lightbox=\"iframe\" data-plugin-options=\'{\"type\":\"ajax\", \"closeOnBgClick\":false}\'>\n                            <i class=\"fa fa-download\"></i>\n                            <span>极速下载</span>\n                        </a>\n                        {%endif%}\n                        {%if info.panurl%}\n                        {%set panurl = info.panurl|strToArray(\"\\r\\n\")%}\n                        {%for url in panurl%}\n                        {%set pu = url|strToArray(\"|\")%}\n                        {%if pu[2]%}\n                        <!-- Modal Ajax Lightbox >-->\n                        <a class=\"btn btn-3d btn-reveal btn-dirtygreen lightbox\" href=\"/dlink/{{info.id}}||3||{{pu[1]|trim|urlencode}}\" data-lightbox=\"iframe\" data-plugin-options=\'{\"type\":\"ajax\", \"closeOnBgClick\":false}\'> {{pu[0]}}</a>\n\n                        {%else%}\n                            <a href=\"/dlink/{{info.id}}||2||{{pu[1]|trim|urlencode}}\" class=\"btn btn-3d btn-reveal btn-dirtygreen\" target=\"_blank\">\n                                <i class=\"et-download\"></i>\n                                <span>{{pu[0]}}</span>\n                            </a>\n\n                        {%endif%}\n                        {%endfor%}\n                        {%endif%}\n                    </div>\n                </div>\n\n                <!-- OWL SLIDER -->\n                <!--\n                <div class=\"owl-carousel buttons-autohide controlls-over\" data-plugin-options=\'{\"items\": 1, \"autoPlay\": 4500, \"autoHeight\": false, \"navigation\": true, \"pagination\": true, \"transitionStyle\":\"fadeUp\", \"progressBar\":\"false\"}\'>\n                <a class=\"lightbox\" href=\"assets/images/demo/1200x800/10-min.jpg\" data-plugin-options=\'{\"type\":\"image\"}\'>\n                <img class=\"img-responsive\" src=\"assets/images/demo/content_slider/10-min.jpg\" alt=\"\" />\n                </a>\n                <a class=\"lightbox\" href=\"assets/images/demo/1200x800/3-min.jpg\" data-plugin-options=\'{\"type\":\"image\"}\'>\n                <img class=\"img-responsive\" src=\"assets/images/demo/content_slider/3-min.jpg\" alt=\"\" />\n                </a>\n                <a class=\"lightbox\" href=\"assets/images/demo/1200x800/21-min.jpg\" data-plugin-options=\'{\"type\":\"image\"}\'>\n                <img class=\"img-responsive\" src=\"assets/images/demo/content_slider/21-min.jpg\" alt=\"\" />\n                </a>\n                </div>\n                -->\n                <!-- /OWL SLIDER -->\n\n                <!-- IMAGE -->\n                {#\n                {% if info.cover_id>0 %}\n                <figure class=\"margin-bottom-20\">\n                    <img class=\"img-responsive\" src=\"{{info.cover_id|get_pic(\'m=2,w=847\')}}\" alt=\"{{info.title}}\">\n                </figure>\n                {% endif %}\n                #}\n                <!-- /IMAGE -->\n\n                <!-- VIDEO -->\n                <!--\n                <div class=\"margin-bottom-20 embed-responsive embed-responsive-16by9\">\n                <iframe class=\"embed-responsive-item\" src=\"http://player.youku.com/embed/XMTQ0ODQ2Nzg4OA==\" width=\"800\" height=\"450\"></iframe>\n                </div>\n                -->\n                <!-- /VIDEO -->\n                <div class=\"heading-title heading-dotted text-center margin-bottom-10\">\n                    <h4><span>[详细信息]</span></h4>\n                </div>\n                <!-- article content -->\n                <div id=\"content\">\n                    {{info.content[0]|safe}}\n                </div>\n                <!-- /article content -->\n\n\n                {#\n                <!--打赏-->\n                <div id=\"cyReward\" role=\"cylabs\" data-use=\"reward\" sourceid=\"{{info.id}}\" cid=\"{{category.id}}\" class=\"text-center\"></div>\n                <!--打赏-->\n                <!--评论表情-->\n                <div id=\"cyEmoji\" role=\"cylabs\" data-use=\"emoji\" sourceid=\"{{info.id}}\" cid=\"{{category.id}}\"></div>\n                <!--评论表情-->\n                #}\n                <div class=\"divider divider-dotted\"><!-- divider --></div>\n\n\n                <!-- TAGS -->\n                {%for k in keywords%}\n                <a class=\"tag\" href=\"/keywords/{{k|urlencode}}\">\n                    <span class=\"txt\">{{k}}</span>\n                    <!--<span class=\"num\">12</span>-->\n                </a>\n                {%endfor%}\n                <!-