INSERT INTO `cmswing_temp` VALUES (25, 'home', 'cover', 'picture', '图片封面模板', '{% extends \"./inc_base.html\" %}\n{% block content %}\n<!--\n           PAGE HEADER\n\n           CLASSES:\n               .page-header-xs	= 20px margins\n               .page-header-md	= 50px margins\n               .page-header-lg	= 80px margins\n               .page-header-xlg= 130px margins\n               .dark			= dark page header\n\n               .shadow-before-1 	= shadow 1 header top\n               .shadow-after-1 	= shadow 1 header bottom\n               .shadow-before-2 	= shadow 2 header top\n               .shadow-after-2 	= shadow 2 header bottom\n               .shadow-before-3 	= shadow 3 header top\n               .shadow-after-3 	= shadow 3 header bottom\n       -->\n<section class=\"page-header page-header-xs \" >\n\n    <div class=\"container\">\n{#\n        <h1>{{category.title}}</h1>\n#}\n        <!-- breadcrumbs -->\n        <ol class=\"breadcrumb  breadcrumb-inverse\">\n            <li><a href=\"/\">首页 </a></li>\n            {%for val in breadcrumb %}\n            {% if val.id == category.id %}\n            <li class=\"active\">{{val.title}}</li>\n            {% else %}\n            <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n\n            {% endif %}\n            {% endfor %}\n        </ol><!-- /breadcrumbs -->\n\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n\n        <div class=\"row\">\n\n            <!-- LEFT -->\n\n            <div class=\"col-md-9 col-sm-9\">\n\n                <!-- REVOLUTION SLIDER -->\n                <div class=\"slider fullwidthbanner-container roundedcorners margin-bottom-30\">\n                    <!--\n                        Navigation Styles:\n\n                            data-navigationStyle=\"\" theme default navigation\n\n                            data-navigationStyle=\"preview1\"\n                            data-navigationStyle=\"preview2\"\n                    