INSERT INTO `cmswing_temp` VALUES (31, 'home', 'detail', 'picture', '图片详情页模版', '{% extends \"../inc/base.html\" %}\n{% block style%}\n<!--App自定义的css-->\n<style type=\"text/css\">\n    .mui-preview-image.mui-fullscreen {\n        position: fixed;\n        z-index: 20;\n        background-color: #000;\n    }\n    .mui-preview-header,\n    .mui-preview-footer {\n        position: absolute;\n        width: 100%;\n        left: 0;\n        z-index: 10;\n    }\n    .mui-preview-header {\n        height: 44px;\n        top: 0;\n    }\n    .mui-preview-footer {\n        height: 50px;\n        bottom: 0px;\n    }\n    .mui-preview-header .mui-preview-indicator {\n        display: block;\n        line-height: 25px;\n        color: #fff;\n        text-align: center;\n        margin: 15px auto 4;\n        width: 70px;\n        background-color: rgba(0, 0, 0, 0.4);\n        border-radius: 12px;\n        font-size: 16px;\n    }\n    .mui-preview-image {\n        display: none;\n        -webkit-animation-duration: 0.5s;\n        animation-duration: 0.5s;\n        -webkit-animation-fill-mode: both;\n        animation-fill-mode: both;\n    }\n    .mui-preview-image.mui-preview-in {\n        -webkit-animation-name: fadeIn;\n        animation-name: fadeIn;\n    }\n    .mui-preview-image.mui-preview-out {\n        background: none;\n        -webkit-animation-name: fadeOut;\n        animation-name: fadeOut;\n    }\n    .mui-preview-image.mui-preview-out .mui-preview-header,\n    .mui-preview-image.mui-preview-out .mui-preview-footer {\n        display: none;\n    }\n    .mui-zoom-scroller {\n        position: absolute;\n        display: -webkit-box;\n        display: -webkit-flex;\n        display: flex;\n        -webkit-box-align: center;\n        -webkit-align-items: center;\n        align-items: center;\n        -webkit-box-pack: center;\n        -webkit-justify-content: center;\n        justify-content: center;\n        left: 0;\n        right: 0;\n        bottom: 0;\n        top: 0;\n        width: 100%;\n        height: 100%;\n        margin: 0;\n        -webkit-backface-visibility: hidden;\n    }\n    .mui-zoom {\n        -webkit-transform-style: preserve-3d;\n        transform-style: preserve-3d;\n    }\n    .mui-slider .mui-slider-group .mui-slider-item img {\n        width: auto;\n        height: auto;\n        max-width: 100%;\n        max-height: 100%;\n    }\n    .mui-android-4-1 .mui-slider .mui-slider-group .mui-slider-item img {\n        width: 100%;\n    }\n    .mui-android-4-1 .mui-slider.mui-preview-image .mui-slider-group .mui-slider-item {\n        display: inline-table;\n    }\n    .mui-android-4-1 .mui-slider.mui-preview-image .mui-zoom-scroller img {\n        display: table-cell;\n        vertical-align: middle;\n    }\n    .mui-preview-loading {\n        position: absolute;\n        width: 100%;\n        height: 100%;\n        top: 0;\n        left: 0;\n        display: none;\n    }\n    .mui-preview-loading.mui-active {\n        display: block;\n    }\n    .mui-preview-loading .mui-spinner-white {\n        position: absolute;\n        top: 50%;\n        left: 50%;\n        margin-left: -25px;\n        margin-top: -25px;\n        height: 50px;\n        width: 50px;\n    }\n    .mui-preview-image img.mui-transitioning {\n        -webkit-transition: -webkit-transform 0.5s ease, opacity 0.5s ease;\n        transition: transform 0.5s ease, opacity 0.5s ease;\n    }\n    @-webkit-keyframes fadeIn {\n        0% {\n            opacity: 0;\n        }\n        100% {\n            opacity: 1;\n        }\n    }\n    @keyframes fadeIn {\n        0% {\n            opacity: 0;\n        }\n        100% {\n            opacity: 1;\n        }\n    }\n    @-webkit-keyframes fadeOut {\n        0% {\n            opacity: 1;\n        }\n        100% {\n            opacity: 0;\n        }\n    }\n    @keyframes fadeOut {\n        0% {\n            opacity: 1;\n        }\n        100% {\n            opacity: 0;\n        }\n    }\n    p img {\n        max-width: 100%;\n        height: auto;\n    }\n</style>\n{% endblock%}\n{% block content %}\n\n<header class=\"mui-bar mui-bar-nav\">\n    <a class=\"mui-action-back mui-icon mui-icon-left-nav mui-pull-left\"></a>\n    <!--<span class=\"mui-icon iconfont icon-cart\"><span class=\"mui-badge\">9</span></span>-->\n    <h1 class=\"mui-title\">{{info.title}}</h1>\n    <span class=\"mui-icon iconfont icon-search mui-pull-right\"></span>\n</header>\n<nav class=\"mui-bar mui-bar-tab\">\n    <a class=\"mui-tab-item mui-action-back\" >\n        <span class=\"mui-icon iconfont icon-back\"></span>\n        <span class=\"mui-tab-label\">返回</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#SOHUCS\">\n        <span class=\"mui-icon iconfont icon-comment\"><span class=\"mui-badge\" id=\"changyan_count_unit\">0</span></span>\n        <span class=\"mui-tab-label\">评论</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#shoucang\">\n        <span class=\"mui-icon iconfont icon-favor\"></span>\n        <span class=\"mui-tab-label\">收藏</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#fenxiang\">\n        <span class=\"mui-icon iconfont icon-share\"></span>\n        <span class=\"mui-tab-label\">分享</span>\n    </a>\n\n    {% if controller.is_login %}\n    <a class=\"mui-tab-item\" href=\"/user/index\">\n        <span class=\"mui-icon iconfont icon-my\"></span>\n        <span class=\"mui-tab-label\">我的</span>\n    </a>\n    {%else%}\n    <a class=\"mui-tab-item\" href=\"/user/login\">\n        <span class=\"mui-icon iconfont icon-myfill\"></span>\n        <span class=\"mui-tab-label\">登录</span>\n    </a>\n    {%endif%}\n\n    <a class=\"mui-tab-item\" href=\"#top\">\n        <span class=\"mui-icon iconfont icon-top\"></span>\n        <span class=\"mui-tab-label\">顶部</span>\n    </a>\n\n</nav>\n<div class=\"mui-content detail\" >\n    <div class=\"mui-content-padded\">\n        <div class=\"article-header\">\n            <div class=\"pgc-bar-top mui-clearfix\" >\n                <a href=\"#\" ga_category=\"toutiaohao\" ga_label=\"\" ga_event=\"click\" class=\"avatar-link vwo-media-profile\">\n                    <div class=\"avatar\">\n                        <img src=\"/uc/index/avatar/uid/{{info.uid}}\" class=\"avatar\">\n                    </div>\n                </a>\n                <div class=\"subtitle\">\n                    <p class=\"name\"><a href=\"#\" class=\"screen-name vwo-media-profile\">\n                        <span id=\"mediaName\" style=\"transform-origin: 0px 0px 0px; opacity: 1; transform: scale(1, 1);\">{{info.uid|get_nickname}}</span>\n                        <img src=\"/static/webapp/img/logo.png\">\n                    </a></p>\n                    <a href=\"#\" class=\"time vwo-media-profile\" >\n\n                        <span class=\"original original-fix\">原创</span>\n\n                        <span>{{ info.update_time|dateformat(\'Y-m-d H:i\') }}</span>\n                    </a>\n                </div>\n\n            </div>\n\n            <div class=\"favor-wrapper\">\n                <button type=\"button\" class=\"mui-btn mui-btn-primary mui-btn-outlined\">关注</button>\n            </div>\n        </div>\n    </div>\n    <div class=\"mui-content-padded\">\n        {%set picurls = info.pictureurls|strToArray%}\n        {%set i =0%}\n        {%for v in picurls%}\n        <p><img src=\"{{v|get_pic(\'m=4,w=1024,h=768\')}}\" alt=\"{{info.content[i]|delhtmltags}}\" data-preview-src=\"\" data-preview-group=\"1\"></p>\n        {%if info.content[i]%}\n        <p>{{info.content[i]|safe}}</p>\n        {%endif%}\n        {%set i=i+1%}\n        {%endfor%}\n    </div>\n    <div class=\"mui-content-padded\">\n        <article class=\"article-content\">\n            {%for val in info.content%}\n\n            {{val|safe}}\n\n            {%endfor%}\n        </article>\n    </div>\n    <div id=\"SOHUCS\" sid=\"{{info.id}}\"></div>\n\n</div>\n{% endblock%}\n\n{% block script %}\n<script src=\"/static/webapp/js/mui.zoom.js\"></script>\n<script src=\"/static/webapp/js/mui.previewimage.js\"></script>\n<script id=\"changyan_mobile_js\" charset=\"utf-8\" type=\"text/javascript\"\n        src=\"//changyan.sohu.com/upload/mobile/wap-js/changyan_mobile.js?client_id={{controller.setup.CY_APPID}}&conf={{controller.setup.CY_APPKEY}}\">\n</script>\n<script type=\"text/javascript\" src=\"//assets.changyan.sohu.com/upload/plugins/plugins.count.js\"></script>\n<script type=\"text/javascript\">\n    mui.previewImage();\n    var btn = document.querySelectorAll(\".mui-bar-tab a.mui-tab-item\");\n    for(var i = 0;i<btn.length;i++){\n        btn[i].addEventListener(\"tap\",function () {\n            var href = this.getAttribute(\"href\");\n            if(href==\"#top\"){\n                mui.scrollTo(0,500);\n            }else if(href==\"#shoucang\"){\n                //todo\n                mui.toast(\"功能开发中。。。\")\n                return\n            }else if(href==\"#fenxiang\"){\n                //todo\n                mui.toast(\"功能开发中。。。\")\n                return\n            }else {\n                mui.openWindow({url: href})\n            }\n\n        })\n    }\n\n</script>\n{% endblock %}', 2, 1);
INSERT INTO `cmswing_temp` VALUES (32, 'home', 'sp', 'index', '默认单页面模版', '{% extends \"../inc/base.html\" %}\n{% block content %}\n    <!--\n        PAGE HEADER\n\n        CLASSES:\n            .page-header-xs	= 20px margins\n            .page-header-md	= 50px margins\n            .page-header-lg	= 80px margins\n            .page-header-xlg= 130px margins\n            .dark			= dark page header\n\n            .shadow-before-1 	= shadow 1 header top\n            .shadow-after-1 	= shadow 1 header bottom\n            .shadow-before-2 	= shadow 2 header top\n            .shadow-after-2 	= shadow 2 header bottom\n            .shadow-before-3 	= shadow 3 header top\n            .shadow-after-3 	= shadow 3 header bottom\n    -->\n    <section class=\"page-header dark page-header-lg parallax parallax-3\" style=\"background-image:url(\'/static/assets/images/demo/1200x800/27-min.jpg\')\">\n        <div class=\"overlay dark-3\"><!-- dark overlay [1 to 9 opacity] --></div>\n\n        <div class=\"container\">\n\n            <h1>{{category.title}}</h1>\n\n            <!-- breadcrumbs -->\n            <ol class=\"breadcrumb f-14\">\n                <li><a href=\"/\">首页</a></li>\n                {%for val in breadcrumb %}\n                {% if val.id == category.id %}\n                <li class=\"active\">{{val.title}}</li>\n                {% else %}\n                <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n\n                {% endif %}\n                {% endfor %}\n            </ol><!-- /breadcrumbs -->\n            {%column data=\"column\",pid=breadcrumb[0].id%}\n            {%if column %}\n            <ul class=\"page-header-tabs list-inline\">\n                <!--<li class=\"active\"><a href=\"portfolio-masonry-3-columns.html\">With Gutter</a></li>-->\n                {%for val in column%}\n                <li  {%if val.id == category.id %}class=\"active\"{%endif%}><a href=\"{{val.url}}{{http.config(\'view.file_ext\')}}\">{{val.name}}</a></li>\n                {%endfor%}\n            </ul>\n            {%endif%}\n        </div>\n    </section>\n    <!-- /PAGE HEADER -->\n\n{{category.sp_html_pc|safe}}\n{% endblock %}', 1, 1);
INSERT INTO `cmswing_temp` VALUES (33, 'home', 'list', 'documentation', '官方文档列表模板', '{% extends \"../inc/base.html\" %}\n{% block content %}\n{%set cate = category.id|get_cate()%}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header page-header-xs \" >\n    <div class=\"container\">\n{#\n        <h1>{{category.title}}</h1>\n#}\n        <!-- breadcrumbs -->\n        <ol class=\"breadcrumb breadcrumb-inverse\">\n            <li><a href=\"/\">首页</a></li>\n            {%for val in breadcrumb %}\n            {% if val.id == category.id %}\n            <li class=\"active\">{{val.title}}</li>\n            {% else %}\n            <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n\n            {% endif %}\n            {% endfor %}\n        </ol><!-- /breadcrumbs -->\n        <!-- page tabs -->\n        {%column data=\"column\",pid=category.id%}\n        {%if column%}\n        <ul class=\"page-header-tabs list-inline\">\n\n            <!--<li class=\"active\"><a href=\"portfolio-masonry-3-columns.html\">With Gutter</a></li>-->\n            {%for val in column%}\n            <li  {%if val.id == category.id %}class=\"active\"{%endif%}><a href=\"{{val.url}}{{http.config(\'view.file_ext\')}}\">{{val.name}}</a></li>\n            {%endfor%}\n        </ul>\n        {%endif%}\n        <!-- /page tabs -->\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section >\n    <div class=\"container\">\n\n        <!--分类信息-->\n        {%if sort%}\n        {%if sort.types|length >1%}\n        <ul class=\"nav nav-tabs\">\n            {%for val in sort.types%}\n            <li class=\"{%if sortid == val.enable%}active{%endif%}\"><a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{val.enable}}{{http.config(\'view.file_ext\')}}\" >{{val.name}}</a></li>\n            {%endfor%}\n\n        </ul>\n        {%endif%}\n        <div class=\"tab-content table-bordered\" style=\"padding: 0; margin-bottom: 20px; margin-top: -1px\">\n            <table class=\"table table-hover  m-b-none\" style=\"margin-top: -1px\">\n                <tbody>\n                {% set typenum = 1%}\n                {%for val in typevar%}\n                {%if val.search > 1%}\n                <tr {% if typenum > 3 %} class=\"type_tr_m\"  style=\"display:none\"{%endif%} >\n                    <td style=\"width: 120px\" class=\"text-right\">\n                        {{val.option.title}}:\n                    </td>\n                    <td>\n                        {%if 0 == nsobj[val.option.identifier] or null == nsobj[val.option.identifier]%}\n                        <span class=\"btn btn-danger btn-xs relative\">全部</span>\n                        {%else%}\n                        <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{0|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\" >全部</a>\n                        {%endif%}\n                        {%for v in val.rules %}\n                        {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                        <span class=\"btn btn-danger btn-xs relative\" >{{v.name}}{{val.option.unit}}</span>\n                        {%else%}\n                        <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v.id|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\" >{{v.name}}{{val.option.unit}}</a>\n                        {%endif%}\n                        {%endfor%}\n                        <!--子栏目-->\n                        {%for v in val.rules %}\n                        {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                        {% if v.children%}\n                        <div class=\"subtsm\">\n\n                            {% for v_ in v.children %}\n                            {%if v_.id|sort_act(nsobj[val.option.identifier])%}\n                            <span class=\"btn btn-danger btn-xs relative\" >{{v_.name}}{{val.option.unit}}</span>\n                            {%else%}\n                            <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v_.id|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\">{{v_.name}}{{val.option.unit}}</a>\n                            {%endif%}\n                            {% endfor %}\n                        </div>\n                        {%endif%}\n                        {%endif%}\n                        {%endfor%}\n                        <!--子栏目-->\n                        {%for v in val.rules %}\n                        {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                        {% if v.children%}\n\n                        {% for v_ in v.children %}\n                        {%if v_.id|sort_act(nsobj[val.option.identifier])%}\n                        {% if v_.children%}\n                        <div class=\"subtsm\">\n                            {% for v__ in v_.children %}\n\n                            {%if v__.id|sort_act(nsobj[val.option.identifier])%}\n\n                            <span class=\"btn btn-danger btn-xs relative\" >{{v__.name}}{{val.option.unit}}</span>\n                            {%else%}\n                            <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v__.id|sort_url(val.option.identifier,typevar,nsobj)}}{{http.config(\'view.file_ext\')}}\" class=\"btn btn-clean btn-xs relative\" >{{v__.name}}{{val.option.unit}}</a>\n\n                            {%endif%}\n                            {%endfor%}\n                        </div>\n                        {%endif%}\n                        {%endif%}\n                        {% endfor %}\n\n                        {%endif%}\n                        {%endif%}\n                        {%endfor%}\n                    </td>\n\n                </tr>\n                {% set typenum = typenum+1%}\n                {%endif%}\n                {%endfor%}\n\n                </tbody>\n            </table>\n            {% if typenum > 4%}\n            <a href=\"javascript:;\" class=\"center-block btn btn-xs btn-white type_tr_b1\" style=\"border-top: 1px solid #cfd8e1; display: block\"><span class=\"fa fa-chevron-down\"></span></a>\n            <a href=\"javascript:;\" class=\"center-block btn btn-xs btn-white type_tr_b2\" style=\"border-top: 1px solid #cfd8e1; display: none\"><span class=\"fa fa-chevron-up\"></span></a>\n            {%endif%}\n        </div>\n\n        {%endif%}\n        <!--/分类信息-->\n        <div class=\"row\">\n\n            <!-- LEFT -->\n            <div class=\"col-md-12 col-sm-12\">\n                <div class=\"text-left\">\n                    <ul class=\"nav nav-pills mix-filter\">\n\n                        <li class=\"filter {%if group_id==0%}active{%endif%}\"><a href=\"{{cate.url}}{{http.config(\'view.file_ext\')}}\">全部</a></li>\n                        {% groups data=\"groups\",cid=category.id%}\n                        {%for v in groups%}\n                        <li class=\"filter {%if group_id==v.id%}active{%endif%}\"><a href=\"{{cate.url}}-{{order}}-{{v.id}}{%if sort%}-{{sortid}}{%endif%}{%if sorturl %}-{%endif%}{{sorturl}}{{http.config(\'view.file_ext\')}}\">{{v.name}}</a></li>\n                        {%endfor%}\n                    </ul>\n                    <!--投稿按钮-->\n                    <div class=\"btn-group btn-group-sm pull-right\">\n                        <a {%if model|length> 1 %} data-toggle=\"dropdown\" {% endif %} class=\"btn btn-primary btn-sm document_add\n                            dropdown-toggle {% if allow == 0 %}disabled{% endif %}\" {%if model|length\n                            == 1 %} href=\"/uc/publish/add/model_id/{{modellist[0].id}}/pid/0/cate_id/{{cate.id}}/group_id/{{group_id}}/sortid/{{sortid}}\"{%endif%}\n                            ><span class=\"fa fa-pencil-square-o\"></span> 投稿 {%if model|length > 1 %}<span class=\"caret\"></span>{% endif %}</a>\n                        {%if model|length > 1 %}\n                        <ul class=\"dropdown-menu\">\n\n                            {% for val in modellist%}\n                            <li> <a href=\"/uc/publish/add/model_id/{{val.id}}/pid/0/cate_id/{{cate.id}}/group_id/{{group_id}}/sortid/{{sortid}}\"\n                                    class=\"text-info\">添加{{val.title}}</a></li>\n                            {% endfor %}\n\n                            <!--<li class=\"divider\"></li>-->\n                            <!--<li><a href=\"#\">添加内容</a></li>-->\n                        </ul>\n                        {% endif %}\n                    </div>\n                    <!--//投稿按钮-->\n                    <div class=\"btn-group btn-group-sm pull-right\">\n                        <a class=\"btn btn-default height-auto {%if order<2%}active{%endif%}\" href=\"{{cate.url}}-{%if order<2%}{{1-order}}{%else%}0{%endif%}{%if group_id or sort%}-{{group_id}}{%endif%}{%if sort%}-{{sortid}}{%endif%}{%if sorturl %}-{%endif%}{{sorturl}}{{http.config(\'view.file_ext\')}}\" >按时间 <span class=\"glyphicon {%if order ==0%}glyphicon-arrow-down{%elif order==1%}glyphicon-arrow-up{%else%}glyphicon-arrow-down{%endif%}\"></span> </a>\n                        <a class=\"btn btn-default height-auto {%if order>=2%}active{%endif%}\" href=\"{{cate.url}}-{%if order>=2%}{%if order ==2%}3{%else%}2{%endif%}{%else%}2{%endif%}{%if group_id or sort%}-{{group_id}}{%endif%}{%if sort%}-{{sortid}}{%endif%}{%if sorturl %}-{%endif%}{{sorturl}}{{http.config(\'view.file_ext\')}}\" style=\"height: auto\">按人气<span class=\"glyphicon  {%if order ==2%}glyphicon-arrow-down{%elif order==3%}glyphicon-arrow-up{%else%}glyphicon-arrow-down{%endif%}\"></span></a>\n                    </div>\n\n                    <div class=\"divider divider-circle divider-center margin-top-0\"><!-- divider -->\n                        <a href=\"#\"><i class=\"fa fa-rss\"></i></a>\n                    </div>\n                </div>\n                <ul class=\"shop-item-list row list-inline nomargin\">\n                {% for val in list %}\n                {% set categoryname = val.category_id|get_cate %}\n                    \n                    <!-- ITEM -->\n                    <li class=\"col-lg-3 col-sm-3\">\n\n                        <div class=\"shop-item\">\n\n                            <div class=\"thumbnail\">\n                                <!-- product image(s) -->\n                                <a class=\"shop-item-image\" href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\">\n                                    <img class=\"img-responsive\" src=\"{{val.cover_id|get_pic(\'m=1,w=265,h=360\')}}\" alt=\"shop first image\" />\n                                </a>\n                                <!-- /product image(s) -->\n\n                                <!-- hover buttons -->\n                                <div class=\"shop-option-over\"><!-- replace data-item-id width the real item ID - used by js/view/demo.shop.js -->\n                                    <a class=\"btn btn-default add-wishlist\" href=\"#\" data-item-id=\"10\" data-toggle=\"tooltip\" title=\"Add To Wishlist\"><i class=\"fa fa-heart nopadding\"></i></a>\n                                    <a class=\"btn btn-default add-compare\" href=\"#\" data-item-id=\"10\" data-toggle=\"tooltip\" title=\"Add To Compare\"><i class=\"fa fa-bar-chart-o nopadding\" data-toggle=\"tooltip\"></i></a>\n                                </div>\n                                <!-- /hover buttons -->\n                            </div>\n\n                            <div class=\"shop-item-summary text-center\">\n                                <h2>{{val.title}}</h2>\n\n                                <!-- rating -->\n                                <div class=\"shop-item-rating-line\">\n                                    <div class=\"rating rating-5 size-13\"><!-- rating-0 ... rating-5 --></div>\n                                </div>\n                                <!-- /rating -->\n\n                                <!-- price -->\n                                <div class=\" size-12\">\n                                    {{val.update_time|dateformat(\'Y-m-d H:i\')}}\n                                </div>\n                                <!-- /price -->\n                            </div>\n\n                        </div>\n\n                    </li>\n                    <!-- /ITEM -->\n\n                {%endfor%}\n                </ul>\n\n                <!-- PAGINATION -->\n                <div class=\"text-left\">\n                    <!-- Pagination Default -->\n                    {{pagination | safe}}\n                    <!-- /Pagination Default -->\n                </div>\n                <!-- /PAGINATION -->\n\n            </div>\n\n            <!-- RIGHT -->\n\n        </div>\n\n    </div>\n</section>\n<!-- / -->\n{% endblock %}\n{% block style %}\n<link href=\"/static/assets/css/layout-shop.css\" rel=\"stylesheet\" type=\"text/css\" />\n{% endblock %}', 1, 1);
INSERT INTO `cmswing_temp` VALUES (34, 'home', 'detail', 'documentp', '官方文档封面模板', '{% extends \"./inc_base.html\" %}\n{% block style %}\n<script src=\"/static/admin/js/udeitor/ueditor.parse.min.js\"></script>\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.css\">\n<link rel=\"stylesheet\"\n      href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.theme.default.css\">\n\n<style>\n    .active > .text{\n        display: block !important;\n    }\n    .temp-info{\n        border-right: 1px solid #d4d4d4;\n        background-color: #f0f0f0;\n        color: #444;\n        z-index: 10;\n        box-shadow: 4px 4px 4px #ccc;\n        -webkit-box-shadow: 4px 4px 4px #ccc;\n        -moz-box-shadow: 4px 4px 4px #ccc;\n    }\n    .temp-img{\n        background-color: #fff;\n        padding-top: 15px;\n        padding-bottom: 15px;\n        border-right: 1px solid #d4d4d4;\n    }\n    .temp-detail{\n        padding-top: 15px;\n        padding-bottom: 15px;\n        background-color: #f0f0f0;\n    }\n\n\n</style>\n{% endblock %}\n{% block content %}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header  page-header-xs\" >\n    <div class=\"container\">\n\n        <div class=\"container temp-info \">\n\n\n            <div class=\"row \">\n\n                <!-- IMAGE -->\n                <div class=\"col-lg-3 col-sm-3 temp-img\">\n\n                    <div class=\" relative margin-bottom-3 \">\n\n\n                        <!--\n                        IMAGE ZOOM\n\n                        data-mode=\"mouseover|grab|click|toggle\"\n                        -->\n                        <figure  style=\"position: relative; overflow: hidden;\">\n                            <img class=\"img-responsive\" src=\"{{info.cover_id|get_pic(\'m=1,w=265,h=360\')}}\" alt=\"This is the product title\" data-bd-imgshare-binded=\"1\">\n                           </figure>\n\n                    </div>\n\n                    <!-- Thumbnails (required height:100px) -->\n\n                    <!-- /Thumbnails -->\n\n                </div>\n                <!-- /IMAGE -->\n\n                <!-- ITEM DESC -->\n                <div class=\"col-lg-9 col-sm-9 temp-detail\">\n\n\n                    <!-- buttons -->\n                    <div class=\"pull-right\">\n                        <!-- replace data-item-id width the real item ID - used by js/view/demo.shop.js -->\n                        <a class=\"btn btn-default add-wishlist\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"\" data-original-title=\"Add To Wishlist\"><i class=\"fa fa-heart nopadding\"></i></a>\n                        <a class=\"btn btn-default add-compare\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"\" data-original-title=\"Add To Compare\"><i class=\"fa fa-bar-chart-o nopadding\" data-toggle=\"tooltip\"></i></a>\n                    </div>\n                    <!-- /buttons -->\n\n                    <!-- price -->\n                    <div class=\"shop-item-price\">\n                        <h1 class=\"blog-post-title\">{{info.title}}</h1>\n                    </div>\n                    <!-- /price -->\n                    <hr>\n                    <div class=\"clearfix margin-bottom-10 size-14 tex-888\">\n                        <span>最后更新时间:</span> {{lastinfo.update_time|moment(\'lll\')}}\n                    </div>\n                    <div class=\"clearfix margin-bottom-10 size-14 tex-888\">\n                        <span>阅读:</span> {{info.view}}  <span>关注:</span> {{info.view}}  <span>评论:</span> {{info.view}}\n                    </div>\n                    <div class=\"clearfix margin-bottom-10 size-16\">\n                        <a class=\"tag bg-white\" href=\"#\">\n                            <span class=\"txt\">CmsWing</span>\n                            <span class=\"num\">12</span>\n                        </a><a class=\"tag bg-white\" href=\"#\">\n                        <span class=\"txt\">ThinkJS</span>\n                        <span class=\"num\">12</span>\n                    </a><a class=\"tag bg-white\" href=\"#\">\n                        <span class=\"txt\">js</span>\n                        <span class=\"num\">12</span>\n                    </a>\n                    </div>\n                    <div class=\"clearfix margin-bottom-10 size-14\">\n                        <div style=\"float: left;margin-right: 10px; padding-top: 9px\">分享:</div>  <div style=\"float: left\" class=\"bdsharebuttonbox bdshare-button-style0-24\" data-bd-bind=\"1474896802958\"><a href=\"#\" class=\"bds_more\" data-cmd=\"more\"></a><a href=\"#\" class=\"bds_qzone\" data-cmd=\"qzone\" title=\"分享到QQ空间\"></a><a href=\"#\" class=\"bds_tsina\" data-cmd=\"tsina\" title=\"分享到新浪微博\"></a><a href=\"#\" class=\"bds_tqq\" data-cmd=\"tqq\" title=\"分享到腾讯微博\"></a><a href=\"#\" class=\"bds_renren\" data-cmd=\"renren\" title=\"分享到人人网\"></a><a href=\"#\" class=\"bds_weixin\" data-cmd=\"weixin\" title=\"分享到微信\"></a></div>\n                        <script>window._bd_share_config={\"common\":{\"bdSnsKey\":{},\"bdText\":\"\",\"bdMini\":\"2\",\"bdMiniList\":false,\"bdPic\":\"\",\"bdStyle\":\"0\",\"bdSize\":\"24\"},\"share\":{},\"image\":{\"viewList\":[\"qzone\",\"tsina\",\"tqq\",\"renren\",\"weixin\"],\"viewText\":\"分享到：\",\"viewSize\":\"16\"},\"selectShare\":{\"bdContainerClass\":null,\"bdSelectMiniList\":[\"qzone\",\"tsina\",\"tqq\",\"renren\",\"weixin\"]}};with(document)0[(getElementsByTagName(\'head\')[0]||body).appendChild(createElement(\'script\')).src=\'http://bdimg.share.baidu.com/static/api/js/share.js?v=89860593.js?cdnversion=\'+~(-new Date()/36e5)];</script>\n\n                    </div>\n                    <hr>\n                    <!-- /FORM -->\n                    <div class=\"clearfix\">\n                        <a href=\"{{ptree[0].name|get_url(ptree[0].id)}}\" class=\"btn btn-info\"><i class=\"fa fa-file-text\"></i>阅读</a> <a href=\"#\" class=\"btn btn-warning\"><i class=\"fa fa-heart\"></i>关注</a> <a href=\"#\" class=\"btn btn-danger\"><i class=\"fa fa-smile-o\"></i>打赏</a>\n                    </div>\n\n                </div>\n                <!-- /ITEM DESC -->\n\n\n            </div>\n\n        </div>\n        <!-- page tabs -->\n            <ul class=\"page-header-tabs list-inline \">\n\n                <li class=\"active\"><a href=\"#tab1\" data-toggle=\"tab\">概要</a></li>\n                <li class=\"\"><a href=\"#tab2\" data-toggle=\"tab\">目录</a></li>\n                <li class=\"\"><a href=\"#tab3\" data-toggle=\"tab\">评论(<span id=\"changyan_count_unit\">0</span>)</a></li>\n\n            </ul>\n        <!-- /page tabs -->\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n\n        <div class=\"row\">\n\n            <!-- LEFT -->\n\n            <div class=\"col-md-9 col-sm-9\">\n                <div class=\"tab-content nopadding\">\n                    <div class=\"tab-pane fade in active\" id=\"tab1\">\n                        <!-- article content -->\n                        <div id=\"contentcmswing\">\n\n                            <div class=\"tab-content\">\n                                {%set p = 1%}\n                                {%for val in info.content%}\n                                <div class=\"tab-pane fade {% if p ==1 %} in active{%endif%}\" id=\"page{{p}}\">\n                                    {{val|safe}}\n                                </div>\n                                {% set p = p+1%}\n                                {%endfor%}\n                            </div>\n                            {%if info.content|length >1%}\n                            <!-- Pagination Default -->\n                            <div class=\"text-center\">\n                                <ul class=\"pagination\">\n                                    {%set o = 1%}\n                                    {%for val in info.content%}\n                                    <li {%if o == 1 %}class=\"active\"{%endif%}><a href=\"#page{{o}}\" data-toggle=\"tab\">{{o}}</a></li>\n                                    {% set o = o+1%}\n                                    {%endfor%}\n                                </ul>\n                            </div>\n                            {%endif%}\n                        </div>\n                        <!-- /article content -->\n                    </div>\n                    <div class=\"tab-pane fade\" id=\"tab2\">\n                        <table class=\"table table-hover treetable\" id=\"category\">\n                            <tbody>\n                            {%for val in ptree%}\n                            <tr data-tt-id=\"{{val.id}}\" {%if val.pid != topid %} data-tt-parent-id=\"{{val.pid}}\"{%else%}class=\"active1\" {%endif%}>\n                                <td><a href=\"{{val.name|get_url(val.id)}}\" class=\"text-info\"><span class=\'folder ui-draggable\'>{{val.title}}</span></a></td>\n                            </tr>\n                            {%endfor%}\n\n\n                            </tbody>\n                        </table>\n                    </div>\n                    <div class=\"tab-pane fade\" id=\"tab3\">\n                        <!--打赏-->\n                        <div id=\"cyReward\" role=\"cylabs\" data-use=\"reward\" sourceid=\"{{info.id}}\" cid=\"{{category.id}}\" class=\"text-center\"></div>\n                        <!--打赏-->\n                        <div class=\"divider divider-dotted\"><!-- divider --></div>\n                        <!-- COMMENTS -->\n                        <div id=\"comments\" class=\"comments margin-top-20\">\n                            <!--打分版，用户可以对当前新闻、游戏或者应用等进行1-5级的评分-->\n                            <div id=\"SOHUCS\" sid=\"{{info.id}}\"></div>\n                            <script charset=\"utf-8\" type=\"text/javascript\" src=\"//changyan.sohu.com/upload/changyan.js\" ></script>\n                            <script type=\"text/javascript\">\n                                window._config = { showScore: true };\n                                window.changyan.api.config({\n                                    appid: \'{{controller.setup.CY_APPID}}\',\n                                    conf: \'{{controller.setup.CY_APPKEY}}\'\n                                });\n                            </script>\n                        </div>\n                        <!-- /COMMENTS -->\n                    </div>\n\n                </div>\n\n\n\n                <ul class=\"pager hide\">\n                    {%if previous.id%}\n                    <li class=\"previous\"><a class=\"noborder\" href=\"{{previous.name|get_url(previous.id)}}\" title=\"{{previous.title}}\">&larr; 上一篇:{{previous.title|block(20)}}</a></li>\n                    {%endif%}\n                    {%if next.id%}\n                    <li class=\"next\"><a class=\"noborder\" href=\"{{next.name|get_url(next.id)}}\" title=\"{{next.title}}\">下一篇:{{next.title|block(20)}} &rarr;</a></li>\n                    {%endif%}\n                </ul>\n\n\n\n\n\n            </div>\n            <!-- RIGHT -->\n            <div class=\"col-md-3 col-sm-3\">\n\n                <!-- INLINE SEARCH -->\n                <div class=\"inline-search clearfix margin-bottom-30\">\n                    <form action=\"\" method=\"get\" class=\"widget_search\">\n                        <input type=\"search\" placeholder=\"Start Searching...\" id=\"s\" name=\"s\" class=\"serch-input\">\n                        <button type=\"submit\">\n                            <i class=\"fa fa-search\"></i>\n                        </button>\n                    </form>\n                </div>\n                <!-- /INLINE SEARCH -->\n\n                <hr />\n\n                <!-- JUSTIFIED TAB -->\n                <div class=\"tabs nomargin-top hidden-xs margin-bottom-10\">\n\n                    <!-- tabs -->\n                    <ul class=\"nav nav-tabs nav-bottom-border nav-justified\">\n                        <li class=\"active\">\n                            <a href=\"#tab_1\" data-toggle=\"tab\">\n                                最热\n                            </a>\n                        </li>\n                        <li>\n                            <a href=\"#tab_2\" data-toggle=\"tab\">\n                                最新\n                            </a>\n                        </li>\n                    </ul>\n\n                    <!-- tabs content -->\n                    <div class=\"tab-content margin-bottom-0 margin-top-10\">\n\n                        <!-- POPULAR -->\n                        <div id=\"tab_1\" class=\"tab-pane active\">\n                            <!-- /post -->\n                            {% topic data = \"hotlist\",limit= \"5\",cid=category.id,type=\"hot\"%}\n                            {%for v in hotlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n\n                        </div>\n                        <!-- /POPULAR -->\n\n\n                        <!-- RECENT -->\n                        <div id=\"tab_2\" class=\"tab-pane\">\n                            {% topic data = \"newlist\",limit= \"5\",cid=category.id%}\n                            {%for v in newlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n                            <!-- /post -->\n                        </div>\n                        <!-- /RECENT -->\n\n                    </div>\n                </div>\n                <!-- JUSTIFIED TAB -->\n\n\n            </div>\n\n        </div>\n\n\n    </div>\n</section>\n<!-- / -->\n{% endblock %}\n\n{% block script %}\n<script src=\"/static/admin/js/treetable/jquery.treetable.js\" type=\"text/javascript\"></script>\n<script type=\"text/javascript\" src=\"//assets.changyan.sohu.com/upload/plugins/plugins.count.js\"></script>\n<script type=\"text/javascript\" charset=\"utf-8\" src=\"//changyan.itc.cn/js/??lib/jquery.js,changyan.labs.js?appid={{controller.setup.CY_APPID}}\"></script>\n<script>\n    $(function () {\n        $(\"#category\").treetable({ expandable: true });\n        $(\"#category\").treetable(\"expandAll\")\n    })\n\n    uParse(\'#contentcmswing\', {\n        rootPath: \'/static/admin/js/udeitor/\'\n    })\n</script>\n{% endblock %}\n', 1, 1);
INSERT INTO `cmswing_temp` VALUES (35, 'home', 'detail', 'docdetail', '官方手册内容模板', '{% extends \"../inc/base.html\" %}\n{% block style %}\n<script src=\"/static/admin/js/udeitor/ueditor.parse.min.js\"></script>\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.css\">\n<link rel=\"stylesheet\"\n      href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.theme.default.css\">\n<style>\n    .active > .text{\n        display: block !important;\n    }\n</style>\n{% endblock %}\n{% block content %}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header  page-header-xs\">\n\n    <div class=\"container\">\n\n        <h1>{{pinfo.title}}</h1>\n\n        <!-- breadcrumbs -->\n        <ol class=\"breadcrumb \">\n            <li><a href=\"/\">首页</a></li>\n            {%for val in breadcrumb %}\n            <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n            {% endfor %}\n            <li><a href=\"{{pinfo.name|get_url(pinfo.id)}}\">{{pinfo.title}}</a></li>\n            <li>{{info.title}}</li>\n        </ol><!-- /breadcrumbs -->\n\n\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n\n        <div class=\"row\">\n\n            <!-- LEFT -->\n            <div class=\"col-md-3 col-sm-3\">\n\n                <!-- INLINE SEARCH -->\n                <div class=\"inline-search clearfix margin-bottom-30\">\n                    <form action=\"\" method=\"get\" class=\"widget_search\">\n                        <input type=\"search\" placeholder=\"Start Searching...\" id=\"s\" name=\"s\" class=\"serch-input\">\n                        <button type=\"submit\">\n                            <i class=\"fa fa-search\"></i>\n                        </button>\n                    </form>\n                </div>\n                <!-- /INLINE SEARCH -->\n                <div class=\"side-nav margin-bottom-20 margin-top-20\">\n                    <div class=\"side-nav-head\">\n                        <button class=\"fa fa-bars\"></button>\n                        <h4>目录</h4>\n                    </div>\n                    <ul class=\"list-group list-group-bordered list-group-noicon uppercase\">\n                        <table class=\"table table-hover  treetable\" id=\"category\">\n                            <tbody>\n                            {%for val in ptree%}\n                            <tr data-tt-id=\"{{val.id}}\" {%if val.pid != topid %} data-tt-parent-id=\"{{val.pid}}\"{%endif%} {%if val.id == http.get(\"id\")%}class=\"active\" {%endif%}>\n                                <td><a href=\"{{val.name|get_url(val.id)}}\" class=\"text-info\"><span class=\'folder ui-draggable\'>{{val.title}}</span></a></td>\n                            </tr>\n                            {%endfor%}\n\n\n                            </tbody>\n                        </table>\n                    </ul>\n                    <!-- /side navigation -->\n                </div>\n\n            </div>\n\n            <!-- RIGHT -->\n            <div class=\"col-md-9 col-sm-9\">\n\n                <h1 class=\"blog-post-title text-center\">{{info.title}}</h1>\n                <ul class=\"blog-post-info list-inline\">\n\n                </ul>\n\n\n\n\n                <!-- article content -->\n                <div id=\"contentcmswing\">\n\n                    <div class=\"tab-content\">\n                        {%set p = 1%}\n                        {%for val in info.content%}\n                        <div class=\"tab-pane fade {% if p ==1 %} in active{%endif%}\" id=\"page{{p}}\">\n                            {{val|safe}}\n                        </div>\n                        {% set p = p+1%}\n                        {%endfor%}\n                    </div>\n                    {%if info.content|length >1%}\n                    <!-- Pagination Default -->\n                    <div class=\"text-center\">\n                        <ul class=\"pagination\">\n                            {%set o = 1%}\n                            {%for val in info.content%}\n                            <li {%if o == 1 %}class=\"active\"{%endif%}><a href=\"#page{{o}}\" data-toggle=\"tab\">{{o}}</a></li>\n                            {% set o = o+1%}\n                            {%endfor%}\n                        </ul>\n                    </div>\n                    {%endif%}\n                </div>\n                <!-- /article content -->\n                <div class=\"divider\"></div>\n                <!--打赏-->\n                <div id=\"cyReward\" role=\"cylabs\" data-use=\"reward\" sourceid=\"{{info.id}}\" cid=\"{{category.id}}\" class=\"text-center\"></div>\n                <!--打赏-->\n                <!--&lt;!&ndash;评论表情&ndash;&gt;-->\n                <!--<div id=\"cyEmoji\" role=\"cylabs\" data-use=\"emoji\" sourceid=\"{{info.id}}\" cid=\"{{category.id}}\"></div>-->\n                <!--&lt;!&ndash;评论表情&ndash;&gt;-->\n                <div class=\"divider divider-dotted\"><!-- divider --></div>\n\n\n                <!-- TAGS -->\n                {%for k in keywords%}\n                <a class=\"tag\" href=\"/keywords/{{k|urlencode}}\">\n                    <span class=\"txt\">{{k}}</span>\n                    <!--<span class=\"num\">12</span>-->\n                </a>\n                {%endfor%}\n                <!-- /TAGS -->\n\n\n\n                <!-- SHARE POST -->\n                <div class=\"clearfix margin-top-20\">\n\n<span class=\"pull-left margin-top-6 bold hidden-xs margin-right-10\">\n分享到:\n</span>\n                    <div class=\"bdsharebuttonbox\"><a href=\"#\" class=\"bds_more\" data-cmd=\"more\"></a><a href=\"#\" class=\"bds_qzone\" data-cmd=\"qzone\" title=\"分享到QQ空间\"></a><a href=\"#\" class=\"bds_tsina\" data-cmd=\"tsina\" title=\"分享到新浪微博\"></a><a href=\"#\" class=\"bds_tqq\" data-cmd=\"tqq\" title=\"分享到腾讯微博\"></a><a href=\"#\" class=\"bds_renren\" data-cmd=\"renren\" title=\"分享到人人网\"></a><a href=\"#\" class=\"bds_weixin\" data-cmd=\"weixin\" title=\"分享到微信\"></a></div>\n                    <script>window._bd_share_config={\"common\":{\"bdSnsKey\":{},\"bdText\":\"\",\"bdMini\":\"2\",\"bdMiniList\":false,\"bdPic\":\"\",\"bdStyle\":\"0\",\"bdSize\":\"24\"},\"share\":{},\"image\":{\"viewList\":[\"qzone\",\"tsina\",\"tqq\",\"renren\",\"weixin\"],\"viewText\":\"分享到：\",\"viewSize\":\"16\"},\"selectShare\":{\"bdContainerClass\":null,\"bdSelectMiniList\":[\"qzone\",\"tsina\",\"tqq\",\"renren\",\"weixin\"]}};with(document)0[(getElementsByTagName(\'head\')[0]||body).appendChild(createElement(\'script\')).src=\'http://bdimg.share.baidu.com/static/api/js/share.js?v=89860593.js?cdnversion=\'+~(-new Date()/36e5)];</script>\n                    {#\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-facebook pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n                        <i class=\"icon-facebook\"></i>\n                        <i class=\"icon-facebook\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-twitter pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n                        <i class=\"icon-twitter\"></i>\n                        <i class=\"icon-twitter\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-gplus pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n                        <i class=\"icon-gplus\"></i>\n                        <i class=\"icon-gplus\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-linkedin pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n                        <i class=\"icon-linkedin\"></i>\n                        <i class=\"icon-linkedin\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-pinterest pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Pinterest\">\n                        <i class=\"icon-pinterest\"></i>\n                        <i class=\"icon-pinterest\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-call pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Email\">\n                        <i class=\"icon-email3\"></i>\n                        <i class=\"icon-email3\"></i>\n                    </a>\n                    #}\n                </div>\n                <!-- /SHARE POST -->\n\n\n                <div class=\"divider\"><!-- divider --></div>\n\n\n                <ul class=\"pager\">\n                    {%if previous.id%}\n                    <li class=\"previous\"><a class=\"noborder\" href=\"{{previous.name|get_url(previous.id)}}\" title=\"{{previous.title}}\">&larr; 上一篇:{{previous.title|block(20)}}</a></li>\n                    {%endif%}\n                    {%if next.id%}\n                    <li class=\"next\"><a class=\"noborder\" href=\"{{next.name|get_url(next.id)}}\" title=\"{{next.title}}\">下一篇:{{next.title|block(20)}} &rarr;</a></li>\n                    {%endif%}\n                </ul>\n\n\n                <div class=\"divider\"><!-- divider --></div>\n\n\n                <!-- COMMENTS -->\n                <div id=\"comments\" class=\"comments margin-top-20\">\n                    <!--打分版，用户可以对当前新闻、游戏或者应用等进行1-5级的评分-->\n                    <div id=\"SOHUCS\" sid=\"{{info.id}}\"></div>\n                    <script charset=\"utf-8\" type=\"text/javascript\" src=\"//changyan.sohu.com/upload/changyan.js\" ></script>\n                    <script type=\"text/javascript\">\n                        window._config = { showScore: true };\n                        window.changyan.api.config({\n                            appid: \'{{controller.setup.CY_APPID}}\',\n                            conf: \'{{controller.setup.CY_APPKEY}}\'\n                        });\n                    </script>\n                </div>\n                <!-- /COMMENTS -->\n\n\n            </div>\n\n        </div>\n\n\n    </div>\n</section>\n<!-- / -->\n{% endblock %}\n\n{% block script %}\n<script src=\"/static/admin/js/treetable/jquery.treetable.js\" type=\"text/javascript\"></script>\n<script type=\"text/javascript\" charset=\"utf-8\" src=\"//changyan.itc.cn/js/??lib/jquery.js,changyan.labs.js?appid={{controller.setup.CY_APPID}}\"></script>\n<script>\n    $(function () {\n        $(\"#category\").treetable({ expandable: true });\n        $(\"#category\").treetable(\"expandAll\")\n    })\n    uParse(\'#contentcmswing\', {\n        rootPath: \'/static/admin/js/udeitor/\'\n    })\n</script>\n{% endblock %}\n', 1, 1);
INSERT INTO `cmswing_temp` VALUES (36, 'home', 'list', 'cwdownload', '官方下载列表模板', '{% extends \"../inc/base.html\" %}\n{% block content %}\n{%set cate = category.id|get_cate()%}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header page-header-xs\" >\n    <div class=\"container\">\n            <div class=\"row\">\n             <!--获取最新cmswing版本下载-->\n                {% topic data = \"lastd\",limit= \"1\",cid=category.id,position=\"1\",isstu=\"1\",issub=\"2\",group=\"1\"%}\n                {%set dtitle = lastd[0].title|strToArray(\"@\")%}\n                <div class=\"col-sm-6\">\n                    <header class=\"margin-bottom-10\">\n                        <h1 class=\"weight-500 size-30 margin-bottom-6\">CmsWing 下载</h1>\n                        <h2 class=\"weight-300 letter-spacing-1 size-13\"><span>版本号:{{dtitle[1]}} / 下载次数:{{lastd[0].download}}  / 更新时间:{{lastd[0].update_time|moment(\"lll\")}}</span></h2>\n                    </header>\n                    <p class=\"margin-bottom-10\"><span class=\"text-navy\">模块化：</span>全新的架构和模块化的开发机制，便于灵活扩展和二次开发。</p>\n                    <p class=\"margin-bottom-10\"><span class=\"text-navy\">模型/栏目/分类信息体系：</span>通过栏目和模型绑定，以及不同的模型类型，不同栏目可以实现差异化的功能，轻松实现诸如资讯、下载、讨论和图片等功能。通过分类信息和栏目绑定，可以自动建立索引表，轻松实现复杂的信息检索。</p>\n                    <p class=\"margin-bottom-10\"><span class=\"text-navy\">用户行为：</span>支持自定义用户行为，可以对单个用户或者群体用户的行为进行记录及分享，为您的运营决策提供有效参考数据。 </p>\n                    <p class=\"margin-bottom-10\"><span class=\"text-navy\">云服务支持：</span>支持云存储、云安全、云过滤和云统计等服务，更多贴心的服务让您的网站更安心。 </p>\n                    <p class=\"margin-bottom-10\"><span class=\"text-navy\">安全稳健：</span>提供稳健的安全策略，包括备份恢复、容错、防止恶意攻击登录，网页防篡改等多项安全管理功能，保证系统安全，可靠、稳定的运行。 </p>\n                    <p class=\"margin-bottom-10 hide\"><span class=\"text-navy\">开源免费：</span>代码遵循MIT开源协议，并且免费使用，对商业用户友好。  </p>\n\n\n                    <hr />\n\n                    <!--\n                        controlls-over		= navigation buttons over the image\n                        buttons-autohide 	= navigation buttons visible on mouse hover only\n\n                        data-plugin-options:\n                            \"singleItem\": true\n                            \"autoPlay\": true (or ms. eg: 4000)\n                            \"navigation\": true\n                            \"pagination\": true\n                    -->\n                    <div class=\"text-center margin-top-20\">\n                        <a href=\"{{lastd[0].name|get_url(lastd[0].id)}}\" target=\"_blank\" class=\"btn btn-3d btn-reveal btn-blue\">\n                            <i class=\"et-download\"></i>\n                            <span>{{dtitle[0]}}</span>\n                        </a>\n                    </div>\n\n                </div>\n\n                <div class=\"col-sm-6\">\n\n                    <!--\n                        controlls-over		= navigation buttons over the image\n                        buttons-autohide 	= navigation buttons visible on mouse hover only\n\n                        data-plugin-options:\n                            \"singleItem\": true\n                            \"autoPlay\": true (or ms. eg: 4000)\n                            \"navigation\": true\n                            \"pagination\": true\n                            \"transitionStyle\":\"fadeUp\" (fade,backSlide,goDown,fadeUp)\n                    -->\n                    <div class=\"owl-carousel buttons-autohide controlls-over\" data-plugin-options=\'{\"singleItem\": true, \"autoPlay\": 3400, \"navigation\": true, \"pagination\": true, \"transitionStyle\":\"fadeUp\"}\'>\n                        <div>\n                            <img class=\"img-responsive\" src=\"/static/dome/macbookpro.png\" alt=\"\">\n                        </div>\n                        <div>\n                            <img class=\"img-responsive\" src=\"/static/dome/ipad.png\" alt=\"\">\n                        </div>\n                        <div>\n                            <img class=\"img-responsive\" src=\"/static/dome/iphone.png\" alt=\"\">\n                        </div>\n                    </div>\n\n                </div>\n\n            </div>\n\n\n\n        <ul class=\"page-header-tabs list-inline\">\n\n            <li class=\"filter {%if group_id==0%}active{%endif%}\"><a href=\"{{cate.url}}{{http.config(\'view.file_ext\')}}\">全部</a></li>\n            {% groups data=\"groups\",cid=category.id%}\n            {%for v in groups%}\n            <li class=\"filter {%if group_id==v.id%}active{%endif%}\"><a href=\"{{cate.url}}-{{order}}-{{v.id}}{%if sort%}-{{sortid}}{%endif%}{%if sorturl %}-{%endif%}{{sorturl}}{{http.config(\'view.file_ext\')}}\">{{v.name}}</a></li>\n            {%endfor%}\n        </ul>\n\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n\n        <div class=\"row\">\n\n            <!-- RIGHT -->\n            <div class=\"col-md-12 col-sm-12\">\n\n                {% for val in list %}\n                <!-- POST ITEM -->\n                <div class=\"blog-post-item padding-bottom-10 margin-bottom-10 clearfix\">\n\n                    <!-- IMAGE -->\n\n                    <figure class=\"download-item-image margin-bottom-0\">\n                        <img class=\"thumbnail margin-bottom-0\" src=\"{{val.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" height=\"50\" alt=\"\">\n                    </figure>\n\n                    <div class=\"download-item-content\">\n                        <h2 class=\"size-18 margin-bottom-6\"><a href=\"{{val.name|get_url(val.id)}}\" title=\"{{val.title}}\" target=\"_blank\" {%if val.group_id==1 and val.position==1%} class=\"text-red\" {%endif%}>{{val.title}}</a></h2>\n                        <ul class=\"blog-post-info list-inline padding-bottom-0 margin-bottom-6\" >\n                            <li>\n                                <a href=\"#\">\n                                    <i class=\"fa fa-clock-o\"></i>\n                                    <span class=\"font-lato\">{{val.update_time|dateformat(\'Y-m-d H:i\')}}</span>\n                                </a>\n                            </li>\n                            <li>\n                                <a href=\"#\">\n                                    <i class=\"fa fa-comment-o\"></i>\n                                    <span class=\"font-lato\">{{val.view}} Comments</span>\n                                </a>\n                            </li>\n                            <li>\n                                <a href=\"#\">\n                                    <i class=\"fa fa-user\"></i>\n                                    <span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n                                </a>\n                            </li>\n                        </ul>\n\n                        <p>{{val.description|truncate(90, true, \"...\")}}</p>\n                        <a href=\"{{val.name|get_url(val.id)}}\" class=\"btn btn-reveal btn-default donwload-btn\">\n                            <i class=\"glyphicon glyphicon-download-alt\"></i>\n                            <span>立即下载</span>\n                        </a>\n                    </div>\n\n                </div>\n                <!-- /POST ITEM -->\n                {%endfor%}\n                <!-- PAGINATION -->\n                <div class=\"text-left\">\n                    <!-- Pagination Default -->\n                    {{pagination | safe}}\n                    <!-- /Pagination Default -->\n                </div>\n                <!-- /PAGINATION -->\n\n   