% set categoryname = val.category_id|get_cate %}\n                    <!-- POST ITEM -->\n\n                    <div class=\"blog-post-item padding-bottom-30 margin-bottom-30\">\n                        <div class=\"timeline-entry\" style=\"height: 77px\"><!-- .rounded = entry -->\n                           <a href=\"#\"><img src=\"/uc/index/avatar/uid/{{val.uid}}\" style=\"width: 58px;height: 58px; margin-top: -10px\"></a><span style=\"text-transform: none;overflow: hidden\">{{val.uid|get_nickname}}</span>\n                            <div class=\"timeline-vline\"><!-- vertical line --></div>\n                        </div>\n                        <!-- IMAGE -->\n                        {% if val.cover_id>0 %}\n                        <figure class=\"blog-item-small-image margin-bottom-0\">\n                            <img class=\"img-responsive\" src=\"{{val.cover_id|get_pic(\'m=1,w=200,h=120\')}}\" alt=\"\">\n                        </figure>\n                        {% endif %}\n                        <div class=\"blog-item-small-content\">\n                            <h2><a href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\" title=\"{{val.title}}\">{{val.title}}</a></h2>\n\n                            <ul class=\"blog-post-info list-inline padding-bottom-10\">\n                                <li>\n                                    <a href=\"#\">\n                                        <i class=\"fa fa-clock-o\"></i>\n                                        <span class=\"font-lato\">{{val.update_time|dateformat(\'Y-m-d H:i\')}}</span>\n                                    </a>\n                                </li>\n                                <li>\n                                    <a href=\"#\">\n                                        <i class=\"fa fa-comment-o\"></i>\n                                        <span class=\"font-lato\">{{val.view}}</span>\n                                    </a>\n                                </li>\n                                <li>\n                                    <i class=\"fa fa-folder-open-o\"></i>\n\n                                    <a class=\"category\" href=\"{{categoryname.url}}\">\n                                        <span class=\"font-lato\">{{categoryname.name}}</span>\n                                    </a>\n                                </li>\n\n                            </ul>\n\n                            <p>{{val.description|truncate(90, true, \"...\")}}</p>\n                        </div>\n                    </div>\n\n                    <!-- /POST ITEM -->\n                    {%endfor%}\n\n                </div>\n                <!-- PAGINATION -->\n                <div class=\"text-left\">\n                    <!-- Pagination Default -->\n                    {{pagination | safe}}\n                    <!-- /Pagination Default -->\n                </div>\n                <!-- /PAGINATION -->\n\n            </div>\n\n            <!-- RIGHT -->\n            <div class=\"col-md-3 col-sm-3\">\n\n                <!-- INLINE SEARCH -->\n                <div class=\"inline-search clearfix margin-bottom-10\">\n                    <form action=\"\" method=\"get\" class=\"widget_search\">\n                        <input type=\"search\" placeholder=\"Start Searching...\" id=\"s\" name=\"s\" class=\"serch-input\">\n                        <button type=\"submit\">\n                            <i class=\"fa fa-search\"></i>\n                        </button>\n                    </form>\n                </div>\n                <!-- /INLINE SEARCH -->\n\n                <hr />\n\n                <!-- side navigation -->\n                {%column data=\"column\",pid=breadcrumb[0].id,isnum=\"1\"%}\n                {%if column|length >0%}\n                <div class=\"side-nav margin-bottom-20 margin-top-20\">\n                    <div class=\"side-nav-head\">\n                        <button class=\"fa fa-bars\"></button>\n                        <h4>{{breadcrumb[0].title}}分类</h4>\n                    </div>\n                    <ul class=\"list-group list-group-bordered list-group-noicon uppercase\">\n                        {%for val in column%}\n                        <li class=\"list-group-item  {%if val.id == category.id %}active{%endif%}\"><a href=\"{{val.url}}{{http.config(\'view.file_ext\')}}\"><span class=\"size-11 text-muted pull-right\">({{val.doc_num}})</span> {{val.name}}</a></li>\n                        {%endfor%}\n                    </ul>\n                    <!-- /side navigation -->\n                </div>\n                {%endif%}\n\n\n                <!-- JUSTIFIED TAB -->\n                <div class=\"tabs nomargin-top hidden-xs margin-bottom-10\">\n\n                    <!-- tabs -->\n                    <ul class=\"nav nav-tabs nav-bottom-border nav-justified\">\n                        <li class=\"active\">\n                            <a href=\"#tab_1\" data-toggle=\"tab\">\n                                最热\n                            </a>\n                        </li>\n                        <li>\n                            <a href=\"#tab_2\" data-toggle=\"tab\">\n                                最新\n                            </a>\n                        </li>\n                    </ul>\n\n                    <!-- tabs content -->\n                    <div class=\"tab-content margin-bottom-0 margin-top-10\">\n\n                        <!-- POPULAR -->\n                        <div id=\"tab_1\" class=\"tab-pane active\">\n                            <!-- /post -->\n                            {% topic data = \"hotlist\",limit= \"5\",cid=category.id,type=\"hot\"%}\n                            {%for v in hotlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n\n                        </div>\n                        <!-- /POPULAR -->\n\n\n                        <!-- RECENT -->\n                        <div id=\"tab_2\" class=\"tab-pane\">\n                            {% topic data = \"newlist\",limit= \"5\",cid=category.id%}\n                            {%for v in newlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n                            <!-- /post -->\n                        </div>\n                        <!-- /RECENT -->\n\n                    </div>\n\n                </div>\n                <!-- JUSTIFIED TAB -->\n\n\n                <!-- TAGS -->\n                <h3 class=\"hidden-xs size-16 margin-bottom-10\">标签</h3>\n                <div class=\"hidden-xs margin-bottom-20\">\n                    {% keywords data =\"kws\",type=\"hot\"%}\n                    {%for key in kws%}\n                    <a class=\"tag\" href=\"{{key.url}}\">\n                        <span class=\"txt\">{{key.keyname}}</span>\n                        <span class=\"num\">{{key.videonum}}</span>\n                    </a>\n                    {%endfor%}\n                </div>\n\n\n                <hr />\n\n\n                <!-- SOCIAL ICONS -->\n                <div class=\"hidden-xs margin-top-30 margin-bottom-60\">\n                    <a href=\"#\" class=\"social-icon social-icon-border social-facebook pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n                        <i class=\"icon-facebook\"></i>\n                        <i class=\"icon-facebook\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-border social-twitter pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n                        <i class=\"icon-twitter\"></i>\n                        <i class=\"icon-twitter\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-border social-gplus pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n                        <i class=\"icon-gplus\"></i>\n                        <i class=\"icon-gplus\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-border social-linkedin pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n                        <i class=\"icon-linkedin\"></i>\n                        <i class=\"icon-linkedin\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-border social-rss pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Rss\">\n                        <i class=\"icon-rss\"></i>\n                        <i class=\"icon-rss\"></i>\n                    </a>\n                </div>\n\n            </div>\n\n        </div>\n\n    </div>\n</section>\n<!-- / -->\n{% endblock %}', 1, 1);
INSERT INTO `cmswing_temp` VALUES (40, 'home', 'detail', 'cwblog', '官方博客模板', '{% extends \"../inc/base.html\" %}\n{% block style %}\n<script src=\"/static/admin/js/udeitor/ueditor.parse.min.js\"></script>\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.css\">\n<link rel=\"stylesheet\"\n      href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.theme.default.css\">\n<style>\n    .active > .text{\n        display: block !important;\n    }\n</style>\n{% endblock %}\n{% block content %}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header  page-header-xs\">\n\n    <div class=\"container\">\n\n        <h1>{{pinfo.title}}</h1>\n\n        <!-- breadcrumbs -->\n        <ol class=\"breadcrumb \">\n            <li><a href=\"/\">首页</a></li>\n            {%for val in breadcrumb %}\n            <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n            {% endfor %}\n            <li><a href=\"{{pinfo.name|get_url(pinfo.id)}}\">{{pinfo.title}}</a></li>\n            <li>{{info.title}}</li>\n        </ol><!-- /breadcrumbs -->\n\n\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n\n        <div class=\"row\">\n\n            <!-- LEFT -->\n            <div class=\"col-md-3 col-sm-3\">\n\n                <!-- INLINE SEARCH -->\n                <div class=\"inline-search clearfix margin-bottom-30\">\n                    <form action=\"\" method=\"get\" class=\"widget_search\">\n                        <input type=\"search\" placeholder=\"Start Searching...\" id=\"s\" name=\"s\" class=\"serch-input\">\n                        <button type=\"submit\">\n                            <i class=\"fa fa-search\"></i>\n                        </button>\n                    </form>\n                </div>\n                <!-- /INLINE SEARCH -->\n                <div class=\"side-nav margin-bottom-20 margin-top-20\">\n                    <div class=\"side-nav-head\">\n                        <button class=\"fa fa-bars\"></button>\n                        <h4>目录</h4>\n                    </div>\n                    <ul class=\"list-group list-group-bordered list-group-noicon uppercase\">\n                        <table class=\"table table-hover  treetable\" id=\"category\">\n                            <tbody>\n                            {%for val in ptree%}\n                            <tr data-tt-id=\"{{val.id}}\" {%if val.pid != topid %} data-tt-parent-id=\"{{val.pid}}\"{%endif%} {%if val.id == http.get(\"id\")%}class=\"active\" {%endif%}>\n                                <td><a href=\"{{val.name|get_url(val.id)}}\" class=\"text-info\"><span class=\'folder ui-draggable\'>{{val.title}}</span></a></td>\n                            </tr>\n                            {%endfor%}\n\n\n                            </tbody>\n                        </table>\n                    </ul>\n                    <!-- /side navigation -->\n                </div>\n\n            </div>\n\n            <!-- RIGHT -->\n            <div class=\"col-md-9 col-sm-9\">\n\n                <h1 class=\"blog-post-title text-center\">{{info.title}}</h1>\n                <ul class=\"blog-post-info list-inline\">\n\n                </ul>\n\n\n\n\n                <!-- article content -->\n                <div id=\"contentcmswing\">\n\n                    <div class=\"tab-content\">\n                        {%set p = 1%}\n                        {%for val in info.content%}\n                        <div class=\"tab-pane fade {% if p ==1 %} in active{%endif%}\" id=\"page{{p}}\">\n                            {{val|safe}}\n                        </div>\n                        {% set p = p+1%}\n                        {%endfor%}\n                    </div>\n                    {%if info.content|length >1%}\n                    <!-- Pagination Default -->\n                    <div class=\"text-center\">\n                        <ul class=\"pagination\">\n                            {%set o = 1%}\n                            {%for val in info.content%}\n                            <li {%if o == 1 %}class=\"active\"{%endif%}><a href=\"#page{{o}}\" data-toggle=\"tab\">{{o}}</a></li>\n                            {% set o = o+1%}\n                            {%endfor%}\n                        </ul>\n                    </div>\n                    {%endif%}\n                </div>\n                <!-- /article content -->\n                <div class=\"divider\"></div>\n                <!--打赏-->\n                <div id=\"cyReward\" role=\"cylabs\" data-use=\"reward\" sourceid=\"{{info.id}}\" cid=\"{{category.id}}\" class=\"text-center\"></div>\n                <!--打赏-->\n                <!--&lt;!&ndash;评论表情&ndash;&gt;-->\n                <!--<div id=\"cyEmoji\" role=\"cylabs\" data-use=\"emoji\" sourceid=\"{{info.id}}\" cid=\"{{category.id}}\"></div>-->\n                <!--&lt;!&ndash;评论表情&ndash;&gt;-->\n                <div class=\"divider divider-dotted\"><!-- divider --></div>\n\n\n                <!-- TAGS -->\n                {%for k in keywords%}\n                <a class=\"tag\" href=\"/keywords/{{k|urlencode}}\">\n                    <span class=\"txt\">{{k}}</span>\n                    <!--<span class=\"num\">12</span>-->\n                </a>\n                {%endfor%}\n                <!-- /TAGS -->\n\n\n\n                <!-- SHARE POST -->\n                <div class=\"clearfix margin-top-20\">\n\n<span class=\"pull-left margin-top-6 bold hidden-xs margin-right-10\">\n分享到:\n</span>\n                    <div class=\"bdsharebuttonbox\"><a href=\"#\" class=\"bds_more\" data-cmd=\"more\"></a><a href=\"#\" class=\"bds_qzone\" data-cmd=\"qzone\" title=\"分享到QQ空间\"></a><a href=\"#\" class=\"bds_tsina\" data-cmd=\"tsina\" title=\"分享到新浪微博\"></a><a href=\"#\" class=\"bds_tqq\" data-cmd=\"tqq\" title=\"分享到腾讯微博\"></a><a href=\"#\" class=\"bds_renren\" data-cmd=\"renren\" title=\"分享到人人网\"></a><a href=\"#\" class=\"bds_weixin\" data-cmd=\"weixin\" title=\"分享到微信\"></a></div>\n                    <script>window._bd_share_config={\"common\":{\"bdSnsKey\":{},\"bdText\":\"\",\"bdMini\":\"2\",\"bdMiniList\":false,\"bdPic\":\"\",\"bdStyle\":\"0\",\"bdSize\":\"24\"},\"share\":{},\"image\":{\"viewList\":[\"qzone\",\"tsina\",\"tqq\",\"renren\",\"weixin\"],\"viewText\":\"分享到：\",\"viewSize\":\"16\"},\"selectShare\":{\"bdContainerClass\":null,\"bdSelectMiniList\":[\"qzone\",\"tsina\",\"tqq\",\"renren\",\"weixin\"]}};with(document)0[(getElementsByTagName(\'head\')[0]||body).appendChild(createElement(\'script\')).src=\'http://bdimg.share.baidu.com/static/api/js/share.js?v=89860593.js?cdnversion=\'+~(-new Date()/36e5)];</script>\n                    {#\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-facebook pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n                        <i class=\"icon-facebook\"></i>\n                        <i class=\"icon-facebook\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-twitter pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n                        <i class=\"icon-twitter\"></i>\n                        <i class=\"icon-twitter\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-gplus pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n                        <i class=\"icon-gplus\"></i>\n                        <i class=\"icon-gplus\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-linkedin pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n                        <i class=\"icon-linkedin\"></i>\n                        <i class=\"icon-linkedin\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-pinterest pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Pinterest\">\n                        <i class=\"icon-pinterest\"></i>\n                        <i class=\"icon-pinterest\"></i>\n                    </a>\n\n                    <a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-call pull-right\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Email\">\n                        <i class=\"icon-email3\"></i>\n                        <i class=\"icon-email3\"></i>\n                    </a>\n                    #}\n                </div>\n                <!-- /SHARE POST -->\n\n\n                <div class=\"divider\"><!-- divider --></div>\n\n\n                <ul class=\"pager\">\n                    {%if previous.id%}\n                    <li class=\"previous\"><a class=\"noborder\" href=\"{{previous.name|get_url(previous.id)}}\" title=\"{{previous.title}}\">&larr; 上一篇:{{previous.title|block(20)}}</a></li>\n                    {%endif%}\n                    {%if next.id%}\n                    <li class=\"next\"><a class=\"noborder\" href=\"{{next.name|get_url(next.id)}}\" title=\"{{next.title}}\">下一篇:{{next.title|block(20)}} &rarr;</a></li>\n                    {%endif%}\n                </ul>\n\n\n                <div class=\"divider\"><!-- divider --></div>\n\n\n                <!-- COMMENTS -->\n                <div id=\"comments\" class=\"comments margin-top-20\">\n                    <!--打分版，用户可以对当前新闻、游戏或者应用等进行1-5级的评分-->\n                    <div id=\"SOHUCS\" sid=\"{{info.id}}\"></div>\n                    <script charset=\"utf-8\" type=\"text/javascript\" src=\"//changyan.sohu.com/upload/changyan.js\" ></script>\n                    <script type=\"text/javascript\">\n                        window._config = { showScore: true };\n                        window.changyan.api.config({\n                            appid: \'{{controller.setup.CY_APPID}}\',\n                            conf: \'{{controller.setup.CY_APPKEY}}\'\n                        });\n                    </script>\n                </div>\n                <!-- /COMMENTS -->\n\n\n            </div>\n\n        </div>\n\n\n    </div>\n</section>\n<!-- / -->\n{% endblock %}\n\n{% block script %}\n<script src=\"/static/admin/js/treetable/jquery.treetable.js\" type=\"text/javascript\"></script>\n<script type=\"text/javascript\" charset=\"utf-8\" src=\"//changyan.itc.cn/js/??lib/jquery.js,changyan.labs.js?appid={{controller.setup.CY_APPID}}\"></script>\n<script>\n    $(function () {\n        $(\"#category\").treetable({ expandable: true });\n        $(\"#category\").treetable(\"expandAll\")\n    })\n    uParse(\'#contentcmswing\', {\n        rootPath: \'/static/admin/js/udeitor/\'\n    })\n</script>\n{% endblock %}\n', 1, 1);
INSERT INTO `cmswing_temp` VALUES (42, 'home', 'detail', 'docdetail', '官方手册内容模板', '{% extends \"../inc/base.html\" %}\n{% block style%}\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/wangEditor/css/wangEditor.min.css\" type=\"text/css\"/>\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.css\">\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.theme.default.css\">\n{%endblock%}\n{% block content %}\n\n<header class=\"mui-bar mui-bar-nav\">\n    <a class=\"mui-action-back mui-icon mui-icon-left-nav mui-pull-left\"></a>\n    <!--<span class=\"mui-icon iconfont icon-cart\"><span class=\"mui-badge\">9</span></span>-->\n    <h1 class=\"mui-title\">{{pinfo.title}}</h1>\n    <span class=\"mui-icon iconfont icon-search mui-pull-right\"></span>\n</header>\n<nav class=\"mui-bar mui-bar-tab\">\n    <a class=\"mui-tab-item \" href=\"{{pinif.name|get_url(pinfo.id)}}\">\n        <span class=\"mui-icon iconfont icon-form\"></span>\n        <span class=\"mui-tab-label\">封面</span>\n    </a>\n    <a class=\"mui-tab-item \"  href=\"#mulu\">\n        <span class=\"mui-icon iconfont icon-sortlight\"></span>\n        <span class=\"mui-tab-label\">目录</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#SOHUCS\">\n        <span class=\"mui-icon iconfont icon-comment\"><span class=\"mui-badge\" id=\"changyan_count_unit\">0</span></span>\n        <span class=\"mui-tab-label\">评论</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#shoucang\">\n        <span class=\"mui-icon iconfont icon-favor\"></span>\n        <span class=\"mui-tab-label\">收藏</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#fenxiang\">\n        <span class=\"mui-icon iconfont icon-share\"></span>\n        <span class=\"mui-tab-label\">分享</span>\n    </a>\n\n    {% if controller.is_login %}\n    <a class=\"mui-tab-item\" href=\"/user/index\">\n        <span class=\"mui-icon iconfont icon-my\"></span>\n        <span class=\"mui-tab-label\">我的</span>\n    </a>\n    {%else%}\n    <a class=\"mui-tab-item\" href=\"/user/login\">\n        <span class=\"mui-icon iconfont icon-myfill\"></span>\n        <span class=\"mui-tab-label\">登录</span>\n    </a>\n    {%endif%}\n\n    <a class=\"mui-tab-item\" href=\"#top\">\n        <span class=\"mui-icon iconfont icon-top\"></span>\n        <span class=\"mui-tab-label\">顶部</span>\n    </a>\n\n</nav>\n<div class=\"mui-content detail\" style=\"background-color: #fff\">\n    <div class=\"mui-content-padded\">\n        <!-- 文章标题 -->\n        <h1 class=\"title\">{{info.title}}</h1>\n        <article class=\"article-content wangEditor-container cmswing-container\" >\n            <div class=\"wangEditor-txt nopadding\">\n                {%for val in info.content%}\n\n                {{val|safe}}\n\n                {%endfor%}\n            </div>\n\n        </article>\n    </div>\n    <div id=\"SOHUCS\" sid=\"{{info.id}}\"></div>\n\n</div>\n<div id=\"mulu\" class=\"mui-modal\">\n    <header class=\"mui-bar mui-bar-nav\">\n        <a class=\"mui-icon mui-icon-close mui-pull-right\" href=\"#mulu\"></a>\n        <h1 class=\"mui-title\">目录</h1>\n    </header>\n    <div class=\"mui-content\" style=\"height: 100%;\">\n        <div class=\"mui-scroll-wrapper\" style=\"top:44px\">\n        <div class=\"mui-scroll\">\n            <table class=\"table table-hover  treetable\" id=\"category\">\n        <tbody>\n        {%for val in ptree%}\n        <tr data-tt-id=\"{{val.id}}\" {%if val.pid != topid %} data-tt-parent-id=\"{{val.pid}}\"{%endif%} {%if val.id == http.get(\"id\")%}class=\"active\" {%endif%}>\n        <td><a href=\"{{val.name|get_url(val.id)}}\" class=\"text-info\"><span class=\'folder ui-draggable\'>{{val.title}}</span></a></td>\n        </tr>\n        {%endfor%}\n\n\n        </tbody>\n    </table>\n        </div>\n    </div>\n</div></div>\n{% endblock%}\n\n{% block script %}\n    <script src=\"/static/admin/js/jquery.min.js\"></script>\n    <script src=\"/static/admin/js/treetable/jquery.treetable.js\" type=\"text/javascript\"></script>\n<script id=\"changyan_mobile_js\" charset=\"utf-8\" type=\"text/javascript\"\n        src=\"//changyan.sohu.com/upload/mobile/wap-js/changyan_mobile.js?client_id={{controller.setup.CY_APPID}}&conf={{controller.setup.CY_APPKEY}}\">\n</script>\n<script type=\"text/javascript\" src=\"//assets.changyan.sohu.com/upload/plugins/plugins.count.js\"></script>\n<script type=\"text/javascript\">\n    mui.init()\n    mui(\'.mui-scroll-wrapper\').scroll();\n    var btn = document.querySelectorAll(\".mui-bar-tab a.mui-tab-item\");\n    $(\"#category\").treetable({ expandable: true });\n    $(\"#category\").treetable(\"expandAll\");\n    for(var i = 0;i<btn.length;i++){\n        btn[i].addEventListener(\"tap\",function () {\n            var href = this.getAttribute(\"href\");\n            if(href==\"#top\"){\n                mui.scrollTo(0,500);\n            }else if(href==\"#shoucang\"){\n                //todo\n                mui.toast(\"功能开发中。。。\")\n                return\n            }else if(href==\"#fenxiang\"){\n                //todo\n                mui.toast(\"功能开发中。。。\")\n                return\n            }else {\n                mui.openWindow({url: href})\n            }\n\n        })\n    }\n\n</script>\n{% endblock %}', 2, 1);
INSERT INTO `cmswing_temp` VALUES (43, 'home', 'detail', 'video', '默认视频模版(手机)', '{% extends \"../inc/base.html\" %}\n{% block content %}\n\n<header id=\"header\" class=\"mui-bar mui-bar-transparent\">\n    <a class=\"mui-action-back mui-icon mui-icon-left-nav mui-pull-left\"></a>\n    <!--<span class=\"mui-icon iconfont icon-cart\"><span class=\"mui-badge\">9</span></span>-->\n    <h1 class=\"mui-title\"><img src=\"/static/webapp/img/logo.png\" ></h1>\n    <span class=\"mui-icon iconfont icon-search mui-pull-right\"></span>\n</header>\n<nav class=\"mui-bar mui-bar-tab\">\n    <a class=\"mui-tab-item mui-action-back\" >\n        <span class=\"mui-icon iconfont icon-back\"></span>\n        <span class=\"mui-tab-label\">返回</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#SOHUCS\">\n        <span class=\"mui-icon iconfont icon-comment\"><span class=\"mui-badge\" id=\"changyan_count_unit\">0</span></span>\n        <span class=\"mui-tab-label\">评论</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#shoucang\">\n        <span class=\"mui-icon iconfont icon-favor\"></span>\n        <span class=\"mui-tab-label\">收藏</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#fenxiang\">\n        <span class=\"mui-icon iconfont icon-share\"></span>\n        <span class=\"mui-tab-label\">分享</span>\n    </a>\n\n    {% if controller.is_login %}\n    <a class=\"mui-tab-item\" href=\"/user/index\">\n        <span class=\"mui-icon iconfont icon-my\"></span>\n        <span class=\"mui-tab-label\">我的</span>\n    </a>\n    {%else%}\n    <a class=\"mui-tab-item\" href=\"/user/login\">\n        <span class=\"mui-icon iconfont icon-myfill\"></span>\n        <span class=\"mui-tab-label\">登录</span>\n    </a>\n    {%endif%}\n\n    <a class=\"mui-tab-item\" href=\"#top\">\n        <span class=\"mui-icon iconfont icon-top\"></span>\n        <span class=\"mui-tab-label\">顶部</span>\n    </a>\n\n</nav>\n<div class=\"mui-content detail\" >\n    <div id=\"video-box\" style=\"width:100%; height: 200px\">\n\n    </div>\n    <ul id=\"list\" class=\"mui-table-view mui-table-view-chevron\">\n        <li class=\"mui-table-view-cell \">\n\n                {{pinfo.title}}\n          <span class=\"mui-badge\">更新至 <em class=\"text-danger\">{{lastlevel.level}}</em> 集</span>\n        </li>\n        <li class=\"mui-table-view-divider\">正在播放:{%if info.level== 0%} {{lastlevel.title}} {%else%} {{info.title}}{%endif%}</li>\n        <li class=\"mui-table-view-cell mui-collapse\">\n            <a class=\"mui-navigate-right\" href=\"#\">\n                播放列表\n            </a>\n            <ul class=\"mui-table-view mui-table-view-chevron\">\n                {%for val in plist%}\n                <li class=\"mui-table-view-cell mui-media mui-media-icon\">\n                    <a class=\"mui-navigate-right\"  href=\"{{val.name|get_url(val.id)}}\">\n                        <div class=\"mui-media-object mui-pull-left\"><span class=\"mui-icon iconfont {%if info.id == val.id%}icon-videofill{%else%}icon-video{%endif%}\"></span>\n                        </div>\n                        <div class=\"mui-media-body\">\n                        {{val.title}}\n                        <span class=\"mui-badge\">{{val.level}}</span>\n                        </div>\n                    </a>\n                </li>\n                {%endfor%}\n            </ul>\n        </li>\n    </ul>\n    <div id=\"SOHUCS\" sid=\"{{info.id}}\"></div>\n\n</div>\n{% endblock%}\n\n{% block script %}\n\n<script id=\"changyan_mobile_js\" charset=\"utf-8\" type=\"text/javascript\"\n        src=\"//changyan.sohu.com/upload/mobile/wap-js/changyan_mobile.js?client_id={{controller.setup.CY_APPID}}&conf={{controller.setup.CY_APPKEY}}\">\n</script>\n<script type=\"text/javascript\" src=\"//assets.changyan.sohu.com/upload/plugins/plugins.count.js\"></script>\n<script type=\"text/javascript\" src=\"http://cytron.cdn.videojj.com/latest/cytron.core.js\"></script>\n<script type=\"text/javascript\">\n    mui.init();\n    var ivaInstance = new Iva(\n            \'video-box\',//父容器id或者父容器DOM对象，给父容器设置宽高640px*480px以上可以获得最佳的浏览体验；\n            {\n                appkey: \'VJa72yR7W\',//必填，请在控制台查看应用标识\n                live: false,//选填，是否是直播流，默认为false\n                video: \'{%if info.video_id != 0%}http://{{controller.setup.QINIU_DOMAIN_NAME}}/{{info.video_id|get_file(\"savename\",false)}}{%else%}{{info.videourl}}{%endif%}\',//必填，播放地址（http://www.tudou.com/programs/view/tM_vZCQy2uM/）或者资源地址（http://7xi4ig.com2.z0.glb.qiniucdn.com/shapuolang_ts.mp4）\n                title: \'{{info.title}}\',//选填，视频标题\n                cover: \'{{info.cover_id|get_pic(\"\")}}\',//选填，视频封面url\n                videoStartPrefixSeconds: 0,//选填，跳过片头，默认为0\n                videoEndPrefixSeconds: 0,//选填，跳过片尾，默认为0\n                /* 以下参数可以在“控制台->项目看板->应用管理->播放器设置”进行全局设置，前端设置可以覆盖全局设置 */\n                skinSelect: 0,//选填，播放器皮肤，可选0、1、2，默认为0，\n                autoplay: true,//选填，是否自动播放，默认为false\n                rightHand: true,//选填，是否开启右键菜单，默认为false\n                autoFormat: false,//选填，是否自动选择最高清晰度，默认为false\n                bubble: true,//选填，是否开启云泡功能，默认为true\n                jumpStep: 10,//选填，左右方向键快退快进的时间\n                tagTrack: false,//选填，云链是否跟踪，默认为false\n                tagShow: false,//选填，云链是否显示，默认为false\n                tagDuration: 5,//选填，云链显示时间，默认为5秒\n                tagFontSize: 16//选填，云链文字大小，默认为16像素\n            }\n    );\n    var btn = document.querySelectorAll(\".mui-bar-tab a.mui-tab-item\");\n    for(var i = 0;i<btn.length;i++){\n        btn[i].addEventListener(\"tap\",function () {\n            var href = this.getAttribute(\"href\");\n            if(href==\"#top\"){\n                mui.scrollTo(0,500);\n            }else if(href==\"#shoucang\"){\n                //todo\n                mui.toast(\"功能开发中。。。\")\n                return\n            }else if(href==\"#fenxiang\"){\n                //todo\n                mui.toast(\"功能开发中。。。\")\n                return\n            }else {\n                mui.openWindow({url: href})\n            }\n\n        })\n    }\n\n</script>\n{% endblock %}', 2, 1);
INSERT INTO `cmswing_temp` VALUES (44, 'home', 'detail', 'download', '默认下载模版(手机)', '{% extends \"../inc/base.html\" %}\n{% block style%}\n<!--App自定义的css-->\n<style type=\"text/css\">\n    .mui-preview-image.mui-fullscreen {\n        position: fixed;\n        z-index: 20;\n        background-color: #000;\n    }\n    .mui-preview-header,\n    .mui-preview-footer {\n        position: absolute;\n        width: 100%;\n        left: 0;\n        z-index: 10;\n    }\n    .mui-preview-header {\n        height: 44px;\n        top: 0;\n    }\n    .mui-preview-footer {\n        height: 50px;\n        bottom: 0px;\n    }\n    .mui-preview-header .mui-preview-indicator {\n        display: block;\n        line-height: 25px;\n        color: #fff;\n        text-align: center;\n        margin: 15px auto 4;\n        width: 70px;\n        background-color: rgba(0, 0, 0, 0.4);\n        border-radius: 12px;\n        font-size: 16px;\n    }\n    .mui-preview-image {\n        display: none;\n        -webkit-animation-duration: 0.5s;\n        animation-duration: 0.5s;\n        -webkit-animation-fill-mode: both;\n        animation-fill-mode: both;\n    }\n    .mui-preview-image.mui-preview-in {\n        -webkit-animation-name: fadeIn;\n        animation-name: fadeIn;\n    }\n    .mui-preview-image.mui-preview-out {\n        background: none;\n        -webkit-animation-name: fadeOut;\n        animation-name: fadeOut;\n    }\n    .mui-preview-image.mui-preview-out .mui-preview-header,\n    .mui-preview-image.mui-preview-out .mui-preview-footer {\n        display: none;\n    }\n    .mui-zoom-scroller {\n        position: absolute;\n        display: -webkit-box;\n        display: -webkit-flex;\n        display: flex;\n        -webkit-box-align: center;\n        -webkit-align-items: center;\n        align-items: center;\n        -webkit-box-pack: center;\n        -webkit-justify-content: center;\n        justify-content: center;\n        left: 0;\n        right: 0;\n        bottom: 0;\n        top: 0;\n        width: 100%;\n        height: 100%;\n        margin: 0;\n        -webkit-backface-visibility: hidden;\n    }\n    .mui-zoom {\n        -webkit-transform-style: preserve-3d;\n        transform-style: preserve-3d;\n    }\n    .mui-slider .mui-slider-group .mui-slider-item img {\n        width: auto;\n        height: auto;\n        max-width: 100%;\n        max-height: 100%;\n    }\n    .mui-android-4-1 .mui-slider .mui-slider-group .mui-slider-item img {\n        width: 100%;\n    }\n    .mui-android-4-1 .mui-slider.mui-preview-image .mui-slider-group .mui-slider-item {\n        display: inline-table;\n    }\n    .mui-android-4-1 .mui-slider.mui-preview-image .mui-zoom-scroller img {\n        display: table-cell;\n        vertical-align: middle;\n    }\n    .mui-preview-loading {\n        position: absolute;\n        width: 100%;\n        height: 100%;\n        top: 0;\n        left: 0;\n        display: none;\n    }\n    .mui-preview-loading.mui-active {\n        display: block;\n    }\n    .mui-preview-loading .mui-spinner-white {\n        position: absolute;\n        top: 50%;\n        left: 50%;\n        margin-left: -25px;\n        margin-top: -25px;\n        height: 50px;\n        width: 50px;\n    }\n    .mui-preview-image img.mui-transitioning {\n        -webkit-transition: -webkit-transform 0.5s ease, opacity 0.5s ease;\n        transition: transform 0.5s ease, opacity 0.5s ease;\n    }\n    @-webkit-keyframes fadeIn {\n        0% {\n            opacity: 0;\n        }\n        100% {\n            opacity: 1;\n        }\n    }\n    @keyframes fadeIn {\n        0% {\n            opacity: 0;\n        }\n        100% {\n            opacity: 1;\n        }\n    }\n    @-webkit-keyframes fadeOut {\n        0% {\n            opacity: 1;\n        }\n        100% {\n            opacity: 0;\n        }\n    }\n    @keyframes fadeOut {\n        0% {\n            opacity: 1;\n        }\n        100% {\n            opacity: 0;\n        }\n    }\n    p img {\n        max-width: 100%;\n        height: auto;\n    }\n</style>\n{% endblock%}\n{% block content %}\n\n<header class=\"mui-bar mui-bar-nav\">\n    <a class=\"mui-action-back mui-icon mui-icon-left-nav mui-pull-left\"></a>\n    <!--<span class=\"mui-icon iconfont icon-cart\"><span class=\"mui-badge\">9</span></span>-->\n    <h1 class=\"mui-title\">{{info.title}}</h1>\n    <span class=\"mui-icon iconfont icon-search mui-pull-right\"></span>\n</header>\n<nav class=\"mui-bar mui-bar-tab\">\n    <a class=\"mui-tab-item mui-action-back\" >\n        <span class=\"mui-icon iconfont icon-back\"></span>\n        <span class=\"mui-tab-label\">返回</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#SOHUCS\">\n        <span class=\"mui-icon iconfont icon-comment\"><span class=\"mui-badge\" id=\"changyan_count_unit\">0</span></span>\n        <span class=\"mui-tab-label\">评论</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#shoucang\">\n        <span class=\"mui-icon iconfont icon-favor\"></span>\n        <span class=\"mui-tab-label\">收藏</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#fenxiang\">\n        <span class=\"mui-icon iconfont icon-share\"></span>\n        <span class=\"mui-tab-label\">分享</span>\n    </a>\n\n    {% if controller.is_login %}\n    <a class=\"mui-tab-item\" href=\"/user/index\">\n        <span class=\"mui-icon iconfont icon-my\"></span>\n        <span class=\"mui-tab-label\">我的</span>\n    </a>\n    {%else%}\n    <a class=\"mui-tab-item\" href=\"/user/login\">\n        <span class=\"mui-icon iconfont icon-myfill\"></span>\n        <span class=\"mui-tab-label\">登录</span>\n    </a>\n    {%endif%}\n\n    <a class=\"mui-tab-item\" href=\"#top\">\n        <span class=\"mui-icon iconfont icon-top\"></span>\n        <span class=\"mui-tab-label\">顶部</span>\n    </a>\n\n</nav>\n<div class=\"mui-content detail\" >\n    <ul class=\"mui-table-view\">\n        <li class=\"mui-table-view-cell mui-media\">\n                <img class=\"mui-media-object mui-pull-left\" src=\"{{info.cover_id|get_pic(\'m=0,w=120\')}}\" style=\"width: 120px;height:120px;max-width: 120px\">\n                <div class=\"mui-media-body\">\n                    {{info.title}}\n                    <p class=\"mui-ellipsis-2\">{% if info.description !=null %}\n                        <!-- short description -->\n                        {{info.description}}\n                    <!-- /short description -->\n                    {% endif %}</p>\n                    <a href=\"#dlist\" type=\"button\" class=\"mui-btn mui-btn-primary\" style=\"position:absolute;bottom: 10px;\"><span class=\"mui-icon mui-icon-download\"></span>下载 <span class=\"mui-badge mui-badge-primary\">{{info.download}}</span></a>\n                </div>\n        </li>\n\n    </ul>\n    <div class=\"mui-content-padded\">\n        <div class=\"article-header\">\n            <div class=\"pgc-bar-top mui-clearfix\" >\n                <a href=\"#\" ga_category=\"toutiaohao\" ga_label=\"\" ga_event=\"click\" class=\"avatar-link vwo-media-profile\">\n                    <div class=\"avatar\">\n                        <img src=\"/uc/index/avatar/uid/{{info.uid}}\" class=\"avatar\">\n                    </div>\n                </a>\n                <div class=\"subtitle\">\n                    <p class=\"name\"><a href=\"#\" class=\"screen-name vwo-media-profile\">\n                        <span id=\"mediaName\" style=\"transform-origin: 0px 0px 0px; opacity: 1; transform: scale(1, 1);\">{{info.uid|get_nickname}}</span>\n                        <img src=\"/static/webapp/img/logo.png\">\n                    </a></p>\n                    <a href=\"#\" class=\"time vwo-media-profile\" >\n\n                        <span class=\"original original-fix\">原创</span>\n\n                        <span>{{ info.update_time|dateformat(\'Y-m-d H:i\') }}</span>\n                    </a>\n                </div>\n\n            </div>\n\n            <div class=\"favor-wrapper\">\n                <button type=\"button\" class=\"mui-btn mui-btn-primary mui-btn-outlined\">关注</button>\n            </div>\n        </div>\n    </div>\n    <div style=\"padding: 10px 10px;\">\n        <div id=\"segmentedControl\" class=\"mui-segmented-control\">\n            <a class=\"mui-control-item mui-active\" href=\"#item1\">\n                详情\n            </a>\n            <a class=\"mui-control-item\" href=\"#item2\">\n                评论\n            </a>\n            <a class=\"mui-control-item\" href=\"#item3\">\n               相关\n            </a>\n        </div>\n    </div>\n    <div id=\"item1\" class=\"mui-control-content mui-active\">\n        <div class=\"mui-content\">\n\n            {% if info.fileimgs %}\n            <div class=\"mui-slider\">\n                {%set pics = info.fileimgs|strToArray%}\n                {%set pn = pics|length%}\n                <div class=\"mui-slider-group mui-slider-loop\">\n                    <!-- 额外增加的一个节点(循环轮播：第一个节点是最后一个图文表格) -->\n                    <div class=\"mui-slider-item mui-slider-item-duplicate\">\n                        <ul class=\"mui-table-view mui-grid-view\">\n                            <li class=\"mui-table-view-cell mui-media mui-col-xs-6\"><a href=\"#\"><img class=\"mui-media-object\" src=\"{{pics[0]|get_pic(\'m=1,w=336,h=336\')}}\">\n                            </a></li>\n                            <li class=\"mui-table-view-cell mui-media mui-col-xs-6\"><a href=\"#\"><img class=\"mui-media-object\" src=\"{{pics[1]|get_pic(\'m=1,w=336,h=336\')}}\">\n                                </a></li>\n                        </ul>\n                    </div>\n                    {% for item in pics | batch(2) %}\n                    <div class=\"mui-slider-item\">\n                        <ul class=\"mui-table-view mui-grid-view\">\n                            {%for v in item%}\n                            <li class=\"mui-table-view-cell mui-media mui-col-xs-6\"><a href=\"#\"><img class=\"mui-media-object\" src=\"{{v|get_pic(\'m=1,w=336,h=336\')}}\" data-preview-src=\"\" data-preview-group=\"1\"></a></li>\n                                {%endfor%}\n                        </ul>\n                    </div>\n                    {% endfor %}\n\n                    <!-- 额外增加的一个节点(循环轮播：最后一个节点是第一个图文表格) -->\n                    <div class=\"mui-slider-item mui-slider-item-duplicate\">\n                        <ul class=\"mui-table-view mui-grid-view\">\n                            <li class=\"mui-table-view-cell mui-media mui-col-xs-6\"><a href=\"#\"><img class=\"mui-media-object\" src=\"{{pics[pn-2]|get_pic(\'m=1,w=336,h=336\')}}\">\n                                </a></li>\n                            <li class=\"mui-table-view-cell mui-media mui-col-xs-6\"><a href=\"#\"><img class=\"mui-media-object\" src=\"{{pics[pn-1]|get_pic(\'m=1,w=336,h=336\')}}\">\n                                </a></li>\n                        </ul>\n                    </div>\n                </div>\n\n\n            </div>\n            {%endif%}\n            <div class=\"mui-table-view-divider\">详情</div>\n\n            <article class=\"article-content\" style=\"background-color: #fff;padding: 10px\">\n                {%for val in info.content%}\n\n                {{val|safe}}\n\n                {%endfor%}\n            </article>\n        </div>\n\n    </div>\n    <div id=\"item2\" class=\"mui-control-content\">\n        <div id=\"SOHUCS\" sid=\"{{info.id}}\"></div>\n    </div>\n    <div id=\"item3\" class=\"mui-control-content\">\n        <ul class=\"mui-table-view\">\n            <li class=\"mui-table-view-cell\">\n                第三个选项卡子项-1\n            </li>\n            <li class=\"mui-table-view-cell\">\n                第三个选项卡子项-2\n            </li>\n            <li class=\"mui-table-view-cell\">\n                第三个选项卡子项-3\n            </li>\n        </ul>\n    </div>\n</div>\n<div id=\"dlist\" class=\"mui-popover mui-popover-action mui-popover-bottom\">\n    <ul class=\"mui-table-view\">\n        {%if info.file_id >0%}\n        <li class=\"mui-table-view-cell\">\n            <a href=\"#\" data-dlink=\"/dlink/{{info.id}}||1\">极速下载</a>\n        </li>\n        {%endif%}\n        {%if info.panurl%}\n        {%set panurl = info.panurl|strToArray(\"\\r\\n\")%}\n        {%for url in panurl%}\n        {%set pu = url|strToArray(\"|\")%}\n        <li class=\"mui-table-view-cell\">\n\n            {%if pu[2]%}\n            <a href=\"#\" data-dlink=\"/dlink/{{info.id}}||3||{{pu[1]|trim|urlencode}}\">{{pu[0]}}</a>\n            {%else%}\n            <a href=\"#\" data-dlink=\"/dlink/{{info.id}}||2||{{pu[1]|trim|urlencode}}\">{{pu[0]}}</a>\n            {%endif%}\n        </li>\n        {%endfor%}\n        {%endif%}\n\n    </ul>\n    <ul class=\"mui-table-view\">\n        <li class=\"mui-table-view-cell\">\n            <a href=\"#dlist\"><b>取消</b></a>\n        </li>\n    </ul>\n</div>\n{% endblock%}\n\n{% block script %}\n<script src=\"/static/webapp/js/mui.zoom.js\"></script>\n<script src=\"/static/webapp/js/mui.previewimage.js\"></script>\n<script type=\"text/javascript\">\n    mui.previewImage();\n    mui(\'body\').on(\'tap\', \'.mui-popover-action li>a\', function() {\n        var a = this,\n                parent;\n        //根据点击按钮，反推当前是哪个actionsheet\n        for (parent = a.parentNode; parent != document.body; parent = parent.parentNode) {\n            if (parent.classList.contains(\'mui-popover-action\')) {\n                break;\n            }\n        }\n        //关闭actionsheet\n        var link = $(a).attr(\"data-dlink\");\n        mui(\'#\' + parent.id).popover(\'toggle\');\n        //mui.alert(\"你刚点击了\\\"\" + link + \"\\\"按钮\");\n        //打开关于页面\n        mui.openWindow({\n            url: link,\n            id:\'dlink\'\n        });\n    })\n    var btn = document.querySelectorAll(\".mui-bar-tab a.mui-tab-item\");\n    for(var i = 0;i<btn.length;i++){\n        btn[i].addEventListener(\"tap\",function () {\n            var href = this.getAttribute(\"href\");\n            if(href==\"#top\"){\n                mui.scrollTo(0,500);\n            }else if(href==\"#shoucang\"){\n                //todo\n                mui.toast(\"功能开发中。。。\")\n                return\n            }else if(href==\"#fenxiang\"){\n                //todo\n                mui.toast(\"功能开发中。。。\")\n                return\n            }else {\n                mui.openWindow({url: href})\n            }\n\n        })\n    }\n\n</script>\n<script id=\"changyan_mobile_js\" charset=\"utf-8\" type=\"text/javascript\" src=\"//changyan.sohu.com/upload/mobile/wap-js/changyan_mobile.js?client_id={{controller.setup.CY_APPID}}&conf={{controller.setup.CY_APPKEY}}\"></script>\n<script type=\"text/javascript\" src=\"//assets.changyan.sohu.com/upload/plugins/plugins.count.js\"></script>\n{% endblock %}', 2, 1);
