INSERT INTO `cmswing_temp` VALUES (47, 'home', 'sp', 'index', '默认单页面模版(移动)', '{% extends \"./inc_base.html\" %}\n{% block content %}\n<header class=\"mui-bar mui-bar-nav\">\n    <!--<a class=\"mui-action-back mui-icon mui-icon-left-nav mui-pull-left\"></a>-->\n    <a href=\"/center/cart/index\" class=\"mui-icon iconfont icon-cart\"><span class=\"mui-badge\">{% if controller.cart.num %}{{controller.cart.num}}{%else%}0{%endif%}</span></a>\n    <h1 class=\"mui-title\"><img src=\"/static/webapp/img/logo.png\" ></h1>\n    <a class=\"mui-icon iconfont icon-search mui-pull-right\"></a>\n</header>\n{% include \"./inc_nav.html\" %}\n{% include \"./inc_sliderbar.html\" %}\n<!--下拉刷新容器-->\n<div class=\"mui-content mui-scroll-wrapper list-content bg-white\">\n    <div class=\"mui-scroll\">\n        <!--数据列表-->\n        <div id=\"contentcmswing\" class=\"mui-content-padded \">{%if category.sp_hatml_m%}{{category.sp_html_m|safe}}{%else%}{{category.sp_html_pc|safe}}{%endif%}</div>\n    </div>\n</div>\n{% endblock%}\n\n{% block script %}\n<script>\n    mui.init();\n\n    //alert(mui.os.plus)\n    mui(\'.mui-scroll-wrapper\').scroll({\n        deceleration: 0.0005 //flick 减速系数，系数越大，滚动速度越慢，滚动距离越小，默认值0.0006\n    });\n\n    mui.ready(function() {\n        var btn = document.querySelectorAll(\".mui-bar-tab a.mui-tab-item\");\n        for(var i = 0;i<btn.length;i++){\n            btn[i].addEventListener(\"tap\",function () {\n                var href = this.getAttribute(\"href\");\n                if(href==\"#wait\"){\n                    //todo\n                    mui.toast(\"功能开发中。。。\")\n                    return\n                }\n                mui.openWindow({url: href})\n\n            })\n        }\n    });\n</script>\n{% endblock %}', 2, 1);
INSERT INTO `cmswing_temp` VALUES (48, 'home', 'inc', 'sort', '分类信息筛选', '<!--分类信息-->\n{%if sort%}\n<div class=\"panel panel-default tabs\">\n    <div class=\"panel-heading \">\n        <ul class=\"nav nav-tabs\">\n            {%for val in sort.types%}\n            <li class=\"{%if sortid == val.enable%}active{%endif%}\"><a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{val.enable}}\" >{{val.name}}</a></li>\n            {%endfor%}\n\n        </ul>\n    </div>\n\n    <div class=\"panel-body nopadding\">\n        <table class=\"table table-hover  m-b-none \" style=\"margin-top: -1px\">\n            <tbody>\n            {% set typenum = 1%}\n            {%for val in typevar%}\n            {%if val.search > 1 and val.available==1%}\n            <tr {% if typenum > 3 %} class=\"type_tr_m\"  style=\"display:none\"{%endif%} >\n                <td style=\"width: 120px\" class=\"text-right\">\n                    {{val.option.title}}:\n                </td>\n                <td>\n                    {%if 0 == nsobj[val.option.identifier] or null == nsobj[val.option.identifier]%}\n                    <span class=\"btn btn-danger btn-xs relative\">全部</span>\n                    {%else%}\n                    <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{0|sort_url(val.option.identifier,typevar,nsobj)}}\" class=\"btn btn-clean btn-xs relative\" >全部</a>\n                    {%endif%}\n                    {%for v in val.rules %}\n                    {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                    <span class=\"btn btn-danger btn-xs relative\" >{{v.name}}{{val.option.unit}}</span>\n                    {%else%}\n                    <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v.id|sort_url(val.option.identifier,typevar,nsobj)}}\" class=\"btn btn-clean btn-xs relative\" >{{v.name}}{{val.option.unit}}</a>\n                    {%endif%}\n                    {%endfor%}\n                    <!--子栏目-->\n                    {%for v in val.rules %}\n                    {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                    {% if v.children%}\n                    <div class=\"subtsm\">\n\n                        {% for v_ in v.children %}\n                        {%if v_.id|sort_act(nsobj[val.option.identifier])%}\n                        <span class=\"btn btn-danger btn-xs relative\" >{{v_.name}}{{val.option.unit}}</span>\n                        {%else%}\n                        <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v_.id|sort_url(val.option.identifier,typevar,nsobj)}}\" class=\"btn btn-clean btn-xs relative\">{{v_.name}}{{val.option.unit}}</a>\n                        {%endif%}\n                        {% endfor %}\n                    </div>\n                    {%endif%}\n                    {%endif%}\n                    {%endfor%}\n                    <!--子栏目-->\n                    {%for v in val.rules %}\n                    {%if v.id|sort_act(nsobj[val.option.identifier])%}\n                    {% if v.children%}\n\n                    {% for v_ in v.children %}\n                    {%if v_.id|sort_act(nsobj[val.option.identifier])%}\n                    {% if v_.children%}\n                    <div class=\"subtsm\">\n                        {% for v__ in v_.children %}\n\n                        {%if v__.id|sort_act(nsobj[val.option.identifier])%}\n\n                        <span class=\"btn btn-danger btn-xs relative\" >{{v__.name}}{{val.option.unit}}</span>\n                        {%else%}\n                        <a href=\"{{cate.url}}-{{order}}-{{group_id}}-{{sortid}}-{{v__.id|sort_url(val.option.identifier,typevar,nsobj)}}\" class=\"btn btn-clean btn-xs relative\" >{{v__.name}}{{val.option.unit}}</a>\n\n                        {%endif%}\n                        {%endfor%}\n                    </div>\n                    {%endif%}\n                    {%endif%}\n                    {% endfor %}\n\n                    {%endif%}\n                    {%endif%}\n                    {%endfor%}\n                </td>\n\n            </tr>\n            {% set typenum = typenum+1%}\n            {%endif%}\n            {%endfor%}\n\n            </tbody>\n        </table>\n        <div class=\"tab-content \" style=\"padding: 0; margin-top: -1px\">\n\n            {% if typenum > 4%}\n            <a href=\"javascript:;\" class=\"center-block btn btn-xs btn-white type_tr_b1\" style=\"border-top: 1px solid #cfd8e1; display: block\"><span class=\"fa fa-chevron-down\"></span></a>\n            <a href=\"javascript:;\" class=\"center-block btn btn-xs btn-white type_tr_b2\" style=\"border-top: 1px solid #cfd8e1; display: none\"><span class=\"fa fa-chevron-up\"></span></a>\n            {%endif%}\n        </div>\n    </div>\n</div>\n{%endif%}\n<!--/分类信息-->', 1, 1);
INSERT INTO `cmswing_temp` VALUES (49, 'home', 'inc', 'right', '公共右侧', '<div class=\"col-md-3 col-sm-3\">\n\n                <!-- INLINE SEARCH -->\n                <div class=\"inline-search clearfix margin-bottom-20\">\n                    <form action=\"/search\" method=\"get\" class=\"widget_search\" target=\"_blank\">\n                        <input type=\"search\" placeholder=\"请输入关键词...\" id=\"q\" name=\"q\" class=\"serch-input\">\n                        <button type=\"submit\">\n                            <i class=\"fa fa-search\"></i>\n                        </button>\n                    </form>\n                </div>\n                <!-- /INLINE SEARCH -->\n                <!-- side navigation -->\n                {%column data=\"cates\",cid=category.id,isnum=\"0\"%}\n                {%if cates==null%}\n                <div class=\"side-nav margin-bottom-20 margin-top-20\">\n                    <div class=\"side-nav-head\">\n                        <button class=\"fa fa-bars\"></button>\n                        <h4>{{category.title}} 子分类</h4>\n                    </div>\n                    <ul class=\"list-group list-group-bordered list-group-noicon uppercase\">\n                        {%for val in cates%}\n                        <li class=\"list-group-item\"><a href=\"{{val.url}}\"><span class=\"size-11 text-muted pull-right\">({{val.doc_num}})</span> {{val.name}}</a></li>\n                        {%endfor%}\n                    </ul>\n                    <!-- /side navigation -->\n                </div>\n                {%endif%}\n\n                <div class=\"panel panel-default tabs\">\n                    <div class=\"panel-heading\">\n                        <!-- tabs -->\n                        <ul class=\"nav nav-tabs nav-justified\">\n                            <li class=\"active\">\n                                <a href=\"#tab_1\" data-toggle=\"tab\">\n                                    最热\n                                </a>\n                            </li>\n                            <li>\n                                <a href=\"#tab_2\" data-toggle=\"tab\">\n                                    最新\n                                </a>\n                            </li>\n                        </ul>\n                    </div>\n                    <div class=\"panel-body tab-content\">\n                        <!-- POPULAR -->\n                        <div id=\"tab_1\" class=\"tab-pane active\">\n                            <!-- /post -->\n                            {% topic data = \"hotlist\",limit= \"5\",cid=category.id,type=\"hot\"%}\n                            {%for v in hotlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n\n                        </div>\n                        <!-- /POPULAR -->\n\n\n                        <!-- RECENT -->\n                        <div id=\"tab_2\" class=\"tab-pane\">\n                            {% topic data = \"newlist\",limit= \"5\",cid=category.id%}\n                            {%for v in newlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n                            <!-- /post -->\n                        </div>\n                        <!-- /RECENT -->\n                    </div>\n                </div>\n                <!-- TAGS -->\n                <div class=\"panel panel-default\">\n                    <div class=\"panel-heading\">\n                        <a class=\"btn btn-danger btn-xs pull-right\">更多</a>\n                        <h2 class=\"panel-title\">话题</h2>\n                    </div>\n                    <div class=\"panel-body\">\n                        {% keywords data =\"kws\",type=\"hot\"%}\n                        {%for key in kws%}\n                        <a class=\"tag\" href=\"{{key.url}}\">\n                            <span class=\"txt\">{{key.keyname}}</span>\n                            <span class=\"num\">{{key.videonum}}</span>\n                        </a>\n                        {%endfor%}\n                    </div>\n                    <div class=\"panel-footer panel-footer-transparent clearfix\">\n                        <a href=\"#\" class=\"social-icon social-icon-border social-facebook pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n                            <i class=\"icon-facebook\"></i>\n                            <i class=\"icon-facebook\"></i>\n                        </a>\n\n                        <a href=\"#\" class=\"social-icon social-icon-border social-twitter pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n                            <i class=\"icon-twitter\"></i>\n                            <i class=\"icon-twitter\"></i>\n                        </a>\n\n                        <a href=\"#\" class=\"social-icon social-icon-border social-gplus pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n                            <i class=\"icon-gplus\"></i>\n                            <i class=\"icon-gplus\"></i>\n                        </a>\n\n                        <a href=\"#\" class=\"social-icon social-icon-border social-linkedin pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n                            <i class=\"icon-linkedin\"></i>\n                            <i class=\"icon-linkedin\"></i>\n                        </a>\n\n                        <a href=\"#\" class=\"social-icon social-icon-border social-rss pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Rss\">\n                            <i class=\"icon-rss\"></i>\n                            <i class=\"icon-rss\"></i>\n                        </a>\n                    </div>\n                </div>\n\n            </div>', 1, 1);
INSERT INTO `cmswing_temp` VALUES (50, 'home', 'detail', 'docs', '文档默认模版', '{% extends \"./inc_base.html\" %}\n{% block style %}\n{%if category.pid !=0%}\n{%column data=\"column\",pid=category.pid%}\n{%else%}\n{%column data=\"column\",cid=category.id%}\n{%endif%}\n<script src=\"/static/admin/js/udeitor/ueditor.parse.min.js\"></script>\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/share/css/share.min.css\">\n<style>\n    .active > .text{\n        display: block !important;\n    }\n</style>\n{% endblock %}\n{% block content %}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header page-header-xs\">\n    <div class=\"container\">\n        {%if column|length>0%}\n        <h1>{{category.title}}</h1>\n        {%endif%}\n        <!-- breadcrumbs -->\n        <ol class=\"breadcrumb {%if column|length==0%} breadcrumb-inverse {%endif%}\">\n            <li><a href=\"/\">首页</a></li>\n            {%for val in breadcrumb %}\n            <li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n            {%if column|length==0%} <li>{{info.title}} </li>{%endif%}\n            {% endfor %}\n        </ol><!-- /breadcrumbs -->\n\n        <!-- page tabs -->\n        {%if column|length >0%}\n        <ul class=\"page-header-tabs list-inline\">\n            <li {%if breadcrumb[0].id == category.id %}class=\"active\"{%endif%}><a href=\"{{breadcrumb[0].url}}\">全部</a></li>\n            {%for val in column%}\n            <li  {%if val.id == category.id %}class=\"active\"{%endif%}><a href=\"{{val.url}}\">{{val.name}}</a></li>\n            {%endfor%}\n        </ul>\n        {%endif%}<!-- /page tabs -->\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n\n        <div class=\"row\">\n\n            <!-- LEFT -->\n            <!-- LEFT -->\n            <div class=\"col-md-3 col-sm-3\">\n\n                <!-- INLINE SEARCH -->\n                <div class=\"inline-search clearfix margin-bottom-20\">\n                    <form action=\"/search\" method=\"get\" class=\"widget_search\" target=\"_blank\">\n                        <input type=\"search\" placeholder=\"请输入关键词...\" id=\"q\" name=\"q\" class=\"serch-input\">\n                        <button type=\"submit\">\n                            <i class=\"fa fa-search\"></i>\n                        </button>\n                    </form>\n                </div>\n                <!-- /INLINE SEARCH -->\n                <div class=\"panel panel-info\">\n                    <div class=\"panel-heading\">\n                        <h2 class=\"panel-title\"><a href=\"{{pinfo.name|get_url(pinfo.id)}}\">{{pinfo.title}}</a></h2>\n                    </div>\n                    <div class=\"panel-body nopadding\">\n                        <ul class=\"list-group list-group-bordered list-group-noicon uppercase\">\n                            <table class=\"table table-hover  treetable\" id=\"category\">\n                                <tbody>\n                                {%for val in ptree%}\n                                <tr data-tt-id=\"{{val.id}}\" {%if val.pid != topid %} data-tt-parent-id=\"{{val.pid}}\"{%endif%} {%if val.id == controller.get(\"id\")%}class=\"active\" {%endif%}>\n                                <td><a href=\"{{val.name|get_url(val.id)}}\" class=\"text-info\"><span class=\'folder ui-draggable\'>{{val.title}}</span></a></td>\n                                </tr>\n                                {%endfor%}\n\n\n                                </tbody>\n                            </table>\n                        </ul>\n                    </div>\n                </div>\n            </div>\n\n            <div class=\"col-md-9 col-sm-9\">\n\n                <div class=\"panel panel-default\">\n                    <div class=\"panel-heading\">\n                        <a class=\"btn btn-default btn-xs pull-right\">CmsWing</a>\n                        <h2 class=\"panel-title\">{{info.title}}</h2>\n                    </div>\n                    <div class=\"panel-body nopadding\">\n\n                        <!--页面内容钩子加载-->\n                        {% if pagehook%}\n                        {%for field in pagehook.fields%}\n                        {%if pagehook.editor == \'1\'%}\n                        {{HOOKS@pageContent@1[field.name]|safe}}\n                        {%elif pagehook.editor == \'2_1\'%}\n                        {{HOOKS@pageContent@2_1[field.name]|safe}}\n                        {%elif pagehook.editor == \'2_2\'%}\n                        {{HOOKS@pageContent@2_2[field.name]|safe}}\n                        {%elif pagehook.editor == \'3\'%}\n                        {{HOOKS@pageContent@3[field.name]|safe}}\n                        {%else%}\n                        {{HOOKS@pageContent[field.name]|safe}}\n                        {%endif%}\n                        {%endfor%}\n                        {%endif%}\n                        <!--/页面内容钩子加载结束-->\n\n                    </div>\n                    {% rkeywords data =\"tags\",type=\"0\",mod_id=info.model_id,id=info.id%}\n                    {%if tags%}\n                    <div class=\"panel-footer panel-footer-transparent\">\n                        <!-- TAGS -->\n\n                        {%set color = [\"btn-primary\",\"btn-success\",\"btn-info\",\"btn-warning\",\"btn-danger\"]%}\n                        {%for k in tags%}\n                        <a href=\"{{k.url}}\" class=\"btn {{color|random}} btn-xs relative\">\n                            {{k.keyname}}\n                        </a>\n                        {%endfor%}\n                        <!-- /TAGS -->\n                    </div>\n                    {%endif%}\n                    <div class=\"panel-footer panel-footer-transparent\">\n                    <div class=\"social-share\" data-disabled=\"google,twitter,facebook,diandian\" ></div>\n                    </div>\n                    <div class=\"panel-footer\">\n                        <ul class=\"pager nomargin\">\n                            {%if previous.id%}\n                            <li class=\"previous\"><a class=\"noborder\" href=\"{{previous.name|get_url(previous.id)}}\" title=\"{{previous.title}}\">&larr; 上一篇:{{previous.title|block(20)}}</a></li>\n                            {%endif%}\n                            {%if next.id%}\n                            <li class=\"next\"><a class=\"noborder\" href=\"{{next.name|get_url(next.id)}}\" title=\"{{next.title}}\">下一篇:{{next.title|block(20)}} &rarr;</a></li>\n                            {%endif%}\n                        </ul>\n                    </div>\n                    {{HOOK@documentDetailAfter|safe}}\n                </div>\n\n\n            </div>\n        </div>\n\n\n    </div>\n</section>\n<!-- / -->\n{% endblock %}\n\n{% block script %}\n<script src=\"/static/assets/plugins/share/js/social-share.min.js\"></script>\n{% endblock %}\n', 1, 1);
INSERT INTO `cmswing_temp` VALUES (51, 'home', 'detail', 'docs', '文档默认模版', '{% extends \"./inc_base.html\" %}\n{% block style%}\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/wangEditor/css/wangEditor.min.css\" type=\"text/css\"/>\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.css\">\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.theme.default.css\">\n{%endblock%}\n{% block content %}\n\n<header class=\"mui-bar mui-bar-nav\">\n    <a class=\"mui-action-back mui-icon mui-icon-left-nav mui-pull-left\"></a>\n    <!--<span class=\"mui-icon iconfont icon-cart\"><span class=\"mui-badge\">9</span></span>-->\n    <h1 class=\"mui-title\">{{pinfo.title}}</h1>\n    <span class=\"mui-icon iconfont icon-search mui-pull-right\"></span>\n</header>\n<nav class=\"mui-bar mui-bar-tab\">\n    <a class=\"mui-tab-item \" href=\"{{pinif.name|get_url(pinfo.id)}}\">\n        <span class=\"mui-icon iconfont icon-form\"></span>\n        <span class=\"mui-tab-label\">封面</span>\n    </a>\n    <a class=\"mui-tab-item \"  href=\"#mulu\">\n        <span class=\"mui-icon iconfont icon-sortlight\"></span>\n        <span class=\"mui-tab-label\">目录</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#SOHUCS\">\n        <span class=\"mui-icon iconfont icon-comment\"><span class=\"mui-badge\" id=\"changyan_count_unit\">0</span></span>\n        <span class=\"mui-tab-label\">评论</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#shoucang\">\n        <span class=\"mui-icon iconfont icon-favor\"></span>\n        <span class=\"mui-tab-label\">收藏</span>\n    </a>\n    <a class=\"mui-tab-item\" href=\"#fenxiang\">\n        <span class=\"mui-icon iconfont icon-share\"></span>\n        <span class=\"mui-tab-label\">分享</span>\n    </a>\n\n    {% if controller.is_login %}\n    <a class=\"mui-tab-item\" href=\"/user/index\">\n        <span class=\"mui-icon iconfont icon-my\"></span>\n        <span class=\"mui-tab-label\">我的</span>\n    </a>\n    {%else%}\n    <a class=\"mui-tab-item\" href=\"/user/login\">\n        <span class=\"mui-icon iconfont icon-myfill\"></span>\n        <span class=\"mui-tab-label\">登录</span>\n    </a>\n    {%endif%}\n\n    <a class=\"mui-tab-item\" href=\"#top\">\n        <span class=\"mui-icon iconfont icon-top\"></span>\n        <span class=\"mui-tab-label\">顶部</span>\n    </a>\n\n</nav>\n<div class=\"mui-content detail\" style=\"background-color: #fff\">\n    <div class=\"mui-content-padded\">\n        <!-- 文章标题 -->\n        <h1 class=\"title\">{{info.title}}</h1>\n        <article class=\"article-content wangEditor-container cmswing-container\" >\n            <div class=\"wangEditor-txt nopadding\">\n                {%for val in info.content%}\n\n                {{val|safe}}\n\n                {%endfor%}\n            </div>\n\n        </article>\n    </div>\n    {{HOOK@documentDetailAfter|safe}}\n\n</div>\n<div id=\"mulu\" class=\"mui-modal\">\n    <header class=\"mui-bar mui-bar-nav\">\n        <a class=\"mui-icon mui-icon-close mui-pull-right\" href=\"#mulu\"></a>\n        <h1 class=\"mui-title\">目录</h1>\n    </header>\n    <div class=\"mui-content\" style=\"height: 100%;\">\n        <div class=\"mui-scroll-wrapper\" style=\"top:44px\">\n        <div class=\"mui-scroll\">\n            <table class=\"table table-hover  treetable\" id=\"category\">\n        <tbody>\n        {%for val in ptree%}\n        <tr data-tt-id=\"{{val.id}}\" {%if val.pid != topid %} data-tt-parent-id=\"{{val.pid}}\"{%endif%} {%if val.id == controller.get(\"id\")%}class=\"active\" {%endif%}>\n        <td><a href=\"{{val.name|get_url(val.id)}}\" class=\"text-info\"><span class=\'folder ui-draggable\'>{{val.title}}</span></a></td>\n        </tr>\n        {%endfor%}\n\n\n        </tbody>\n    </table>\n        </div>\n    </div>\n</div></div>\n{% endblock%}\n\n{% block script %}\n    <script src=\"/static/admin/js/jquery.min.js\"></script>\n    <script src=\"/static/admin/js/treetable/jquery.treetable.js\" type=\"text/javascript\"></script>\n\n<script type=\"text/javascript\">\n    mui.init()\n    mui(\'.mui-scroll-wrapper\').scroll();\n    var btn = document.querySelectorAll(\".mui-bar-tab a.mui-tab-item\");\n    $(\"#category\").treetable({ expandable: true });\n    $(\"#category\").treetable(\"expandAll\");\n    for(var i = 0;i<btn.length;i++){\n        btn[i].addEventListener(\"tap\",function () {\n            var href = this.getAttribute(\"href\");\n            if(href==\"#top\"){\n                mui.scrollTo(0,500);\n            }else if(href==\"#shoucang\"){\n                //todo\n                mui.toast(\"功能开发中。。。\")\n                return\n            }else if(href==\"#fenxiang\"){\n                //todo\n                mui.toast(\"功能开发中。。。\")\n                return\n            }else {\n                mui.openWindow({url: href})\n            }\n\n        })\n    }\n\n</script>\n{% endblock %}', 2, 1);
INSERT INTO `cmswing_temp_bak` VALUES (1, 'home', 'detail', 'documentp', '官方文档封面模板', '{% extends \"../inc/base.html\" %}\n{% block style %}\n<script src=\"/static/admin/js/udeitor/ueditor.parse.min.js\"></script>\n<link rel=\"stylesheet\" href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.css\">\n<link rel=\"stylesheet\"\n      href=\"/static/assets/plugins/jquery-treetable/css/jquery.treetable.theme.default.css\">\n\n<style>\n    .active > .text{\n        display: block !important;\n    }\n    .temp-info{\n        border-right: 1px solid #d4d4d4;\n        background-color: #f0f0f0;\n        color: #444;\n        z-index: 10;\n        box-shadow: 4px 4px 4px #ccc;\n        -webkit-box-shadow: 4px 4px 4px #ccc;\n        -moz-box-shadow: 4px 4px 4px #ccc;\n    }\n    .temp-img{\n        background-color: #fff;\n        padding-top: 15px;\n        padding-bottom: 15px;\n        border-right: 1px solid #d4d4d4;\n    }\n    .temp-detail{\n        padding-top: 15px;\n        padding-bottom: 15px;\n        background-color: #f0f0f0;\n    }\n\n\n</style>\n{% endblock %}\n{% block content %}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header  page-header-xs\" >\n    <div class=\"container\">\n\n        <div class=\"container temp-info \">\n\n\n            <div class=\"row \">\n\n                <!-- IMAGE -->\n                <div class=\"col-lg-3 col-sm-3 temp-img\">\n\n                    <div class=\" relative margin-bottom-3 \">\n\n\n                        <!--\n                        IMAGE ZOOM\n\n                        data-mode=\"mouseover|grab|click|toggle\"\n                        -->\n                        <figure  style=\"position: relative; overflow: hidden;\">\n                            <img class=\"img-responsive\" src=\"{{info.cover_id|get_pic(\'m=1,w=265,h=360\')}}\" alt=\"This is the product title\" data-bd-imgshare-binded=\"1\">\n                           </figure>\n\n                    </div>\n\n                    <!-- Thumbnails (required height:100px) -->\n\n                    <!-- /Thumbnails -->\n\n                </div>\n                <!-- /IMAGE -->\n\n                <!-- ITEM DESC -->\n                <div class=\"col-lg-9 col-sm-9 temp-detail\">\n\n\n                    <!-- buttons -->\n                    <div class=\"pull-right\">\n                        <!-- replace data-item-id width the real item ID - used by js/view/demo.shop.js -->\n                        <a class=\"btn btn-default add-wishlist\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"\" data-original-title=\"Add To Wishlist\"><i class=\"fa fa-heart nopadding\"></i></a>\n                        <a class=\"btn btn-default add-compare\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"\" data-original-title=\"Add To Compare\"><i class=\"fa fa-bar-chart-o nopadding\" data-toggle=\"tooltip\"></i></a>\n                    </div>\n                    <!-- /buttons -->\n\n                    <!-- price -->\n                    <div class=\"shop-item-price\">\n                        <h1 class=\"blog-post-title\">{{info.title}}</h1>\n                    </div>\n                    <!-- /price -->\n                    <hr>\n                    <div class=\"clearfix margin-bottom-10 size-14 tex-888\">\n                        <span>最后更新时间:</span> {{lastinfo.update_time|moment(\'lll\')}}\n                    </div>\n                    <div class=\"clearfix margin-bottom-10 size-14 tex-888\">\n                        <span>阅读:</span> {{info.view}}  <span>关注:</span> {{info.view}}  <span>评论:</span> {{info.view}}\n                    </div>\n                    <div class=\"clearfix margin-bottom-10 size-16\">\n                        <a class=\"tag bg-white\" href=\"#\">\n                            <span class=\"txt\">CmsWing</span>\n                            <span class=\"num\">12</span>\n                        </a><a class=\"tag bg-white\" href=\"#\">\n                        <span class=\"txt\">ThinkJS</span>\n                        <span class=\"num\">12</span>\n                    </a><a class=\"tag bg-white\" href=\"#\">\n                        <span class=\"txt\">js</span>\n                        <span class=\"num\">12</span>\n                    </a>\n                    </div>\n                    <div class=\"clearfix margin-bottom-10 size-14\">\n                        <div style=\"float: left;margin-right: 10px; padding-top: 9px\">分享:</div>  <div style=\"float: left\" class=\"bdsharebuttonbox bdshare-button-style0-24\" data-bd-bind=\"1474896802958\"><a href=\"#\" class=\"bds_more\" data-cmd=\"more\"></a><a href=\"#\" class=\"bds_qzone\" data-cmd=\"qzone\" title=\"分享到QQ空间\"></a><a href=\"#\" class=\"bds_tsina\" data-cmd=\"tsina\" title=\"分享到新浪微博\"></a><a href=\"#\" class=\"bds_tqq\" data-cmd=\"tqq\" title=\"分享到腾讯微博\"></a><a href=\"#\" class=\"bds_renren\" data-cmd=\"renren\" title=\"分享到人人网\"></a><a href=\"#\" class=\"bds_weixin\" data-cmd=\"weixin\" title=\"分享到微信\"></a></div>\n                        <script>window._bd_share_config={\"common\":{\"bdSnsKey\":{},\"bdText\":\"\",\"bdMini\":\"2\",\"bdMiniList\":false,\"bdPic\":\"\",\"bdStyle\":\"0\",\"bdSize\":\"24\"},\"share\":{},\"image\":{\"viewList\":[\"qzone\",\"tsina\",\"tqq\",\"renren\",\"weixin\"],\"viewText\":\"分享到：\",\"viewSize\":\"16\"},\"selectShare\":{\"bdContainerClass\":null,\"bdSelectMiniList\":[\"qzone\",\"tsina\",\"tqq\",\"renren\",\"weixin\"]}};with(document)0[(getElementsByTagName(\'head\')[0]||body).appendChild(createElement(\'script\')).src=\'http://bdimg.share.baidu.com/static/api/js/share.js?v=89860593.js?cdnversion=\'+~(-new Date()/36e5)];</script>\n\n                    </div>\n                    <hr>\n                    <!-- /FORM -->\n                    <div class=\"clearfix\">\n                        <a href=\"{{ptree[0].name|get_url(ptree[0].id)}}\" class=\"btn btn-info\"><i class=\"fa fa-file-text\"></i>阅读</a> <a href=\"#\" class=\"btn btn-warning\"><i class=\"fa fa-heart\"></i>关注</a> <a href=\"#\" class=\"btn btn-danger\"><i class=\"fa fa-smile-o\"></i>打赏</a>\n                    </div>\n\n                </div>\n                <!-- /ITEM DESC -->\n\n\n            </div>\n\n        </div>\n        <!-- page tabs -->\n            <ul class=\"page-header-tabs list-inline \">\n\n                <li class=\"active\"><a href=\"#tab1\" data-toggle=\"tab\">概要</a></li>\n                <li class=\"\"><a href=\"#tab2\" data-toggle=\"tab\">目录</a></li>\n                <li class=\"\"><a href=\"#tab3\" data-toggle=\"tab\">评论(<span id=\"changyan_count_unit\">0</span>)</a></li>\n\n            </ul>\n        <!-- /page tabs -->\n    </div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n    <div class=\"container\">\n\n        <div class=\"row\">\n\n            <!-- LEFT -->\n\n            <div class=\"col-md-9 col-sm-9\">\n                <div class=\"tab-content nopadding\">\n                    <div class=\"tab-pane fade in active\" id=\"tab1\">\n                        <!-- article content -->\n                        <div id=\"contentcmswing\">\n\n                            <div class=\"tab-content\">\n                                {%set p = 1%}\n                                {%for val in info.content%}\n                                <div class=\"tab-pane fade {% if p ==1 %} in active{%endif%}\" id=\"page{{p}}\">\n                                    {{val|safe}}\n                                </div>\n                                {% set p = p+1%}\n                                {%endfor%}\n                            </div>\n                            {%if info.content|length >1%}\n                            <!-- Pagination Default -->\n                            <div class=\"text-center\">\n                                <ul class=\"pagination\">\n                                    {%set o = 1%}\n                                    {%for val in info.content%}\n                                    <li {%if o == 1 %}class=\"active\"{%endif%}><a href=\"#page{{o}}\" data-toggle=\"tab\">{{o}}</a></li>\n                                    {% set o = o+1%}\n                                    {%endfor%}\n                                </ul>\n                            </div>\n                            {%endif%}\n                        </div>\n                        <!-- /article content -->\n                    </div>\n                    <div class=\"tab-pane fade\" id=\"tab2\">\n                        <table class=\"table table-hover treetable\" id=\"category\">\n                            <tbody>\n                            {%for val in ptree%}\n                            <tr data-tt-id=\"{{val.id}}\" {%if val.pid != topid %} data-tt-parent-id=\"{{val.pid}}\"{%else%}class=\"active1\" {%endif%}>\n                                <td><a href=\"{{val.name|get_url(val.id)}}\" class=\"text-info\"><span class=\'folder ui-draggable\'>{{val.title}}</span></a></td>\n                            </tr>\n                            {%endfor%}\n\n\n                            </tbody>\n                        </table>\n                    </div>\n                    <div class=\"tab-pane fade\" id=\"tab3\">\n                        <!--打赏-->\n                        <div id=\"cyReward\" role=\"cylabs\" data-use=\"reward\" sourceid=\"{{info.id}}\" cid=\"{{category.id}}\" class=\"text-center\"></div>\n                        <!--打赏-->\n                        <div class=\"divider divider-dotted\"><!-- divider --></div>\n                        <!-- COMMENTS -->\n                        <div id=\"comments\" class=\"comments margin-top-20\">\n                            <!--打分版，用户可以对当前新闻、游戏或者应用等进行1-5级的评分-->\n                            <div id=\"SOHUCS\" sid=\"{{info.id}}\"></div>\n                            <script charset=\"utf-8\" type=\"text/javascript\" src=\"//changyan.sohu.com/upload/changyan.js\" ></script>\n                            <script type=\"text/javascript\">\n                                window._config = { showScore: true };\n                                window.changyan.api.config({\n                                    appid: \'{{controller.setup.CY_APPID}}\',\n                                    conf: \'{{controller.setup.CY_APPKEY}}\'\n                                });\n                            </script>\n                        </div>\n                        <!-- /COMMENTS -->\n                    </div>\n\n                </div>\n\n\n\n                <ul class=\"pager hide\">\n                    {%if previous.id%}\n                    <li class=\"previous\"><a class=\"noborder\" href=\"{{previous.name|get_url(previous.id)}}\" title=\"{{previous.title}}\">&larr; 上一篇:{{previous.title|block(20)}}</a></li>\n                    {%endif%}\n                    {%if next.id%}\n                    <li class=\"next\"><a class=\"noborder\" href=\"{{next.name|get_url(next.id)}}\" title=\"{{next.title}}\">下一篇:{{next.title|block(20)}} &rarr;</a></li>\n                    {%endif%}\n                </ul>\n\n\n\n\n\n            </div>\n            <!-- RIGHT -->\n            <div class=\"col-md-3 col-sm-3\">\n\n                <!-- INLINE SEARCH -->\n                <div class=\"inline-search clearfix margin-bottom-30\">\n                    <form action=\"\" method=\"get\" class=\"widget_search\">\n                        <input type=\"search\" placeholder=\"Start Searching...\" id=\"s\" name=\"s\" class=\"serch-input\">\n                        <button type=\"submit\">\n                            <i class=\"fa fa-search\"></i>\n                        </button>\n                    </form>\n                </div>\n                <!-- /INLINE SEARCH -->\n\n                <hr />\n\n                <!-- JUSTIFIED TAB -->\n                <div class=\"tabs nomargin-top hidden-xs margin-bottom-10\">\n\n                    <!-- tabs -->\n                    <ul class=\"nav nav-tabs nav-bottom-border nav-justified\">\n                        <li class=\"active\">\n                            <a href=\"#tab_1\" data-toggle=\"tab\">\n                                最热\n                            </a>\n                        </li>\n                        <li>\n                            <a href=\"#tab_2\" data-toggle=\"tab\">\n                                最新\n                            </a>\n                        </li>\n                    </ul>\n\n                    <!-- tabs content -->\n                    <div class=\"tab-content margin-bottom-0 margin-top-10\">\n\n                        <!-- POPULAR -->\n                        <div id=\"tab_1\" class=\"tab-pane active\">\n                            <!-- /post -->\n                            {% topic data = \"hotlist\",limit= \"5\",cid=category.id,type=\"hot\"%}\n                            {%for v in hotlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n\n                        </div>\n                        <!-- /POPULAR -->\n\n\n                        <!-- RECENT -->\n                        <div id=\"tab_2\" class=\"tab-pane\">\n                            {% topic data = \"newlist\",limit= \"5\",cid=category.id%}\n                            {%for v in newlist%}\n                            <div class=\"row tab-post\"><!-- post -->\n                                {%if v.cover_id%}\n                                <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\">\n                                        <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                                    </a>\n                                </div>\n                                {%endif%}\n                                <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                                    <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                                    <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                                </div>\n                            </div><!-- /post -->\n                            {%endfor%}\n                            <!-- /post -->\n                        </div>\n                        <!-- /RECENT -->\n\n                    </div>\n                </div>\n                <!-- JUSTIFIED TAB -->\n\n\n            </div>\n\n        </div>\n\n\n    </div>\n</section>\n<!-- / -->\n{% endblock %}\n\n{% block script %}\n<script src=\"/static/admin/js/treetable/jquery.treetable.js\" type=\"text/javascript\"></script>\n<script type=\"text/javascript\" src=\"//assets.changyan.sohu.com/upload/plugins/plugins.count.js\"></script>\n<script type=\"text/javascript\" charset=\"utf-8\" src=\"//changyan.itc.cn/js/??lib/jquery.js,changyan.labs.js?appid={{controller.setup.CY_APPID}}\"></script>\n<script>\n    $(function () {\n        $(\"#category\").treetable({ expandable: true });\n        $(\"#category\").treetable(\"expandAll\")\n    })\n\n    uParse(\'#contentcmswing\', {\n        rootPath: \'/static/admin/js/udeitor/\'\n    })\n</script>\n{% endblock %}\n', 1, 1, 34, 1504255473897, 1);
INSERT INTO `cmswing_temp_bak` VALUES (2, 'home', 'list', 'shop', '手机版商城列表模板', '{% extends \"../inc/base.html\" %}\n{% block page%}\n<div class=\"page page-current\" id=\"list_shop\">\n<header class=\"bar bar-nav\">\n<a class=\"button button-link button-nav pull-left\" href=\"/\" ><img src=\"/static/mobile/img/logo.png\" /></a>\n<a class=\"button button-link button-nav pull-right open-panel\" href=\"#\" data-panel=\'#panel-left-menu\'>\n<span class=\"icon icon-menu\"></span>\n导航\n</a>\n<h1 class=\"title\">{{controller.meta_title}}</h1>\n</header>\n\n<!--nav-->\n{% include \"../inc/nav.html\" %}\n<!--end_nav-->\n<div class=\"bar bar-header-secondary\">\n<div class=\"searchbar\">\n<a class=\"searchbar-cancel\">取消</a>\n<div class=\"search-input\">\n<label for=\"search\" class=\"icon icon-search\"></label>\n<input type=\"search\" placeholder=\"输入关键字...\" id=\"search\">\n</div>\n</div>\n</div>\n<!-- 添加 class infinite-scroll 和 data-distance  向下无限滚动可不加infinite-scroll-bottom类，这里加上是为了和下面的向上无限滚动区分-->\n<div class=\"content infinite-scroll native-scroll\" data-distance=\"100\" data-url=\"/column/{{http.get(\'category\')}}\">\n<div class=\"swiper-container\" data-space-between=\'10\' data-pagination=\".swiper-pagination\" data-loop=\"true\">\n<div class=\"swiper-wrapper\">\n<div class=\"swiper-slide\"><img src=\"/static/assets/images/demo/shop/banners/top_1.png\" alt=\"\" style=\'width: 100%\'></div>\n<div class=\"swiper-slide\"><img src=\"/static/assets/images/demo/shop/banners/top_2.png\" alt=\"\" style=\'width: 100%\'></div>\n</div>\n<div class=\"swiper-pagination\"></div>\n</div>\n<div class=\"row no-gutter list-container\">\n{% for item in list%}\n<a href=\"{{item.name|get_url(item.id)}}\" class=\"col-50 external\"><div  class=\"card demo-card-header-pic margin-5\">\n<div class=\"card-header color-white no-border no-padding\" valign=\"bottom\">\n{% set pic = item.pics|strToArray%}\n<img alt=\"\" src=\"{{pic[0]|get_pic(\'m=1,w=200\')}}\" class=\"card-cover\">\n</div>\n<div class=\"card-content tex-black\">\n<div class=\"card-content-inner padding-6\">\n<p class=\"color-danger margin-bottom-0\">{% if item.price|get_price_format(\'2\') %}<span class=\"color-gray line-through\">¥{{item.price|get_price_format(\'2\')}}</span>{%endif%}\n¥{{item.price|get_price_format(\'1\')}}</p>\n<p class=\"margin-bottom-0 margin-top-0\" style=\"overflow: hidden; height: 40px\">{{item.title}}</p>\n</div>\n</div>\n<div class=\"card-footer\">\n<span>有货</span>\n<span>5 评论</span>\n</div>\n</div></a>\n{% endfor %}\n</div>\n{#\n<div class=\"content-block-title\">列表式后面完善</div>\n<div class=\"list-block media-list\">\n<ul class=\"list-container\">\n{% for item in list%}\n<li>\n<a class=\"item-link item-content\" href=\"#\">\n{% set pic = item.pics|strToArray%}\n<div class=\"item-media\"><img width=\"80\" src=\"{{pic[0]|get_pic(\'m=1,w=80\')}}\"></div>\n<div class=\"item-inner\">\n<div class=\"item-title-row\">\n<div class=\"item-title color-danger\">{% if item.price|get_price_format(\'2\') %}<span class=\"\">¥{{item.price|get_price_format(\'2\')}}</span>{%endif%}\n¥{{item.price|get_price_format(\'1\')}}</div>\n<div class=\"item-after\"></div>\n</div>\n\n<div class=\"item-text\">{{item.title}}</div>\n</div>\n</a>\n</li>\n{% endfor %}\n\n</ul>\n</div>\n#}\n<!-- 加载提示符 -->\n<div class=\"infinite-scroll-preloader\">\n<div class=\"preloader\"></div>\n</div>\n</div>\n</div>\n{% endblock %}\n{% block pageout%}\n<div class=\"panel-overlay\"></div>\n<!-- Left Panel with Reveal effect -->\n<div class=\"panel panel-left panel-reveal theme-dark\" id=\'panel-left-menu\'>\n<div class=\"content-block\">\n<p>这是一个侧栏</p>\n<p>你可以在这里放用户设置页面</p>\n<p><a href=\"#\" class=\"close-panel\">关闭</a></p>\n</div>\n<div class=\"list-block\">\n<!-- .... -->\n</div>\n</div>\n{% endblock%}\n{% block script %}\n{% endblock %}', 2, 1, 10, 1506747222949, 1);
INSERT INTO `cmswing_temp_bak` VALUES (3, 'home', 'detail', 'shop', '商城内容页模板(PC)', '{% extends \"../inc/base.html\" %}\n{% block style %}\n<script src=\"/static/admin/js/udeitor/ueditor.parse.min.js\"></script>\n<link href=\"/static/assets/css/layout-shop.css\" rel=\"stylesheet\" type=\"text/css\" />\n{% endblock %}\n{% block content %}\n<!--\nPAGE HEADER\n\nCLASSES:\n.page-header-xs	= 20px margins\n.page-header-md	= 50px margins\n.page-header-lg	= 80px margins\n.page-header-xlg= 130px margins\n.dark			= dark page header\n\n.shadow-before-1 	= shadow 1 header top\n.shadow-after-1 	= shadow 1 header bottom\n.shadow-before-2 	= shadow 2 header top\n.shadow-after-2 	= shadow 2 header bottom\n.shadow-before-3 	= shadow 3 header top\n.shadow-after-3 	= shadow 3 header bottom\n-->\n<section class=\"page-header dark page-header-xs parallax parallax-2\" style=\"background-image:url(\'/static/assets/images/demo/thematics/lawyer/slider_bg_4-min.jpg\')\">\n<div class=\"overlay dark-2\"><!-- dark overlay [1 to 9 opacity] --></div>\n<div class=\"container\">\n\n<h1>{{category.title}}</h1>\n\n<!-- breadcrumbs -->\n<ol class=\"breadcrumb f-14\">\n<li><a href=\"/\">首页</a></li>\n{%for val in breadcrumb %}\n<li><a href=\"{{val.url}}\">{{val.title}}</a></li>\n{% endfor %}\n</ol><!-- /breadcrumbs -->\n\n<!-- page tabs -->\n{%column data=\"cate\",pid=category.pid%}\n{%if cate%}\n<ul class=\"page-header-tabs list-inline\">\n\n<!--<li class=\"active\"><a href=\"portfolio-masonry-3-columns.html\">With Gutter</a></li>-->\n{%for val in cate%}\n<li  {%if val.id == category.id %}class=\"active\"{%endif%}><a href=\"{{val.url}}\">{{val.name}}</a></li>\n{%endfor%}\n</ul>\n{%endif%}<!-- /page tabs -->\n</div>\n</section>\n<!-- /PAGE HEADER -->\n<!-- -->\n<section>\n<div class=\"container\">\n\n<div class=\"row\">\n\n<!-- RIGHT -->\n<div class=\"col-lg-9 col-md-9 col-sm-9 col-lg-push-3 col-md-push-3 col-sm-push-3\">\n<h1 class=\"blog-post-title size-18\">{{info.title}}</h1>\n<hr>\n<div class=\"row\">\n\n<!-- IMAGE -->\n<div class=\"col-lg-6 col-sm-6\">\n{% set pic = info.pics|strToArray%}\n<div class=\"thumbnail relative margin-bottom-3 \">\n\n\n<!-- \nIMAGE ZOOM \n\ndata-mode=\"mouseover|grab|click|toggle\"\n-->\n<figure id=\"zoom-primary\" class=\"zoom\" data-mode=\"mouseover\">\n<!-- \nzoom buttton\n\npositions available:\n.bottom-right\n.bottom-left\n.top-right\n.top-left\n-->\n<a class=\"lightbox bottom-right\" href=\"{{pic[0]|get_pic(\'m=0\')}}\" data-plugin-options=\'{\"type\":\"image\"}\'><i class=\"glyphicon glyphicon-search\"></i></a>\n\n<!-- \nimage \n\nExtra: add .image-bw class to force black and white!\n-->\n<img class=\"img-responsive\" src=\"{{pic[0]|get_pic(\'m=1,w=800,h=800\')}}\"  alt=\"This is the product title\" />\n</figure>\n\n</div>\n\n<!-- Thumbnails (required height:100px) -->\n{% if pic|length >1%}\n<div data-for=\"zoom-primary\" class=\"zoom-more owl-carousel owl-padding-3 featured\" \ndata-plugin-options=\'{\"singleItem\": false, \"autoPlay\": false, \"navigation\": true, \"pagination\": false,\"itemsMobile\":[479,5]}\'>\n{%set i = 0%}\n{% for val in pic%}\n<a class=\"thumbnail {%if i == 0%}active{%endif%}\" href=\"{{val|get_pic(\'m=1,w=800,h=800\')}}\">\n<img src=\"{{val|get_pic(\'m=2,w=100,h=100\')}}\" height=\"100\" alt=\"\" />\n</a>\n{%set i=i+1%}\n{% endfor %}\n\n</div>\n{% endif %}\n<!-- /Thumbnails -->\n\n</div>\n<!-- /IMAGE -->\n\n<!-- ITEM DESC -->\n<div class=\"col-lg-6 col-sm-6\">\n\n<!-- buttons -->\n<div class=\"pull-right\">\n<!-- replace data-item-id width the real item ID - used by js/view/demo.shop.js -->\n<a class=\"btn btn-default add-wishlist\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"Add To Wishlist\"><i class=\"fa fa-heart nopadding\"></i></a>\n<a class=\"btn btn-default add-compare\" href=\"#\" data-item-id=\"1\" data-toggle=\"tooltip\" title=\"Add To Compare\"><i class=\"fa fa-bar-chart-o nopadding\" data-toggle=\"tooltip\"></i></a>\n</div>\n<!-- /buttons -->\n\n<!-- price -->\n<div class=\"shop-item-price text-danger\">\n{% if info.price|get_price_format(\'2\') %}<span class=\"line-through nopadding-left\">¥{{info.price|get_price_format(\'2\')}}</span>{%endif%}\n¥<price>{{info.price|get_price_format(\'1\')}}</price>\n</div>\n<!-- /price -->\n\n<hr />\n\n<div class=\"clearfix margin-bottom-30\" >\n<div class=\"pull-right\" id=\"stock\">\n{% if info.total_stock != 0%}\n\n<span class=\" text-success\" ><i class=\"fa fa-check\"></i> 有货 <span class=\"badge badge-aqua btn-xs \">{{info.total_stock}}</span></span>\n{% else%}\n<span class=\"pull-right text-danger\"><i class=\"glyphicon glyphicon-remove\"></i> 无货</span>\n{% endif %}\n</div>\n<strong>商家编码:</strong> {{info.goods_no}}\n</div>\n{#\n<hr />\n<!-- countdown -->\n<div class=\"text-center\">\n<h5>折扣时间</h5>\n<div class=\"countdown\" data-from=\"January 31, 2018 15:03:26\" data-labels=\"年,月,周,天,时,分,秒\"><!-- Example Date From: December 31, 2018 15:03:26 --></div>\n</div>\n<!-- /countdown -->\n#}\n<hr/>\n\n<!-- FORM -->\n<form class=\"clearfix form-inline nomargin ichecks\"   data-icheck-info =\"{{info.suk}}\">\n<!--{{info.suk}}-->\n{% if info.suk %}\n{% set suk = info.suk|strToJson%}\n{% set i = 0 %}\n{% for item in suk.type%}\n<div class=\"icheck m-b clearfix shop-sku\" >\n<div class=\"item dt pull-left text-justify text-right\">\n{{item}}:\n</div>\n<div class=\"itemnbox\">\n{% if i == 0 %}\n{% for _item in suk.data %}\n<!--{{suk.data[0].name}}-->\n\n<div class=\"item\">\n<input type=\"radio\"  name=\"size1\" value=\"{{_item.name}}\" >\n<label >{%if suk.is_pic == 1%}<img class=\"m-r-xs\" height=\"30\" alt=\"\" data-src=\"{{_item.pic|get_pic(\'m=1,w=800,h=800\')}}\" src=\"{{_item.pic|get_pic(\'m=2,w=30,h=30\')}}\">{%endif%}{{_item.name}}</label>\n</div>\n\n{% endfor %}\n\n{% elif i==1 %}\n{% for _item in suk.data[0].ch %}\n<!--{{suk.data[0].name}}-->\n<div class=\"item\">\n<input type=\"radio\"  name=\"size2\" value=\"{{_item.name}}\" >\n<label >{{_item.name}}</label>\n</div>\n{% endfor %}\n{% else %}\n{% for _item in suk.data[0].ch[0].ch %}\n<!--{{suk.data[0].name}}-->\n<div class=\"item\">\n<input type=\"radio\"  name=\"size3\" value=\"{{_item.name}}\" >\n<label >{{_item.name}}</label>\n</div>\n{% endfor %}\n{% endif %}\n</div>\n</div>\n{% set i = i+1%}\n{% endfor %}\n<hr />\n{% endif %}\n<input type=\"hidden\" name=\"product_id\" value=\"{{info.id}}\" />\n<input type=\"hidden\" id=\"type\" name=\"type\" value=\"\" />\n<!-- see assets/js/view/demo.shop.js -->\n\n<input type=\"hidden\" id=\"qty\" name=\"qty\" value=\"1\" />\n\n<!-- see assets/js/view/demo.shop.js -->\n<div  id=\"out-of-stock\" class=\"{% if info.total_stock != 0%}hide{% endif %}\">\n\n<!-- buttons -->\n<div class=\"shop-item-buttons text-center \">\n<span class=\"out-of-stock\">已售罄</span><!-- add .clean to remove css characteres -->\n</div>\n<!-- /buttons -->\n</div>\n<div id=\"in-of-stock\" class=\"{% if info.total_stock == 0%}hide{% endif %}\">\n<div class=\"btn-group pull-left product-opt-qty {% if info.total_stock == 0%}hide{% endif %}\">\n<button type=\"button\" class=\"btn btn-default dropdown-toggle product-qty-dd noradius\" data-toggle=\"dropdown\">\n<span class=\"caret\"></span>\n数量 <small id=\"product-selected-qty\">(<span>1</span>)</small>\n</button>\n\n<ul id=\"product-qty-dd\" class=\"dropdown-menu clearfix\" role=\"menu\">\n<li class=\"active\"><a data-val=\"1\" href=\"#\">1</a></li>\n<li><a data-val=\"2\" href=\"#\">2</a></li>\n<li><a data-val=\"3\" href=\"#\">3</a></li>\n<li><a data-val=\"4\" href=\"#\">4</a></li>\n<li><a data-val=\"5\" href=\"#\">5</a></li>\n<li><a data-val=\"6\" href=\"#\">6</a></li>\n<li><a data-val=\"7\" href=\"#\">7</a></li>\n<li><a data-val=\"8\" href=\"#\">8</a></li>\n<li><a data-val=\"9\" href=\"#\">9</a></li>\n<li><a data-val=\"10\" href=\"#\">10</a></li>\n</ul>\n</div><!-- /btn-group -->\n\n<button class=\"btn btn-danger pull-left product-add-cart noradius \"> <i class=\"fa fa-cart-plus \"></i> 添加到购物车</button>\n</div>\n\n</form>\n<!-- /FORM -->\n\n\n<hr />\n{% if info.description %}\n<!-- short description -->\n<small class=\"text-muted\">{{info.description}}</small>\n<hr />\n<!-- /short description -->\n{% endif %}\n\n\n\n<!-- Share -->\n<div class=\"pull-right\">\n\n<a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-facebook pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n<i class=\"icon-facebook\"></i>\n<i class=\"icon-facebook\"></i>\n</a>\n\n<a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-twitter pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n<i class=\"icon-twitter\"></i>\n<i class=\"icon-twitter\"></i>\n</a>\n\n<a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-gplus pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n<i class=\"icon-gplus\"></i>\n<i class=\"icon-gplus\"></i>\n</a>\n\n<a href=\"#\" class=\"social-icon social-icon-sm social-icon-transparent social-linkedin pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n<i class=\"icon-linkedin\"></i>\n<i class=\"icon-linkedin\"></i>\n</a>\n\n</div>\n<!-- /Share -->\n\n\n<!-- rating -->\n<div class=\"rating rating-4 size-13 margin-top-10 width-100\"><!-- rating-0 ... rating-5 --></div>\n<!-- /rating -->\n\n</div>\n<!-- /ITEM DESC -->\n\n</div>\n\n\n\n<ul id=\"myTab\" class=\"nav nav-tabs nav-top-border margin-top-40\" role=\"tablist\">\n<li role=\"presentation\" class=\"active\"><a href=\"#description\" role=\"tab\" data-toggle=\"tab\">商品介绍</a></li>\n<li role=\"presentation\"><a href=\"#specs\" role=\"tab\" data-toggle=\"tab\">规格参数</a></li>\n<li role=\"presentation\"><a href=\"#reviews\" role=\"tab\" data-toggle=\"tab\">商品评价 (2)</a></li>\n</ul>\n\n<div class=\"tab-content padding-top-20\">\n<!-- DESCRIPTION -->\n<div role=\"tabpanel\" class=\"tab-pane fade in active\" id=\"description\">\n{{info.content[0]|safe}}\n</div>\n\n<!-- SPECIFICATIONS -->\n<div role=\"tabpanel\" class=\"tab-pane fade\" id=\"specs\">\n<div class=\"table-responsive\">\n<table class=\"table table-hover\">\n<thead>\n<tr>\n<th>Column name</th>\n<th>Column name</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>Size</td>\n<td>2XL</td>\n</tr>\n<tr>\n<td>Color</td>\n<td>Red</td>\n</tr>\n<tr>\n<td>Weight</td>\n<td>132lbs</td>\n</tr>\n<tr>\n<td>Height</td>\n<td>74cm</td>\n</tr>\n<tr>\n<td>Bluetooth</td>\n<td><i class=\"fa fa-check text-success\"></i> YES</td>\n</tr>\n<tr>\n<td>Wi-Fi</td>\n<td><i class=\"glyphicon glyphicon-remove text-danger\"></i> NO</td>\n</tr>\n</tbody>\n</table>\n</div>\n</div>\n\n<!-- REVIEWS -->\n<div role=\"tabpanel\" class=\"tab-pane fade\" id=\"reviews\">\n<!-- REVIEW ITEM -->\n<div class=\"block margin-bottom-60\">\n\n<span class=\"user-avatar\"><!-- user-avatar -->\n<img class=\"pull-left media-object\" src=\"/static/assets/images//avatar2.jpg\" width=\"64\" height=\"64\" alt=\"\">\n</span>\n\n<div class=\"media-body\">\n<h4 class=\"media-heading size-14\">\nJohn Doe &ndash; \n<span class=\"text-muted\">June 29, 2014 - 11:23</span> &ndash;\n<span class=\"size-14 text-muted\"><!-- stars -->\n<i class=\"fa fa-star\"></i>\n<i class=\"fa fa-star\"></i>\n<i class=\"fa fa-star\"></i>\n<i class=\"fa fa-star\"></i>\n<i class=\"fa fa-star-o\"></i>\n</span>\n</h4>\n\n<p>\nProin eget tortor risus. Cras ultricies ligula sed magna dictum porta. Pellentesque in ipsum id orci porta dapibus. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas metus nulla, commodo a sodales sed, dignissim pretium nunc. Nam et lacus neque.\n</p>\n\n</div>\n\n</div>\n<!-- /REVIEW ITEM -->\n\n<!-- REVIEW ITEM -->\n<div class=\"block margin-bottom-60\">\n\n<span class=\"user-avatar\"><!-- user-avatar -->\n<img class=\"pull-left media-object\" src=\"/static/assets/images//avatar2.jpg\" width=\"64\" height=\"64\" alt=\"\">\n</span>\n\n<div class=\"media-body\">\n<h4 class=\"media-heading si